INSERT INTO `creature_text` VALUES (42047, 0, 0, 'I will not leave you! Do not forget that I am a hunter in this family!', 12, 0, 100, 1, 0, 0, 41958, 0, 0, 0, 'Masha Cliffwalker');
INSERT INTO `creature_text` VALUES (42050, 0, 0, 'Глупый старый таурен, как ты смеешь проявлять подобное неуважение?!', 12, 0, 100, 1, 0, 0, 41968, 0, 0, 0, 'General Grebo');
INSERT INTO `creature_text` VALUES (42050, 1, 0, 'Что это все значит?', 12, 0, 100, 6, 0, 0, 41969, 0, 0, 0, 'General Grebo');
INSERT INTO `creature_text` VALUES (42050, 2, 0, 'Мой знак различия! Да. Я потерял его. Где ты нашел его, таурен?', 12, 0, 100, 1, 0, 0, 41971, 0, 0, 0, 'General Grebo');
INSERT INTO `creature_text` VALUES (42050, 3, 0, '%s смеется.', 16, 0, 100, 11, 0, 0, 41974, 0, 0, 0, 'General Grebo');
INSERT INTO `creature_text` VALUES (42050, 4, 0, 'Твой сын был слабак. Совершенно бесхребетный. Такой же, как его отец.', 12, 0, 100, 25, 0, 0, 41975, 0, 0, 0, 'General Grebo');
INSERT INTO `creature_text` VALUES (42050, 5, 0, 'А что касается тебя, $n, то ты $gразочаровал:разочаровала; меня. Ты – позор Орды!', 12, 0, 100, 1, 0, 0, 41976, 0, 0, 0, 'General Grebo');
