INSERT INTO `creature_text` VALUES (42106, 0, 0, 'Смотри же, Скалоступ, как грандиозно завершится эта баталия.', 12, 0, 100, 1, 0, 20788, 42029, 0, 0, 0, 'Overlord Kromgar');
