INSERT INTO `creature_text` VALUES (42124, 0, 0, 'Что ты сделал, Кром\'гар?', 12, 0, 100, 25, 0, 20520, 42047, 0, 0, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (42124, 1, 0, 'Мой приказ?', 12, 0, 100, 6, 0, 20521, 42050, 0, 0, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (42124, 2, 0, 'Разве я давал тебе приказ убивать невинных, Кром\'гар?', 12, 0, 100, 6, 0, 20522, 42051, 0, 0, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (42124, 3, 0, 'Ты думаешь, я – убийца, Кромгар?', 12, 0, 100, 6, 0, 20523, 42054, 0, 0, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (42124, 4, 0, 'Тогда я повторяю свой вопрос: ЧТО ТЫ СДЕЛАЛ?', 12, 0, 100, 25, 0, 20524, 42056, 0, 0, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (42124, 5, 0, 'I sent you with an army to Stonetalon Mountains. You have been ordered to secure the Horde in these lands.', 12, 0, 100, 1, 0, 20525, 42057, 0, 0, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (42124, 6, 0, 'Но вместо этого ты опустошил их. Вырезал мирных жителей. Даже детей.', 12, 0, 100, 1, 0, 20526, 42058, 0, 0, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (42124, 7, 0, 'Я долго жил в Нордсколе, Кромгар. Там я многое понял об Орде.', 12, 0, 100, 1, 0, 20527, 42059, 0, 0, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (42124, 8, 0, 'Один мудрый, старый солдат сказал то, что я запомнил на всю жизнь…', 12, 0, 100, 1, 0, 20528, 42060, 0, 0, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (42124, 9, 0, '\"Честь, какой бы жестокой не была битва... никогда не забывай о чести\".', 12, 0, 100, 1, 0, 20529, 42061, 0, 0, 0, 'Garrosh Hellscream');
