INSERT INTO `creature_text` VALUES (42124, 11, 0, 'Ты обесчестил свой народ.', 12, 0, 100, 1, 0, 20531, 42063, 0, 0, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (42124, 12, 0, 'По праву Вождя я освобождаю тебя от службы.', 12, 0, 100, 0, 0, 20532, 42064, 0, 0, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (42124, 13, 0, 'ТЫ СВОБОДЕН.', 12, 0, 100, 0, 0, 20533, 42065, 0, 0, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (42124, 14, 0, 'И ты!', 12, 0, 100, 25, 0, 20536, 42066, 0, 0, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (42124, 15, 0, 'Сжалиться? Убиты твоя жена и ребенок. Вырезаны твои соплеменники. Твой дом сгорел дотла.', 12, 0, 100, 6, 0, 20537, 42069, 0, 0, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (42124, 16, 0, 'Сжалиться... Вождь, мне есть чему у тебя поучиться.', 12, 0, 100, 66, 0, 20538, 42070, 0, 0, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (42124, 17, 0, 'Армии Кромгара больше нет. Твое звание больше ничего не значит. Если ты и правда хочешь послужить Орде, отправляйся в Пустоши или Южные степи.', 12, 0, 100, 1, 0, 20539, 42075, 0, 0, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (42124, 18, 0, 'Выбор за тобой.', 12, 0, 100, 1, 0, 20540, 42076, 0, 0, 0, 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES (42124, 19, 0, 'Живи по чести. Помни, что Адский Крик всегда следит за тобой.', 12, 0, 100, 1, 0, 20541, 42077, 0, 0, 0, 'Garrosh Hellscream');
