INSERT INTO `creature_text` VALUES (42680, 2, 0, 'Наш, с позволения сказать, \"король\" сидит на своем высоком золотом троне и совершенно не думает о нашем бедственном положении!', 14, 0, 100, 1, 0, 0, 42572, 0, 0, 0, 'Shadowy Figure');
INSERT INTO `creature_text` VALUES (42680, 3, 0, 'А наши дети меж тем погибают от голода на этих улицах!', 14, 0, 100, 1, 0, 0, 42573, 0, 0, 0, 'Shadowy Figure');
INSERT INTO `creature_text` VALUES (42680, 4, 0, 'Его война – не наша – лишила нас средств к существованию. МЫ заплатили за победы Альянса своей кровью и кровью наших близких!', 14, 0, 100, 15, 0, 0, 42574, 0, 0, 0, 'Shadowy Figure');
INSERT INTO `creature_text` VALUES (42680, 5, 0, 'Пришло время, братья и сестры, положить конец этой несправедливости!', 14, 0, 100, 5, 0, 0, 42575, 0, 0, 0, 'Shadowy Figure');
INSERT INTO `creature_text` VALUES (42680, 6, 0, 'Король Штормгарда и остальные правители Альянса должны заплатить за то, что они сделали с нами!', 14, 0, 100, 1, 0, 0, 42576, 0, 0, 0, 'Shadowy Figure');
INSERT INTO `creature_text` VALUES (42680, 7, 0, 'Сегодня мы возродимся! Мы станем личностями, а не безликой, безымянной толпой!', 14, 0, 100, 5, 0, 0, 42577, 0, 0, 0, 'Shadowy Figure');
INSERT INTO `creature_text` VALUES (42680, 8, 0, 'Скоро начнется!', 42, 0, 100, 0, 0, 37666, 42569, 0, 0, 0, 'text');
INSERT INTO `creature_text` VALUES (42693, 0, 0, '|cFF768EBDFive years ago, in the heart of the Deadmines ... |r |TInterface\\QuestFrame\\UI-QUESTLOG-BOOKICON.BLP:32|t', 42, 0, 100, 0, 0, 0, 42864, 0, 0, 0, 'Видения прошлого to Player');
INSERT INTO `creature_text` VALUES (42695, 1, 0, 'Враги в залах святилища!', 14, 0, 100, 22, 0, 0, 44523, 0, 0, 0, 'Часовой Каменных Недр to Player');
INSERT INTO `creature_text` VALUES (42695, 4, 0, 'Поднимайтесь, болваны! Нас атакуют!', 14, 0, 100, 5, 0, 0, 44524, 7000, 7000, 0, 'Часовой Каменных Недр to Player');
INSERT INTO `creature_text` VALUES (42697, 1, 0, 'Никто не смеет бросать вызов братству!', 14, 0, 100, 0, 0, 5780, 97777, 0, 0, 0, 'Эдвин ван Клиф to Воин Альянса');
INSERT INTO `creature_text` VALUES (42697, 4, 0, 'Глупцы! Правда на нашей стороне!', 14, 0, 100, 0, 0, 5783, 6064, 72995, 72995, 0, 'Эдвин ван Клиф');
INSERT INTO `creature_text` VALUES (42698, 1, 0, '%s выпрыгивает из тени!', 16, 0, 100, 0, 0, 5780, 1327, 0, 0, 0, 'Черный страж из Братства Справедливости to Воин Альянса');
INSERT INTO `creature_text` VALUES (42699, 3, 0, 'Мы здесь, чтобы покончить с тобой! Выходи и сражайся!', 14, 0, 100, 397, 0, 0, 42594, 9483, 9483, 0, 'Воин Альянса to Player');
INSERT INTO `creature_text` VALUES (42707, 0, 0, 'Cover the door - we\'ve got him!.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (42744, 0, 0, 'Скажи мне только одно, Ванесса.', 12, 0, 100, 1, 0, 0, 42674, 0, 0, 0, 'Lieutenant Horatio Laine');
INSERT INTO `creature_text` VALUES (42744, 1, 0, 'Зачем тебе было убивать всех Хмуробровов?', 12, 0, 100, 1, 0, 0, 42675, 0, 0, 0, 'Lieutenant Horatio Laine');
INSERT INTO `creature_text` VALUES (42748, 3, 0, 'Спасибо, радость моя.', 12, 0, 100, 1, 0, 0, 42663, 61369, 61369, 0, 'Адмирал Терзающий Рев to Player');
INSERT INTO `creature_text` VALUES (42749, 0, 0, 'Ублюдки, гореть вам за то, что вы сделали.', 12, 0, 100, 25, 0, 0, 42656, 0, 0, 0, 'Hope Saldean');
INSERT INTO `creature_text` VALUES (42749, 1, 0, 'Надежда? Именно ее я должна была чувствовать, когда смотрела, как твои приспешники отрубили голову моему отцу?', 12, 0, 100, 6, 0, 0, 42658, 0, 0, 0, 'Hope Saldean');
INSERT INTO `creature_text` VALUES (42749, 2, 0, 'Надежда – это жестокая шутка этого немилосердного мира. Нет никакой надежды, есть только Ванесса. Ванесса ван Клиф.', 12, 0, 100, 1, 0, 0, 42659, 0, 0, 0, 'Hope Saldean');
INSERT INTO `creature_text` VALUES (42750, 0, 0, 'Не нравится мне это, $n. Будь настороже!', 12, 0, 100, 1, 0, 0, 42655, 0, 0, 0, 'Marshal Gryan Stoutmantle');
INSERT INTO `creature_text` VALUES (42750, 1, 0, 'Надежда! Что...', 12, 0, 100, 5, 0, 0, 42657, 0, 0, 0, 'Marshal Gryan Stoutmantle');
INSERT INTO `creature_text` VALUES (42750, 2, 0, '$n, доберись до Штормграда. Расскажи обо всем королю Ринну. ОБО ВСЕМ! ИДИ ЖЕ!', 12, 0, 100, 0, 0, 0, 42681, 0, 0, 0, 'Marshal Gryan Stoutmantle');
INSERT INTO `creature_text` VALUES (42777, 0, 0, 'Nobody tells me how to run my business here.', 12, 0, 100, 0, 0, 0, 42724, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (42777, 1, 0, 'It was just a couple of rivets. That ship had plenty!', 12, 0, 100, 0, 0, 0, 42738, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (42777, 2, 0, 'I\'m sure it would\'ve been seaworthy in calm waters.', 12, 0, 100, 0, 0, 0, 42739, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (42777, 3, 0, 'Less rivets means a cleaner environment!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (42777, 4, 0, '...can we settle this with cash?', 12, 0, 100, 0, 0, 0, 42741, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (42777, 5, 0, 'Okay okay, stop! You make a compelling argument.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (42777, 6, 0, 'I\'ll double the worker shifts and put in my own money to get these ships seaworthy and back on schedule. Count on it!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (42778, 0, 0, 'Спасибо, радость моя.', 12, 0, 100, 1, 0, 0, 42663, 0, 0, 0, 'Admiral Ripsnarl');
INSERT INTO `creature_text` VALUES (42803, 4, 0, '%s изменяется, мерцая.', 16, 0, 100, 0, 0, 0, 9793, 30610, 30610, 80369, 'Дракобель-полукровка');
