INSERT INTO `creature_text` VALUES (42975, 4, 0, '%s falls into a frenzy!', 16, 0, 100, 1, 0, 0, 30726, 26000, 26000, 0, 'Чумной вурдалак');
INSERT INTO `creature_text` VALUES (43026, 3, 0, 'Все чужаки должны быть уничтожены!', 12, 0, 100, 0, 0, 0, 42904, 98000, 98000, 0, 'Глубинный элементаль to Player');
INSERT INTO `creature_text` VALUES (43032, 0, 0, 'The cannoneer\'s hands are black with gunpowder.', 42, 0, 100, 0, 0, 0, 42916, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (43044, 0, 0, 'The shell\'s markings read: \"STORMWIND ROYAL INDUSTRIES\"', 42, 0, 100, 0, 0, 0, 42942, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (43047, 0, 0, 'I am ready to meet with your so-called Earthcaller.', 12, 0, 100, 0, 0, 0, 42941, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (43047, 1, 0, 'Earthcaller, my father, Stonefather, Oremantle, has been taken prisoner by the stone troggs of Needlerock Spire.', 12, 0, 100, 2, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (43048, 0, 0, 'The Captain\'s Log lies nearby.', 42, 0, 100, 0, 0, 0, 42930, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (43081, 3, 0, '$C покидает Красногорье!', 12, 7, 100, 0, 0, 0, 42989, 67158, 67158, 0, 'Стражник Бейтмен to Player');
INSERT INTO `creature_text` VALUES (43138, 0, 0, 'For Stonefather Oremantle!', 12, 0, 100, 0, 0, 0, 43161, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (43138, 0, 1, 'For Stonehearth!', 12, 0, 100, 0, 0, 0, 43162, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (43138, 0, 2, 'For the Stonefather!', 12, 0, 100, 0, 0, 0, 43160, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (43138, 0, 3, 'Kill the stone troggs!', 12, 0, 100, 0, 0, 0, 43163, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (43173, 3, 0, 'Приветствую тебя! Я советую тебе поговорить с капитаном стражи Паркером. Он сейчас на вершине этой башни.', 12, 0, 100, 25, 0, 0, 43164, 33649, 41231, 0, 'Дозорный гарнизона Красногорья to Player');
INSERT INTO `creature_text` VALUES (43184, 0, 0, 'Take us out, Messner.', 12, 0, 100, 0, 0, 0, 43524, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (43197, 0, 0, 'ROCK NOT SO HEAVY! PUNY HUMIES!', 12, 0, 100, 0, 0, 0, 43218, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (43197, 1, 0, 'Where trow? TROW ON BRIDGE??', 12, 0, 100, 0, 0, 0, 43220, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (43197, 2, 0, 'OK! Me trow in water!', 12, 0, 100, 0, 0, 0, 43222, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (43203, 0, 0, 'Gmurgl starts follow you.', 16, 0, 0, 1, 0, 0, 0, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (43204, 0, 0, 'Oh... Orgus not feel so good...', 12, 0, 0, 0, 0, 0, 43224, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (43205, 0, 0, 'Well blow me down! The landlubber scalawag comes bearin\' rum!', 12, 0, 0, 1, 0, 0, 0, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (43205, 1, 0, 'Yarr?', 12, 0, 0, 1, 0, 0, 43228, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (43205, 2, 0, 'Shiver me timbers!', 12, 0, 0, 1, 0, 0, 43229, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (43219, 0, 0, 'Not so fast, wet guy!', 14, 0, 0, 0, 0, 0, 43244, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (43219, 1, 0, 'Zombie-pirates?! Kill it!', 14, 0, 0, 0, 0, 0, 0, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (43222, 3, 0, 'Тебе следует что-нибудь предпринять, Соломон, иначе начнется бунт!', 12, 7, 100, 274, 0, 0, 43751, 24395, 24395, 0, 'Селянин Красногорья');
INSERT INTO `creature_text` VALUES (43229, 0, 0, 'Don\'t worry about me. We have to rescue the king!', 12, 0, 100, 0, 0, 0, 43418, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (43229, 0, 1, 'Kill a few for me, would you?', 12, 0, 100, 0, 0, 0, 43417, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (43229, 0, 2, 'What happened?', 12, 0, 100, 0, 0, 0, 78944, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (43229, 0, 3, 'You\'re a strange-looking creature. Thank you.', 12, 0, 100, 0, 0, 0, 43415, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (43229, 0, 4, 'You need to kill Troggzor the Earthinator!', 12, 0, 100, 0, 0, 0, 43420, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (43245, 0, 0, 'Мастер Проклятий Джин\'до! Давненько я этой маски не видел.', 12, 0, 100, 0, 0, 0, 43362, 0, 0, 0, 'Занзил Отверженный to Player');
INSERT INTO `creature_text` VALUES (43245, 1, 0, 'Что привело тебя в мою часть Тернистой долины? Я не привык принимать гостей из Зул\'Гуруба.', 12, 0, 100, 0, 0, 0, 43363, 0, 0, 0, 'Занзил Отверженный to Player');
INSERT INTO `creature_text` VALUES (43245, 2, 0, 'Какое безобразие... Но старого изгоя Занзила это не касается.', 12, 0, 100, 0, 0, 0, 43366, 0, 0, 0, 'Занзил Отверженный to Тень проклинателя');
INSERT INTO `creature_text` VALUES (43245, 3, 0, 'Друг? Ты был среди тех, кто плевал и бросал в меня камни, Джин\'до. Однако, как говорится, кто старое помянет, тому глаз вон, так?', 12, 0, 100, 0, 0, 0, 43369, 0, 0, 0, 'Занзил Отверженный to Тень проклинателя');
INSERT INTO `creature_text` VALUES (43245, 4, 0, 'Значит, тебе нужна помощь Занзила? Хорошо. Встретимся в Зул\'Гурубе... дружище.', 12, 0, 100, 0, 0, 0, 43370, 0, 0, 0, 'Занзил Отверженный to Тень проклинателя');
INSERT INTO `creature_text` VALUES (43246, 0, 0, 'Ты прекрасно осведомлен о том, что я здесь делаю, Занзил.', 12, 0, 100, 0, 0, 0, 43364, 0, 0, 0, 'Тень проклинателя to Занзил Отверженный');
INSERT INTO `creature_text` VALUES (43246, 1, 0, 'Гурубаши переживают не лучшие времена. Я жив, и Мандокир вернулся... Но остальных больше нет.', 12, 0, 100, 0, 0, 0, 43365, 0, 0, 0, 'Тень проклинателя to Занзил Отверженный');
INSERT INTO `creature_text` VALUES (43246, 2, 0, 'Что ж, Занзил, хочу возложить эту ответственность на тебя. Я добыл тела верховной жрицы Джеклик и верховного жреца Веноксиса.', 12, 0, 100, 0, 0, 0, 43367, 0, 0, 0, 'Тень проклинателя to Занзил Отверженный');
INSERT INTO `creature_text` VALUES (43246, 3, 0, 'I remember what these elixirs of yours are capable of. But will they help one old friend?', 12, 0, 100, 0, 0, 0, 43368, 0, 0, 0, 'Тень проклинателя to Занзил Отверженный');
INSERT INTO `creature_text` VALUES (43247, 3, 0, 'Да пошел ты, Соломон. Я сама найду свою семью.', 12, 7, 100, 14, 0, 0, 43760, 80793, 80793, 0, 'Селянка Красногорья');
INSERT INTO `creature_text` VALUES (43247, 3, 0, 'Прошу, помогите мне! Из всей семьи осталась я одна. Остальных похитили!', 12, 7, 100, 20, 0, 0, 43759, 15084, 15084, 0, 'Селянка Красногорья');
INSERT INTO `creature_text` VALUES (43255, 1, 0, 'Что ты здесь делаешь? Хотя ладно. Я все равно воскрешу Джеклик, прежде чем ты мне помешаешь!', 14, 0, 100, 0, 0, 0, 43300, 0, 0, 0, 'Занзил Отверженный');
INSERT INTO `creature_text` VALUES (43255, 4, 0, 'Довольно с меня этого! Пора сделать перерыв...', 14, 0, 100, 0, 0, 0, 43292, 6105, 6105, 0, 'Занзил Отверженный');
INSERT INTO `creature_text` VALUES (43296, 0, 0, 'Chimaeron snorts laudly.', 16, 0, 0, 0, 0, 0, 0, 0, 0, 0, 'VO_BD_Chimaeron aggro');
INSERT INTO `creature_text` VALUES (43296, 1, 0, 'Chimaeron prepares to massacre his foes!', 41, 0, 0, 0, 0, 0, 0, 0, 0, 0, 'VO_BD_Chimaeron massacre');
INSERT INTO `creature_text` VALUES (43296, 2, 0, 'Chimaeron\'s massacre has knocked the Bile-O-Tron offline!', 41, 0, 0, 0, 0, 0, 0, 0, 0, 0, 'VO_BD_Chimaeron offline');
INSERT INTO `creature_text` VALUES (43296, 3, 0, 'The Bile-O-Tron is back online!', 41, 0, 0, 0, 0, 0, 0, 0, 0, 0, 'VO_BD_Chimaeron online');
INSERT INTO `creature_text` VALUES (43300, 0, 0, 'I remember the time when Kishan and I fished from a bridge in Priozerye, and Kishan farted. I think Oslow never forgave Kishan for this ...', 12, 0, 100, 0, 0, 0, 43456, 0, 0, 0, 'Месснер to Player');
INSERT INTO `creature_text` VALUES (43300, 1, 0, 'Итак, говорят, что Вариан Ринн вернулся. Это правда? Извини, я малость отстал от жизни.', 12, 0, 100, 0, 0, 0, 43461, 0, 0, 0, 'Месснер to Player');
INSERT INTO `creature_text` VALUES (43302, 1, 0, 'Оставь сдачу себе, грязное животное!', 12, 7, 100, 0, 0, 0, 43432, 0, 0, 22120, 'Данфорт to Призыватель из клана Черной горы');
INSERT INTO `creature_text` VALUES (43302, 3, 0, 'Знаешь, жену Кишана убили орки из клана Черной горы. С тех пор он очень изменился. Словно бы солнце его жизни закатилось. Тьма, $gпарень:подруга;. Чистая тьма.', 12, 0, 100, 0, 0, 0, 43457, 178000, 178000, 0, 'Данфорт to Player');
INSERT INTO `creature_text` VALUES (43303, 0, 0, 'Погоди, щас мы врежем этим оркам! Команда \"Браво\" СНОВА В ДЕЛЕ!', 12, 0, 100, 0, 0, 0, 43472, 0, 0, 0, 'Кракауер to Player');
INSERT INTO `creature_text` VALUES (43305, 0, 0, 'Вы слышали этот хруст?', 12, 7, 100, 0, 0, 0, 43442, 0, 0, 0, 'Йоргенсен to Следопыт из клана Черной горы');
INSERT INTO `creature_text` VALUES (43305, 1, 0, 'I am the most valuable member of this team, I am a paladin and all that.', 12, 0, 100, 0, 0, 0, 43471, 0, 0, 0, 'Йоргенсен to Player');
INSERT INTO `creature_text` VALUES (43305, 2, 0, 'Очень смешно, Данфорт.', 12, 0, 100, 0, 0, 0, 43561, 0, 0, 0, 'Йоргенсен to Месснер');
INSERT INTO `creature_text` VALUES (43322, 1, 0, 'Опять ты? Еще хочется? В этот раз я составил зелье посильнее! Веноксис будет жить!', 14, 0, 100, 0, 0, 0, 43320, 0, 0, 0, 'Занзил Отверженный');
INSERT INTO `creature_text` VALUES (43322, 4, 0, 'I can’t bear it soon ...', 14, 0, 100, 0, 0, 0, 43321, 8594, 8594, 0, 'Занзил Отверженный');
INSERT INTO `creature_text` VALUES (43324, 1, 0, '(Blood for the master!)', 14, 0, 0, 0, 0, 22069, 47851, 0, 0, 0, 'VO_BT_Chogall_BotEvent16');
INSERT INTO `creature_text` VALUES (43324, 1, 1, 'Such is your fate.', 14, 0, 0, 0, 0, 22070, 47852, 0, 0, 0, 'VO_BT_Chogall_BotEvent17');
INSERT INTO `creature_text` VALUES (43324, 1, 2, 'Pathetic flesh!', 14, 0, 0, 0, 0, 22071, 47853, 0, 0, 0, 'VO_BT_Chogall_BotEvent18');
INSERT INTO `creature_text` VALUES (43324, 1, 3, '(Finish it! Finish them all!)', 14, 0, 0, 0, 0, 22072, 47854, 0, 0, 0, 'VO_BT_Chogall_BotEvent19');
INSERT INTO `creature_text` VALUES (43324, 1, 4, 'Embrace your end.', 14, 0, 0, 0, 0, 22073, 47855, 0, 0, 0, 'VO_BT_Chogall_BotEvent20');
INSERT INTO `creature_text` VALUES (43324, 1, 5, '(Body and soul, to be swallowed whole!)', 14, 0, 0, 0, 0, 22088, 47882, 0, 0, 0, 'VO_BT_Chogall_BotEvent35');
INSERT INTO `creature_text` VALUES (43324, 1, 6, 'Now the Master has them.', 14, 0, 0, 0, 0, 22091, 47885, 0, 0, 0, 'VO_BT_Chogall_BotEvent38');
INSERT INTO `creature_text` VALUES (43324, 1, 7, '(Chaos, madness! Like a hug for your brain.)', 14, 0, 0, 0, 0, 22092, 47886, 0, 0, 0, 'VO_BT_Chogall_BotEvent39');
INSERT INTO `creature_text` VALUES (43324, 2, 0, 'Give me your strength!', 14, 0, 0, 0, 0, 22078, 47856, 0, 0, 0, 'VO_BT_Chogall_BotEvent25');
INSERT INTO `creature_text` VALUES (43324, 2, 1, 'Behold the might of the hammer!', 14, 0, 0, 0, 0, 22079, 47857, 0, 0, 0, 'VO_BT_Chogall_BotEvent26');
INSERT INTO `creature_text` VALUES (43324, 2, 2, '(Stop. Hammertime!)', 14, 0, 0, 0, 0, 22080, 47858, 0, 0, 0, 'VO_BT_Chogall_BotEvent27');
INSERT INTO `creature_text` VALUES (43324, 3, 0, 'Enough! (To His will all flesh succumbs. From His kingdom, chaos comes.) Yes - what he said.', 14, 0, 0, 0, 0, 22081, 47859, 0, 0, 0, 'VO_BT_Chogall_BotEvent28');
