INSERT INTO `creature_text` VALUES (43873, 0, 0, 'Ветер внезапно меняет направление по воле |3-1(%s)!', 41, 0, 100, 0, 0, 0, 47549, 0, 0, 0, 'Альтаирий to 0');
INSERT INTO `creature_text` VALUES (43875, 0, 0, 'YOU tread upon the sacrosanct! Mortals have no place amidst the clouds.', 14, 0, 0, 0, 0, 20867, 49573, 0, 0, 0, 'VO_VP_AsimAlAkir_Event01');
