INSERT INTO `creature_text` VALUES (43911, 3, 0, 'Убить нарушителя!', 12, 0, 100, 0, 0, 0, 77788, 46366, 46366, 0, 'Командир Гаул to Player');
INSERT INTO `creature_text` VALUES (43919, 0, 0, 'Woo hoo.', 12, 0, 100, 1, 0, 0, 44282, 0, 0, 0, 'KillCreditText');
INSERT INTO `creature_text` VALUES (43920, 0, 0, 'Thank you! I don\'t know how much longer we would have survived.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'KillCreditText');
INSERT INTO `creature_text` VALUES (43921, 0, 0, 'Freedom! Sweet, sweet freedom!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'KillCreditText');
INSERT INTO `creature_text` VALUES (43929, 0, 0, 'Goodbye, nearby living beings! My unique presence is requested in another dimension.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'B-4000 ByeText');
INSERT INTO `creature_text` VALUES (43934, 1, 0, 'Осколок души сливается с душой Вестника Тьмы!', 41, 0, 100, 0, 0, 0, 52151, 0, 0, 82263, 'Фрагмент души to Player');
INSERT INTO `creature_text` VALUES (44058, 0, 0, 'Whew, that was a bit of a tight squeeze, but the rest of the trip should be a breeze.', 12, 0, 100, 0, 0, 0, 44386, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (44058, 1, 0, 'There`s nothing quite like the smell of Durotar on a warm, comfortable boat ride.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (44058, 2, 0, 'This is where you`d expect something to go wrong on a beautiful trip like this.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (44058, 3, 0, 'Everyone always says, Horton, why do you drive a boat up and down the river? I ask them, Why don`t you? ... that`s usually where I run out of witty retorts.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (44058, 4, 0, 'We`re pulling up near your stop. Head due west along the road to reach Nozzlepot`s Outpost.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (44083, 3, 0, 'Привет, $n!', 15, 0, 100, 0, 0, 18276, 62261, 308747, 308747, 0, 'Линзи Зарубай to Player');
INSERT INTO `creature_text` VALUES (44099, 3, 0, '...мы наносим зелье на образец...', 12, 0, 100, 0, 0, 0, 44434, 1558032, 1558032, 0, 'Даск \"Пузырь\" Гоблопшик to Player');
INSERT INTO `creature_text` VALUES (44120, 1, 0, 'Silence, idiot.', 12, 0, 100, 0, 0, 0, 44459, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (44120, 2, 0, 'Hear anything interesting? Your little revelation about me is about to cost you your life.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (44120, 3, 0, 'The Twilight\'s Hammer has agents everywhere! Kill me and a dozen take my place!', 12, 0, 100, 0, 0, 0, 44409, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (44120, 4, 0, 'My powers of sight... I beheld the future... I have seen what is to come...', 12, 0, 100, 0, 0, 0, 44410, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (44120, 5, 0, 'This world unravels. If you could see the horrors I have seen...', 12, 0, 100, 0, 0, 0, 44411, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (44120, 6, 0, 'To serve is madness, but defiance ... impossible!', 12, 0, 100, 0, 0, 0, 44412, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (44155, 3, 0, 'Эй! Что это ты там делаешь?', 14, 0, 100, 0, 0, 0, 44446, 15905, 15905, 0, 'Бронвин Зарубай to Биплан Лиги исследователей');
INSERT INTO `creature_text` VALUES (44160, 0, 0, 'Wait, uh - you put words in my mouth! Not fair!', 12, 0, 100, 0, 0, 0, 44454, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (44202, 0, 0, 'Помогите! Я попал в ловушку!', 14, 0, 100, 0, 0, 20833, 49317, 0, 0, 0, 'Айс Вентурон to 0');
INSERT INTO `creature_text` VALUES (44202, 1, 0, 'Как вы ловко с ним управились! Ключ должен быть у него внутри. Просто... покопайтесь там, и вы его найдете.', 14, 0, 100, 0, 0, 20838, 49322, 0, 0, 0, 'Айс Вентурон to Player');
INSERT INTO `creature_text` VALUES (44238, 3, 0, 'Археология ищет факты. Не истину.', 12, 0, 100, 396, 0, 0, 44508, 46815, 46815, 0, 'Харрисон Джонс to Player');
INSERT INTO `creature_text` VALUES (44239, 3, 0, '%s заливается истерическим смехом.', 16, 0, 100, 153, 0, 0, 37127, 52541, 52541, 0, 'Джанрик Моллер to Харрисон Джонс');
INSERT INTO `creature_text` VALUES (44241, 3, 0, 'Grab ROPE!', 12, 7, 100, 14, 0, 0, 49007, 52589, 52589, 0, 'Лана Дюбинг to Player');
INSERT INTO `creature_text` VALUES (44243, 3, 0, '%s смеется.', 16, 0, 100, 153, 0, 0, 106407, 53758, 53758, 0, 'Дарнелла Винфорд to Харрисон Джонс');
INSERT INTO `creature_text` VALUES (44262, 0, 0, 'What was that? And what the heck is that smell?', 12, 0, 100, 1, 0, 0, 44499, 0, 0, 0, 'Smart text');
INSERT INTO `creature_text` VALUES (44262, 1, 0, 'AAAAAAGH! CURSE YOUR SUDDEN YET INEVITABLE BETRAYAL!', 14, 0, 100, 1, 0, 0, 44502, 0, 0, 0, 'Smart text');
INSERT INTO `creature_text` VALUES (44263, 0, 0, 'РРГЛРГЛРГЛРГЛРГЛ!!!', 14, 0, 100, 0, 0, 0, 44507, 0, 0, 0, 'Представитель племени Синежабрых to Player');
INSERT INTO `creature_text` VALUES (44269, 0, 0, 'It burns like the moon!', 12, 0, 100, 0, 0, 0, 44861, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (44293, 0, 0, 'I myself am preparing to become a priest - someday, I hope, I will be able to control the same forces.', 12, 0, 100, 0, 0, 0, 44750, 0, 0, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (44293, 1, 0, 'Могу я помочь тебе с этим расследованием деятельности Сумеречного Молота? Моему отцу совсем не обязательно об этом знать...', 15, 0, 100, 0, 0, 0, 44900, 0, 0, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (44293, 2, 0, 'Вся наша взрывчатка похищена?! $n, здесь творится что-то нехорошее.', 12, 0, 100, 0, 0, 0, 44938, 0, 0, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (44293, 3, 0, 'Что это за место? Ох... этот запах...', 12, 0, 100, 0, 0, 0, 44987, 0, 0, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (44293, 4, 0, 'На этом алтаре явно проводились ритуалы. Похоже на какие-то еретические ритуалы посвящения, о которых мне рассказывал Велен. Кровавые и отвратительные ритуалы.', 12, 0, 100, 16, 0, 0, 44988, 0, 0, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (44293, 5, 0, 'Кто ты думаешь... Стой! $n, оглянись!', 12, 0, 100, 5, 0, 0, 44989, 0, 0, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (44293, 6, 0, 'Осторожно! Еще один!', 12, 0, 100, 0, 0, 0, 45004, 0, 0, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (44293, 7, 0, 'Сразись с ними, я не смогу долго удерживать этот щит!', 12, 0, 100, 0, 0, 0, 45005, 0, 0, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (44293, 8, 0, '$n, смотри – они бегут.', 12, 0, 100, 0, 0, 0, 45006, 0, 0, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (44293, 9, 0, 'Эй, $n, нам не стоит сообщать моему отцу об этой засаде. Если он узнает о покушении на мою жизнь, больше никогда не позволит мне покинуть замок...', 12, 0, 100, 1, 0, 0, 45017, 0, 0, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (44293, 10, 0, 'Мы на месте. Бумаги Самуэльсона должны быть на третьем этаже, это вверх по спиральной лестнице.', 12, 0, 100, 1, 0, 0, 45027, 0, 0, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (44293, 11, 0, 'Сейчас посмотрим. Никому не придет в голову задавать вопросы принцу, который решил посмотреть кое-какие бумаги.', 12, 0, 100, 1, 0, 0, 45028, 0, 0, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (44293, 12, 0, 'Хм...', 12, 0, 100, 0, 0, 0, 76105, 0, 0, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (44293, 13, 0, 'Все это очень подозрительно. Здесь нет никакой информации о Сумеречном Молоте. Что же задумал Самуэльсон?', 12, 0, 100, 274, 0, 0, 45030, 0, 0, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (44293, 14, 0, 'Я вернусь в тронный зал и пригляжу за Самуэльсоном, пока ты поговоришь с Матиасом. Береги себя!', 12, 0, 100, 0, 0, 0, 45059, 0, 0, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (44323, 4, 0, 'Отныне ты в заточении.', 12, 0, 100, 5, 0, 0, 46677, 10880, 10880, 0, 'Темный магистр Гандлинг');
INSERT INTO `creature_text` VALUES (44347, 3, 0, 'Waldir Nu waldir ', 12, 7, 100, 0, 0, 0, 0, 191000, 191000, 0, 'Вернер Истбрук to Player');
INSERT INTO `creature_text` VALUES (44365, 0, 0, 'Где этот шут с мозгами огра?', 12, 0, 100, 1, 0, 0, 44695, 0, 0, 0, 'Sylvanas');
INSERT INTO `creature_text` VALUES (44365, 1, 0, 'А, легок на помине!', 12, 0, 100, 1, 0, 0, 44696, 0, 0, 0, 'Sylvanas');
INSERT INTO `creature_text` VALUES (44365, 2, 0, 'Вождь, я так рада, что ты смог прибыть.', 12, 0, 100, 0, 0, 0, 44701, 0, 0, 0, 'Sylvanas');
INSERT INTO `creature_text` VALUES (44365, 3, 0, 'Многие воины Плети, которым удалось сохранить рассудок, после смерти Короля-лича остались не у дел. Эти \"твари\", как ты их назвал, зовутся валь\'кирами. Теперь они подвластны мне...', 12, 0, 100, 0, 0, 0, 44702, 0, 0, 0, 'Sylvanas');
INSERT INTO `creature_text` VALUES (44365, 4, 0, '...И позвала я тебя в том числе из-за них.', 12, 0, 100, 0, 0, 0, 44703, 0, 0, 0, 'Sylvanas');
INSERT INTO `creature_text` VALUES (44365, 5, 0, 'Да,Вождь. Так вот, я сняла с Отрекшихся их проклятие!', 12, 0, 100, 5, 0, 0, 0, 0, 0, 0, 'Sylvanas');
INSERT INTO `creature_text` VALUES (44365, 6, 0, 'Мы, Отрекшиеся, не можем иметь потомства.', 12, 0, 100, 1, 0, 0, 44706, 0, 0, 0, 'Sylvanas');
INSERT INTO `creature_text` VALUES (44365, 7, 0, 'Но с помощью валь\'кир мы научились оживлять тела погибших и превращать их в Отрекшихся.', 12, 0, 100, 0, 0, 0, 44707, 0, 0, 0, 'Sylvanas');
INSERT INTO `creature_text` VALUES (44365, 8, 0, 'Агата, покажи Вождю!', 12, 0, 100, 22, 0, 0, 44709, 0, 0, 0, 'Sylvanas');
INSERT INTO `creature_text` VALUES (44365, 9, 0, 'Вождь, без новых Отрекшихся мой народ бы вымер... Мы потеряли бы влияние в Гилнеасе и северном Лордероне.', 12, 0, 100, 0, 0, 0, 44715, 0, 0, 0, 'Sylvanas');
INSERT INTO `creature_text` VALUES (44365, 10, 0, 'Как это чем, Вождь? Я служу Орде.', 12, 0, 100, 66, 0, 0, 44718, 0, 0, 0, 'Sylvanas');
INSERT INTO `creature_text` VALUES (44402, 3, 0, 'Счастливого пути, Чарлга. Да хранит тебя Мать-Земля.', 12, 0, 100, 0, 0, 0, 79085, 4212, 4212, 0, 'Ольд Каменное Копье to Player');
INSERT INTO `creature_text` VALUES (44412, 0, 0, 'Ведомый местью, Барон Геддон превратил тебя в ходячую бомбу! Держись подальше от своих союзников, чтобы не навредить им!', 41, 0, 100, 0, 0, 0, 44570, 0, 0, 0, 'Baron Geddon Warning Trigger to Player');
INSERT INTO `creature_text` VALUES (44414, 0, 0, 'Durak says: Hellscream\'s involved, you say?', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (44414, 1, 0, 'Durak says: This is all happening sooner than I would have liked.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (44414, 2, 0, 'Durak says: Well then there is only one thing left to be done...', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (44418, 0, 0, 'Желчетрон оживает и начинает извергать из себя некое вонючее вещество.', 16, 0, 100, 0, 0, 0, 44593, 0, 0, 0, 'Желчетрон-800 to Player');
INSERT INTO `creature_text` VALUES (44418, 1, 0, 'Желчетрон дает утвердительный ответ и выключается навсегда.', 16, 0, 100, 0, 0, 20838, 48794, 0, 0, 0, 'Желчетрон-800 to Player');
INSERT INTO `creature_text` VALUES (44436, 3, 0, 'Здравствуйте!', 12, 0, 100, 5, 0, 0, 45501, 9000, 9000, 0, 'Вооруженный щитом рыцарь Дольного Очага to Player');
INSERT INTO `creature_text` VALUES (44441, 0, 0, 'Прощай, $n. Передай мой поклон достойным мужчинам и женщинам на Участке Целителей Земли.', 12, 0, 100, 2, 0, 0, 45160, 0, 0, 0, 'Верховный лорд Тирион Фордринг to Player');
INSERT INTO `creature_text` VALUES (44451, 0, 0, 'Coltira! Your queen is calling you!', 14, 0, 0, 22, 0, 0, 444510, 0, 0, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (44451, 0, 0, 'Кольтира! Твоя королева зовет тебя!', 14, 0, 0, 22, 0, 0, 444510, 0, 0, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (44451, 1, 0, 'Enough of this nerve! You think I don\'t see anything going on outside the walls of the suburbs?', 12, 0, 0, 6, 0, 0, 444511, 0, 0, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (44451, 1, 0, 'Довольно этой наглости! Ты думаешь, я не вижу ничего, что творится за стенами Подгорода?', 12, 0, 0, 6, 0, 0, 444511, 0, 0, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (44451, 2, 0, 'And it\'s not your fault. You should have taken the Andorals a few days ago when we beat the Whip.', 12, 0, 0, 274, 0, 0, 444512, 0, 0, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (44451, 2, 0, 'И это — не твоя заслуга. Ты должен был взять Андорал еще несколько дней назад, когда мы победили Плеть.', 12, 0, 0, 274, 0, 0, 444512, 0, 0, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (44451, 3, 0, 'I know your secret, Coltira. I\'ve heard of your friendship with the Alliance death knight, Tassarian.', 12, 0, 0, 25, 0, 0, 444513, 0, 0, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (44451, 3, 0, 'Я знаю твою тайну, Кольтира. Я слышала о вашей дружбе с рыцарем смерти Альянса, Тассарианом.', 12, 0, 0, 25, 0, 0, 444513, 0, 0, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (44451, 4, 0, 'Artas was completely united when he created his knights of death. You\'re still weak and soft.', 12, 0, 0, 1, 0, 0, 444514, 0, 0, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (44451, 4, 0, 'Артас совершенно сплошал, когда создал своих рыцарей смерти. Ты все еще слаб и мягкотел.', 12, 0, 0, 1, 0, 0, 444514, 0, 0, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (44451, 5, 0, 'Fortunately, my servants can help you... ...overcome your weakness. In the Suburbs, where you will go.', 12, 0, 0, 1, 0, 0, 444515, 0, 0, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (44451, 5, 0, 'К счастью, мои слуги могут помочь тебе... преодолеть слабость. В Подгороде, куда ты и отправишься.', 12, 0, 0, 1, 0, 0, 444515, 0, 0, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (44451, 6, 0, 'Quiet, knight of death. When this is over, you will no longer feel fear.', 12, 0, 0, 1, 0, 0, 444516, 0, 0, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (44451, 6, 0, 'Тише, рыцарь смерти. Когда это закончится, ты перестанешь чувствовать страх.', 12, 0, 0, 1, 0, 0, 444516, 0, 0, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (44451, 7, 0, 'You\'ll say thank you later.', 12, 0, 0, 1, 0, 0, 444517, 0, 0, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (44451, 7, 0, 'Скажешь спасибо потом.', 12, 0, 0, 1, 0, 0, 444517, 0, 0, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (44452, 0, 0, 'За Орду!', 14, 0, 100, 0, 0, 0, 143151, 0, 0, 0, 'Кольтира Ткач Смерти to Темный магистр Гандлинг');
INSERT INTO `creature_text` VALUES (44453, 0, 0, 'За Альянс!', 14, 0, 100, 0, 0, 0, 146212, 0, 0, 0, 'Тассариан to Темный магистр Гандлинг');
INSERT INTO `creature_text` VALUES (44453, 3, 0, 'Гандлинг силен, но не волнуйся. Начни с ним сражаться, а я потом присоединюсь.', 12, 0, 100, 396, 0, 0, 45516, 808715, 808715, 0, 'Тассариан to Player');
INSERT INTO `creature_text` VALUES (44454, 3, 0, 'Мне понадобится время на изучение этих образцов, $n. Когда закончу анализ, я поговорю с тобой.', 12, 0, 100, 1, 0, 0, 45488, 1534927, 1534927, 0, 'Агент ШРУ Каартиш to Player');
