INSERT INTO `creature_text` VALUES (44629, 2, 0, 'Продолжай, Сильвана.', 12, 0, 100, 1, 0, 0, 44704, 0, 0, 0, 'Garrosh');
INSERT INTO `creature_text` VALUES (44629, 3, 0, 'Что ты здесь устроила, Сильвана... Это противоречит всем законам природы! Этому... нет названия!', 12, 0, 100, 274, 0, 0, 44714, 0, 0, 0, 'Garrosh');
