INSERT INTO `creature_text` VALUES (44629, 7, 0, 'Кромуш, останься тут и следи, чтобы королеву банши хорошо охраняли. В следующий раз я жду полного отчета.', 12, 0, 100, 1, 0, 0, 44720, 0, 0, 0, 'Garrosh');
INSERT INTO `creature_text` VALUES (44629, 8, 0, 'Помни, Сильвана, что в конце концов мы все предстанем перед Творцом и будем держать ответ за свои дела. Может быть, твой день не так далек...', 12, 0, 100, 1, 0, 0, 44721, 0, 0, 0, 'Garrosh');
