INSERT INTO `creature_text` VALUES (45027, 3, 0, 'Мы захватили храм и стараемся уменьшить урон, насколько можем. Поговори с Маруутом, он в храме.', 12, 0, 100, 0, 0, 22615, 45187, 52000, 52000, 0, 'Аггра to Player');
INSERT INTO `creature_text` VALUES (45027, 3, 0, 'Первый осколок должен был доставить боевой корабль Орды, \"Неистовство бури\". Но он так сюда и не добрался.', 12, 0, 100, 0, 0, 22614, 45186, 42000, 42000, 0, 'Аггра to Player');
INSERT INTO `creature_text` VALUES (45027, 3, 0, 'Столп рухнул, когда Смертокрыл пробил свод Подземья и создал брешь между двумя мирами. Если мы не восстановим столп мира, Подземье и Азерот столкнутся и произойдет катастрофа.', 12, 0, 100, 0, 0, 22613, 45182, 28000, 28000, 0, 'Аггра to Player');
INSERT INTO `creature_text` VALUES (45027, 3, 0, 'У тебя получилось! Отлично.', 12, 0, 100, 4, 0, 22610, 45175, 3000, 3000, 0, 'Аггра to Player');
INSERT INTO `creature_text` VALUES (45027, 3, 0, 'Это Храм Земли. Ключевое место Подземья.', 12, 0, 100, 0, 0, 22611, 45176, 8000, 8000, 0, 'Аггра to Player');
INSERT INTO `creature_text` VALUES (45028, 3, 0, 'Мы в центре Водоворота – портала между Подземьем и Азеротом. Он разрывает ваш мир на части!', 12, 0, 100, 0, 0, 22607, 45170, 14000, 14000, 0, 'Аггра to Player');
INSERT INTO `creature_text` VALUES (45028, 3, 0, 'Мы уже близко, держись!', 14, 0, 100, 0, 0, 22609, 45172, 33000, 33000, 86971, 'Аггра to Player');
INSERT INTO `creature_text` VALUES (45028, 3, 0, 'Надеюсь, у тебя крепкие нервы. Путешествие через пролом не для неженок.', 12, 0, 100, 0, 0, 22606, 45169, 4000, 4000, 0, 'Аггра to Player');
INSERT INTO `creature_text` VALUES (45028, 3, 0, 'Надеюсь, что Тралл сможет удерживать его, пока ты будешь внутри.', 12, 0, 100, 0, 0, 22608, 45171, 25000, 25000, 0, 'Аггра to Player');
INSERT INTO `creature_text` VALUES (45099, 0, 0, 'Подземье? Ненавижу это место. Погоди... ты же не посвященный Клондацк!', 14, 0, 100, 0, 0, 0, 45238, 0, 0, 0, 'Magmatooth');
INSERT INTO `creature_text` VALUES (45099, 1, 0, 'Я знаю, зачем ты здесь. Я чувствую это!', 14, 0, 100, 0, 0, 0, 45239, 0, 0, 0, 'Magmatooth');
