INSERT INTO `creature_text` VALUES (45329, 3, 0, 'Поздравляю вас, герои! Благодаря вам Стратхольм снова стал нашим! Мы ждем вас у бойни!', 14, 0, 100, 0, 0, 0, 45606, 4000, 4000, 0, 'Командир Элигор Вестник Рассвета to Лорд Аурий Ривендер');
INSERT INTO `creature_text` VALUES (45337, 3, 0, 'The creature is lousy!', 12, 1, 100, 14, 0, 0, 31681, 11562, 11562, 0, 'Тиелис to Player');
INSERT INTO `creature_text` VALUES (45377, 0, 0, 'У-у-у! Сокровище тебе не достанется!!!', 12, 0, 100, 0, 0, 0, 45529, 0, 0, 0, 'Ауг to 0');
INSERT INTO `creature_text` VALUES (45377, 1, 0, 'У-у-у! Я тебя отдам крокам!', 12, 0, 100, 0, 0, 0, 45544, 0, 0, 0, 'Ауг to 0');
INSERT INTO `creature_text` VALUES (45378, 0, 0, 'Gwaaaaaaaaaaaahhh!!!', 0, 0, 0, 0, 0, 0, 45530, 0, 0, 0, 'Augh - SAY_WHIRLWIND');
