INSERT INTO `creature_text` VALUES (45412, 0, 0, 'Кристаллы Аш\'ари разрушены! Бойня в опасности!', 14, 0, 100, 0, 0, 0, 6289, 0, 0, 0, 'Лорд Аурий Ривендер to Тузадинский послушник');
INSERT INTO `creature_text` VALUES (45412, 1, 0, 'Так ты считаешь возможным играться с созданиями Короля-лича? Рамштайн, обязательно встреть чужаков как следует.', 14, 0, 100, 0, 0, 0, 6398, 0, 0, 0, 'Лорд Аурий Ривендер to Player');
INSERT INTO `creature_text` VALUES (45412, 2, 0, 'Пора взять дело в свои руки. Идем. Вступи в мои владения и брось вызов могуществу Плети!', 14, 0, 100, 0, 0, 0, 6401, 0, 0, 0, 'Лорд Аурий Ривендер to Караульный Черной Стражи');
INSERT INTO `creature_text` VALUES (45412, 3, 0, '%s оживляет своего слугу-нежить!', 16, 0, 100, 0, 0, 0, 6511, 0, 0, 0, 'Лорд Аурий Ривендер to 0');
INSERT INTO `creature_text` VALUES (45412, 4, 0, '%s пытается воздействовать заклинанием \"Смертельный союз\" на своих слуг!', 16, 0, 100, 0, 0, 0, 6512, 0, 0, 0, 'Лорд Аурий Ривендер to 0');
INSERT INTO `creature_text` VALUES (45417, 0, 0, 'By the way, you are welcome to borrow any supplies you find inside the caravan... as long as you bring them back when you are done!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Fiona');
INSERT INTO `creature_text` VALUES (45417, 1, 0, 'Fiona laughs', 16, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Fiona');
INSERT INTO `creature_text` VALUES (45417, 2, 0, 'Gidwin, you can sit in front with me next time.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Fiona');
INSERT INTO `creature_text` VALUES (45417, 3, 0, 'There\'s a tower coming up on our left. What do you say we stop and rest a bit?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Fiona');
INSERT INTO `creature_text` VALUES (45417, 3, 0, 'Кстати, можешь пользоваться любыми вещами, которые найдешь в караване. Только не забудь потом вернуть все на свои места!', 12, 0, 100, 0, 0, 0, 46090, 1382162, 1382162, 0, 'Фиона to Player');
INSERT INTO `creature_text` VALUES (45417, 4, 0, 'The roads here are rough, Gidwin. I don\'t want to risk losing a horse.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Fiona');
INSERT INTO `creature_text` VALUES (45417, 5, 0, 'Here we are!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Fiona');
INSERT INTO `creature_text` VALUES (45429, 0, 0, 'Shotgun!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Tarenar Sunstrike ');
INSERT INTO `creature_text` VALUES (45429, 1, 0, 'Because I won our bet. And I called shotgun.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Tarenar Sunstrike ');
INSERT INTO `creature_text` VALUES (45429, 2, 0, 'Fine. I killed five zombies. And they were huge. How many undead did you kill?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Tarenar Sunstrike ');
INSERT INTO `creature_text` VALUES (45429, 3, 0, 'I\'m going to bet that you only killed four.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Tarenar Sunstrike ');
INSERT INTO `creature_text` VALUES (45429, 4, 0, 'I didn\'t. You just told me!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Tarenar Sunstrike ');
INSERT INTO `creature_text` VALUES (45429, 5, 0, 'So seriously, how did your undead hunting go? I want to hear stories.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Tarenar Sunstrike ');
INSERT INTO `creature_text` VALUES (45429, 6, 0, 'Gidwin? Are you ignoring me?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Tarenar Sunstrike ');
INSERT INTO `creature_text` VALUES (45429, 7, 0, 'Giddy... you are mad at me, aren\'t you?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Tarenar Sunstrike ');
INSERT INTO `creature_text` VALUES (45429, 8, 0, 'Remember when we were kids, Gidwin? We would spend all day pretending to fight the trolls, or the murlocs, or the girls.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Tarenar Sunstrike ');
INSERT INTO `creature_text` VALUES (45429, 9, 0, 'We had a name we called ourselves... what was it?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Tarenar Sunstrike ');
INSERT INTO `creature_text` VALUES (45429, 10, 0, 'I\'m dead serious. I\'ve completely forgotten.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Tarenar Sunstrike ');
INSERT INTO `creature_text` VALUES (45429, 11, 0, 'Actually, I agree with Fiona.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Tarenar Sunstrike ');
INSERT INTO `creature_text` VALUES (45429, 12, 0, ' The Argent Crusade can wait for us a little while longer. Besides, our guest may want to get off the caravan soon.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Tarenar Sunstrike ');
INSERT INTO `creature_text` VALUES (45431, 0, 0, 'Hey now! Why do you get to sit in front?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Gidwin Goldbraids ');
INSERT INTO `creature_text` VALUES (45431, 0, 0, 'В чем причина задержки, Фиона?', 12, 0, 100, 6, 0, 0, 46103, 0, 0, 0, 'Гидвин Златокос');
INSERT INTO `creature_text` VALUES (45431, 1, 0, 'Who says you won the bet? We haven\'t compared tallies yet.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Gidwin Goldbraids ');
INSERT INTO `creature_text` VALUES (45431, 2, 0, 'I\'m pretty sure I killed six.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Gidwin Goldbraids ');
INSERT INTO `creature_text` VALUES (45431, 3, 0, 'H... how did you know that?', 12, 0, 100, 11, 0, 0, 0, 0, 0, 0, 'Gidwin Goldbraids ');
INSERT INTO `creature_text` VALUES (45431, 3, 0, 'Почему мы все еще здесь? Если немедленно не отправимся, я пойду один.', 12, 0, 100, 1, 0, 0, 46101, 1887038, 3063395, 0, 'Гидвин Златокос');
INSERT INTO `creature_text` VALUES (45431, 4, 0, 'Don\'t do this, Tarenar.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Gidwin Goldbraids ');
INSERT INTO `creature_text` VALUES (45431, 5, 0, 'You remember damn well enough what it was.', 12, 0, 100, 11, 0, 0, 0, 0, 0, 0, 'Gidwin Goldbraids ');
INSERT INTO `creature_text` VALUES (45431, 6, 0, ' <Sigh>... \"The Paladin Pals\".', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Gidwin Goldbraids ');
INSERT INTO `creature_text` VALUES (45431, 7, 0, 'Maybe now I don\'t want to. Maybe I like it better sitting back here with my new friend. It smells like horses up there.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Gidwin Goldbraids ');
INSERT INTO `creature_text` VALUES (45431, 8, 0, 'We just rested a few minutes ago! Let\'s just go to Light\'s Hope Chapel!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Gidwin Goldbraids ');
INSERT INTO `creature_text` VALUES (45431, 9, 0, 'We won\'t lose a horse. Look at them! They look exactly like they did when we started.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Gidwin Goldbraids ');
INSERT INTO `creature_text` VALUES (45445, 4, 0, 'Ого! Это я его – или он сам так быстро сдулся?', 12, 0, 100, 0, 0, 0, 50400, 5414, 5414, 0, 'Таренар Удар Солнца to Гнилостень Ступеней Смерти');
INSERT INTO `creature_text` VALUES (45447, 0, 0, 'I have been taken prisoner by the Twilight\'s Hammer.', 14, 0, 100, 0, 0, 0, 45614, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45447, 1, 0, 'I\'m not certain where. Start with their western base overlooking Highperch.', 14, 0, 100, 0, 0, 0, 45615, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45447, 2, 0, 'Stop! You have bested my wind serpent.', 14, 0, 100, 0, 0, 0, 45613, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45447, 3, 0, 'Спасибо за подарки. Посмотрим, чего ты стоишь, $c!', 14, 0, 100, 0, 0, 0, 45611, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45574, 0, 0, 'Братья! Атакуйте |3-3($r)!', 14, 0, 100, 23, 0, 0, 45708, 0, 0, 0, 'Vex\'tul');
INSERT INTO `creature_text` VALUES (45612, 0, 0, 'ВЫ С УМА СОШЛИ!', 12, 0, 100, 5, 0, 0, 44713, 0, 0, 0, 'Cromush');
INSERT INTO `creature_text` VALUES (45612, 1, 0, 'Как прикажешь, вождь!', 12, 0, 100, 66, 0, 0, 44738, 0, 0, 0, 'Cromush');
INSERT INTO `creature_text` VALUES (45700, 0, 0, '%s attempts to run away in fear!', 2, 0, 100, 0, 0, 0, 1150, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` VALUES (45704, 0, 0, '%s понимает, что ее заметили, и притворяется мертвой!', 16, 0, 100, 0, 0, 0, 46485, 0, 0, 0, 'Затаившаяся буря to Затаившаяся буря');
INSERT INTO `creature_text` VALUES (45707, 0, 0, 'Let the trial begin. Rise, champions! Rise, and meet these three in combat!', 12, 0, 100, 5, 0, 0, 45805, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45707, 1, 0, 'Your warm-up is complete. Now, the real trial begins!', 12, 0, 100, 0, 0, 0, 45806, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45707, 2, 0, 'Well-done, heroes. You have passed my test of combat.', 12, 0, 100, 5, 0, 0, 45812, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45772, 0, 0, '%s attempts to run away in fear!', 2, 0, 100, 0, 0, 0, 1150, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` VALUES (45809, 0, 0, '%s is trying to escape in a panic!', 16, 0, 100, 0, 0, 0, 1150, 0, 0, 0, 'Береговой макрура');
INSERT INTO `creature_text` VALUES (45847, 3, 0, 'Все в порядке... с тобой все будет хорошо.', 12, 0, 100, 1, 0, 0, 46503, 11445, 11445, 0, 'Участник команды С.П.А.С.');
INSERT INTO `creature_text` VALUES (45870, 1, 0, 'It shall be I that earns the favor of our lord by casting out the intruders. My calmest wind shall still prove too much for them!', 14, 0, 0, 0, 0, 0, 50596, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45870, 2, 0, 'Begone, outsider!', 14, 0, 0, 0, 0, 0, 50597, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45870, 3, 0, 'Your presence shall no longer defile our home!', 14, 0, 0, 0, 0, 0, 50598, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45870, 4, 0, 'My power grows feeble, brothers. I shamefully must rely on you for a time.', 14, 0, 0, 0, 0, 0, 50601, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45870, 5, 0, 'You think to outrun the wind? A fatal mistake.', 14, 0, 0, 0, 0, 0, 50599, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45870, 6, 0, 'The power of our winds, UNLEASHED!', 14, 0, 0, 0, 0, 0, 50600, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45871, 1, 0, 'The honor of slaying the interlopers shall be mine, brothers! Their feeble bodies will freeze solid from my wind\'s icy chill!', 14, 0, 0, 0, 0, 0, 50589, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45871, 2, 0, 'Frozen solid.', 14, 0, 0, 0, 0, 0, 50590, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45871, 3, 0, 'Another mortal has taken their last breath!', 14, 0, 0, 0, 0, 0, 50591, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45871, 4, 0, 'Brothers, beware! These mortals are dangerous. I must pause and gather my strength.', 14, 0, 0, 0, 0, 0, 50595, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45871, 5, 0, 'You throw away your honor and flee as cowards? Then die!', 14, 0, 0, 0, 0, 0, 50592, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45872, 1, 0, 'As I am the strongest wind, it shall be I that tears the invaders apart!', 14, 0, 0, 0, 0, 0, 50602, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45872, 2, 0, 'Mere dust...', 14, 0, 0, 0, 0, 0, 50604, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45872, 3, 0, 'Blown away!', 14, 0, 0, 0, 0, 0, 50603, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45872, 4, 0, 'The intruders stand fast, brothers, I cannot break them. Allow me a brief respite to strengthen my winds.', 14, 0, 0, 0, 0, 0, 50607, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45872, 5, 0, 'Why do you flee, mortals? There is nowhere you can run or hide here!', 14, 0, 0, 0, 0, 0, 50606, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (45878, 2, 0, 'Lorna, is that how you greet old friends?', 12, 0, 100, 0, 0, 0, 46073, 0, 0, 0, 'SmartText');
INSERT INTO `creature_text` VALUES (45878, 5, 0, 'My kingdom? My kingdom is no more.', 12, 0, 100, 0, 0, 0, 46076, 0, 0, 0, 'SmartText');
INSERT INTO `creature_text` VALUES (45878, 6, 0, 'Your father and Greymane saw to that... No, Gilneas died when they allowed murderous beasts to reign free. Unchecked.', 12, 0, 100, 0, 0, 0, 46077, 0, 0, 0, 'SmartText');
INSERT INTO `creature_text` VALUES (45878, 7, 0, 'Now lay down your arms and surrender. We are not here to kill you. Not yet.', 12, 0, 100, 0, 0, 0, 46078, 0, 0, 0, 'SmartText');
INSERT INTO `creature_text` VALUES (45878, 9, 0, 'Quickly! We must head back to the Forsaken Front before we are discovered!', 12, 0, 100, 0, 0, 0, 46084, 0, 0, 0, 'SmartText');
INSERT INTO `creature_text` VALUES (45910, 1, 0, 'They struck from the shadows. We didn\'t stand a chance.', 12, 0, 100, 0, 0, 0, 45939, 0, 0, 0, 'SmartText');
INSERT INTO `creature_text` VALUES (45941, 0, 0, 'Вот мы и прибыли!', 12, 0, 100, 0, 0, 0, 45602, 0, 0, 0, 'Фиона to Караван Фионы');
INSERT INTO `creature_text` VALUES (45941, 3, 0, 'Смотри, слева какая-то башня. Может, остановимся и передохнем?', 12, 0, 100, 1, 0, 0, 45596, 98174, 98174, 0, 'Фиона to Караван Фионы');
INSERT INTO `creature_text` VALUES (45966, 3, 0, 'Wir müssen durch diese hirnlosen Lepragnome durchbrechen, wenn wir hier lebend wieder herauskommen wollen!', 12, 0, 100, 25, 0, 0, 46382, 6063, 6063, 0, 'Nevin Drallzang to Player');
INSERT INTO `creature_text` VALUES (45966, 3, 0, 'Нужно прорваться через ряды этих безумных лепрогномов, если мы хотим выбраться отсюда живыми!', 12, 0, 100, 25, 0, 0, 46382, 14000, 14000, 0, 'Невин Крутилкинс to Player');
INSERT INTO `creature_text` VALUES (45967, 4, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 1150, 4469, 4469, 0, 'Marshfin Murkdweller');
INSERT INTO `creature_text` VALUES (45967, 4, 0, '%s is trying to escape in a panic!', 16, 0, 100, 0, 0, 0, 1150, 8000, 8000, 0, 'Темножитель Болотных Плавников');
INSERT INTO `creature_text` VALUES (45979, 0, 0, 'The cannon was rigged with an explosive trap!', 41, 0, 100, 0, 0, 0, 48790, 0, 0, 0, 'General Purpose Bunny JMF to Player');
INSERT INTO `creature_text` VALUES (45979, 1, 0, 'A shadowy figure appears in the ship\'s cabin!', 41, 0, 100, 0, 0, 0, 48276, 0, 0, 0, 'General Purpose Bunny JMF to Player');
INSERT INTO `creature_text` VALUES (45992, 0, 0, 'Do as the master commands, Theralion! Kill them!', 14, 0, 0, 0, 0, 21894, 47523, 0, 0, 0, 'VO_BT_Valiona_Event01');
INSERT INTO `creature_text` VALUES (45992, 1, 0, 'You are worthless, Theralion!', 14, 0, 0, 0, 0, 21895, 47524, 0, 0, 0, 'VO_BT_Valiona_Event02');
INSERT INTO `creature_text` VALUES (45992, 2, 0, 'Breathe your dying breath!', 14, 0, 0, 0, 0, 21896, 47959, 0, 0, 0, 'VO_BT_Valiona_Event03');
INSERT INTO `creature_text` VALUES (45992, 3, 0, 'At least Theralion dies with me...', 14, 0, 0, 0, 0, 21897, 47960, 0, 0, 0, 'VO_BT_Valiona_Event04');
INSERT INTO `creature_text` VALUES (45992, 4, 0, 'Theralion! I will engulf the Hallway! Cover their escape!', 14, 0, 0, 0, 0, 21898, 47961, 0, 0, 0, 'VO_BT_Valiona_Event05');
INSERT INTO `creature_text` VALUES (45992, 5, 0, 'Enter Twilight!', 14, 0, 0, 0, 0, 21899, 47962, 0, 0, 0, 'VO_BT_Valiona_Event06');
INSERT INTO `creature_text` VALUES (45993, 0, 0, 'The master was clearly speaking to you, Valiona. I am far too busy to attack ANYONE.', 14, 0, 0, 0, 0, 20300, 47521, 0, 0, 0, 'VO_BT_Theralion_Event01');
INSERT INTO `creature_text` VALUES (45993, 1, 0, 'How dare you call me worthless! You will see why I am Mothers favored child!', 14, 0, 0, 0, 0, 20301, 47522, 0, 0, 0, 'VO_BT_Theralion_Event02');
INSERT INTO `creature_text` VALUES (45993, 2, 0, 'Bare your soul to me, mortal.', 14, 0, 0, 0, 0, 20302, 47908, 0, 0, 0, 'VO_BT_Theralion_Event03');
INSERT INTO `creature_text` VALUES (45993, 3, 0, 'I knew I should have stayed out of this...', 14, 0, 0, 0, 0, 20303, 47910, 0, 0, 0, 'VO_BT_Theralion_Event04');
INSERT INTO `creature_text` VALUES (45993, 4, 0, 'You are not the boss of me Valiona! I will engulf as I please!', 14, 0, 0, 0, 0, 20304, 47911, 0, 0, 0, 'VO_BT_Theralion_Event05');
INSERT INTO `creature_text` VALUES (45993, 5, 0, 'There is no escape from the fury of the Twilight Flight!', 14, 0, 0, 0, 0, 20305, 47912, 0, 0, 0, 'VO_BT_Theralion_Event06');
INSERT INTO `creature_text` VALUES (45993, 6, 0, 'Writhe in AGONY!', 14, 0, 0, 0, 0, 20306, 47913, 0, 0, 0, 'VO_BT_Theralion_Event07');
INSERT INTO `creature_text` VALUES (45993, 7, 0, 'Theralion cackles maniacally.', 16, 0, 0, 0, 0, 20307, 0, 0, 0, 0, 'VO_BT_Theralion_Laugh1');
INSERT INTO `creature_text` VALUES (45997, 1, 0, 'So the Banshee Queen has sent assassins?', 12, 0, 100, 0, 0, 0, 46072, 0, 0, 0, 'SmartText');
INSERT INTO `creature_text` VALUES (45997, 3, 0, 'Godfrey? So the rumors are true.', 12, 0, 100, 0, 0, 0, 46074, 0, 0, 0, 'SmartText');
INSERT INTO `creature_text` VALUES (45997, 4, 0, 'You would betray your kingdom out of spite?', 12, 0, 100, 0, 0, 0, 46075, 0, 0, 0, 'SmartText');
INSERT INTO `creature_text` VALUES (45997, 8, 0, 'HAH! You\'ll have to kill me! DIE!', 12, 0, 100, 0, 0, 0, 46079, 0, 0, 0, 'SmartText');
INSERT INTO `creature_text` VALUES (46083, 0, 0, '%s изменяется, мерцая.', 16, 0, 100, 0, 0, 0, 9793, 0, 0, 0, 'Дракобель-полукровка to 0');
INSERT INTO `creature_text` VALUES (46134, 0, 0, '%s attempts to run away in fear!', 2, 0, 100, 0, 0, 0, 1150, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` VALUES (46149, 3, 0, 'Blowin \'em to bits, boss!', 12, 0, 100, 0, 0, 0, 46330, 4937, 4937, 0, 'Southsea Grenadier to Tony Two-Tusk');
INSERT INTO `creature_text` VALUES (46154, 3, 0, 'Aye aye!', 12, 0, 100, 113, 0, 0, 46326, 4766, 4766, 0, 'Southsea Burnmaster to Tony Two-Tusk');
INSERT INTO `creature_text` VALUES (46173, 0, 0, 'Кто это сказал, что победа твоя? Мы еще не подсчитали очки.', 12, 0, 100, 0, 0, 0, 45579, 0, 0, 0, 'Гидвин Златокос to Караван Фионы');
