INSERT INTO `creature_text` VALUES (47299, 0, 0, 'Чурбан пойдет на поиски настоящих приключений!', 14, 0, 100, 0, 0, 0, 47425, 0, 0, 0, 'Чурбан to Player');
INSERT INTO `creature_text` VALUES (47299, 0, 0, 'Чурбану не нравятся все эти убийства! Чурбан уходит!', 14, 0, 100, 0, 0, 0, 47424, 0, 0, 0, 'Чурбан to Player');
INSERT INTO `creature_text` VALUES (47299, 3, 0, 'Lunk going to find some real adventures!', 14, 0, 100, 22, 0, 0, 47425, 8641, 8641, 0, 'Lunk to Player');
INSERT INTO `creature_text` VALUES (47301, 0, 0, 'Dig wide, dig deep.  Seek out the places of fire beneath the world.  My brothers and sisters yearn to be free.', 14, 0, 100, 0, 0, 0, 47692, 0, 0, 0, 'Image of Archduke Calcinder to Player');
INSERT INTO `creature_text` VALUES (47301, 0, 0, 'You dwarves are worthless!  You come to Ragnaros seeking ascendance, but you have nothing to offer!', 14, 0, 100, 0, 0, 0, 47690, 0, 0, 0, 'Image of Archduke Calcinder to Player');
INSERT INTO `creature_text` VALUES (47301, 0, 0, 'Ничего, кроме ваших лопат и спин. Так что будете копать.', 14, 0, 100, 0, 0, 0, 47691, 0, 0, 0, 'Проекция эрцгерцога Кальциндера to Player');
INSERT INTO `creature_text` VALUES (47301, 3, 0, 'Nothing to offer, except your backs and your shovels.  So you will dig.', 14, 0, 100, 0, 0, 0, 47691, 7781, 7781, 0, 'Image of Archduke Calcinder to Player');
INSERT INTO `creature_text` VALUES (47301, 3, 0, 'Succeed, and you may ascend after all.  Fail, and burn.', 14, 0, 100, 0, 0, 0, 47693, 19125, 19125, 0, 'Image of Archduke Calcinder to Player');
INSERT INTO `creature_text` VALUES (47301, 3, 0, 'Копайте глубже. Ищите подземные скопления огня. Мои братья и сестры тоскуют по свободе.', 14, 0, 100, 0, 0, 0, 47692, 13000, 13000, 0, 'Проекция эрцгерцога Кальциндера to Player');
INSERT INTO `creature_text` VALUES (47301, 3, 0, 'От вас, дворфов, никакого толку! Вы стремитесь к власти, но вам нечего предложить взамен!', 14, 0, 100, 0, 0, 0, 47690, 2000, 2000, 0, 'Проекция эрцгерцога Кальциндера to Player');
INSERT INTO `creature_text` VALUES (47301, 3, 0, 'Преуспеешь – возвысишься. Не сможешь – сгоришь.', 14, 0, 100, 0, 0, 0, 47693, 19000, 19000, 0, 'Проекция эрцгерцога Кальциндера to Player');
INSERT INTO `creature_text` VALUES (47309, 1, 0, 'Ты будешь страдать! На тебя обрушится ярость Сумеречного Молота!', 12, 0, 100, 0, 0, 0, 47704, 0, 0, 0, 'Министр Киюби');
INSERT INTO `creature_text` VALUES (47309, 3, 0, 'You will suffer!  You will face the wrath of the Twilight\'s Hammer!', 12, 0, 100, 0, 0, 0, 47704, 34188, 34188, 0, 'Minister Kiyuubi');
INSERT INTO `creature_text` VALUES (47324, 0, 0, 'Победа на Тол Бараде за нами! Пока армия Орды зализывает раны, нам срочно нужна твоя помощь в сборе ресурсов и очистке острова от врагов!', 14, 0, 100, 0, 0, 0, 50894, 0, 0, 0, 'Толбарадский страж');
INSERT INTO `creature_text` VALUES (47333, 3, 0, 'He will be sleepy for a while.  Lunk know.  Lunk do before.', 12, 0, 100, 0, 0, 0, 47526, 138890, 234515, 88320, 'Lunk to Dark Iron Steamsmith');
INSERT INTO `creature_text` VALUES (47333, 3, 0, 'Here you go.', 12, 0, 100, 0, 0, 0, 86525, 222359, 222359, 88320, 'Lunk to Dark Iron Steamsmith');
INSERT INTO `creature_text` VALUES (47333, 3, 0, 'Here, I stealed this from little dwarf.', 12, 0, 100, 0, 0, 0, 47525, 160797, 160797, 88320, 'Lunk to Dark Iron Steamsmith');
INSERT INTO `creature_text` VALUES (47333, 3, 0, 'Here, you can have dwarf\'s things.', 12, 0, 100, 0, 0, 0, 47527, 18672, 18672, 88320, 'Lunk to Dark Iron Steamsmith');
INSERT INTO `creature_text` VALUES (47333, 3, 0, 'No!  No kill!', 12, 0, 100, 1, 0, 0, 47700, 92765, 197562, 0, 'Lunk to Player');
INSERT INTO `creature_text` VALUES (47333, 3, 0, 'Oh no!  Why do you kill it?', 12, 0, 100, 1, 0, 0, 47703, 172375, 172375, 0, 'Lunk to Player');
INSERT INTO `creature_text` VALUES (47333, 3, 0, 'Please, no kill when Lunk is around.', 12, 0, 100, 1, 0, 0, 47702, 64234, 64234, 0, 'Lunk to Player');
INSERT INTO `creature_text` VALUES (47333, 3, 0, 'You hurt Lunk\'s feelings when you kill.', 12, 0, 100, 1, 0, 0, 47701, 30781, 30781, 0, 'Lunk to Player');
INSERT INTO `creature_text` VALUES (47333, 3, 0, 'Бери у дворфов вещи. Не надо убивать.', 12, 0, 100, 0, 0, 0, 47528, 71000, 71000, 88320, 'Чурбан to Кузнец из клана Черного Железа');
INSERT INTO `creature_text` VALUES (47333, 3, 0, 'Вот, теперь ты можешь забрать у дворфов вещи.', 12, 0, 100, 0, 0, 0, 47527, 34000, 89000, 88320, 'Чурбан to Кузнец из клана Черного Железа');
INSERT INTO `creature_text` VALUES (47333, 3, 0, 'Вот, я стащил это у маленького дворфа.', 12, 0, 100, 0, 0, 0, 47525, 10000, 10000, 88320, 'Чурбан to Кузнец из клана Черного Железа');
INSERT INTO `creature_text` VALUES (47333, 3, 0, 'Ладно.', 12, 0, 100, 0, 0, 0, 111531, 58000, 129000, 88320, 'Чурбан to Кузнец из клана Черного Железа');
INSERT INTO `creature_text` VALUES (47333, 3, 0, 'Сидеть проще, чем убивать.', 12, 0, 100, 0, 0, 0, 47529, 109000, 109000, 88320, 'Чурбан to Кузнец из клана Черного Железа');
INSERT INTO `creature_text` VALUES (47335, 0, 0, 'Victory in Tol Barad is ours! While the Alliance offensive is crippled, we need your immediate efforts to gather resources and secure the island!', 14, 0, 100, 0, 0, 0, 50895, 0, 0, 0, 'Baradin Grunt');
INSERT INTO `creature_text` VALUES (47335, 0, 0, 'Победа на Тол Бараде за нами! Пока армия Альянса зализывает раны, нам срочно нужна твоя помощь в сборе ресурсов и очистке острова от врагов!', 14, 0, 100, 0, 0, 0, 50895, 0, 0, 0, 'Толбарадский рубака');
