INSERT INTO `creature_text` VALUES (47442, 0, 0, 'Джонни Потрясный прибыл, филистимлянин. Давай свои дурацкие задания, которые я выполню исключительно из чистого энтузиазма, презрев все твои указания относительно ориентирования на местности, потому что моя карта довольно условна.', 12, 0, 100, 1, 0, 0, 47682, 0, 0, 0, 'Johnny Awesome');
INSERT INTO `creature_text` VALUES (47442, 1, 0, 'Хорошо, я сделаю то единственное, о чем ты меня просишь. Но постарайся найти для меня еще какую-нибудь неквалифицированную работу, иначе ты будешь видеть меня снова и снова, а мои комментарии на форумах тебе и вовсе не понравятся.', 12, 0, 100, 1, 0, 0, 47689, 0, 0, 0, 'Johnny Awesome');
INSERT INTO `creature_text` VALUES (47443, 0, 0, 'ЧУЕШЬ, ЧЕМ ПАХНЕТ ЛОК\'ТАР?!', 14, 0, 100, 22, 0, 0, 47668, 0, 0, 0, ' Kingslayer Orkus');
INSERT INTO `creature_text` VALUES (47443, 1, 0, 'ХА! Похоже, у вас кончились дураки, которых бы можно было посадить на эту лошадь, Дарталия. Большего задохлика я не видел.', 12, 0, 100, 11, 0, 0, 47669, 0, 0, 0, 'Kingslayer Orkus');
INSERT INTO `creature_text` VALUES (47443, 2, 0, 'Что у тебя сегодня, слабак?! Покажи мне, где укрытие Альянса, и я ОДЕРЖУ НАД НИМИ ВЕРХ!', 12, 0, 100, 5, 0, 0, 47670, 0, 0, 0, 'Kingslayer Orkus');
INSERT INTO `creature_text` VALUES (47443, 3, 0, 'Да, трусишка, сиди на своей белой лошадке, пока Оркус принесет Орде славу! Я вернусь с тысячей трофейных черепов!', 12, 0, 100, 25, 0, 0, 47679, 0, 0, 0, 'Kingslayer Orkus');
INSERT INTO `creature_text` VALUES (47444, 0, 0, 'ЭЙ! ЭЙ! ПОМОГИТЕ!', 12, 0, 100, 3, 0, 0, 47660, 0, 0, 0, 'Dumass');
INSERT INTO `creature_text` VALUES (47444, 1, 0, 'А, СЕВЕР! ПОНЯЛ! СПАСИБО! ПОКА! СПАСИБО!', 12, 0, 100, 3, 0, 0, 47665, 0, 0, 0, 'Dumass');
INSERT INTO `creature_text` VALUES (47462, 0, 0, 'The flames of Ragnaros will burn you all.', 14, 0, 100, 0, 0, 0, 50366, 0, 0, 0, 'Archduke Calcinder to Player');
INSERT INTO `creature_text` VALUES (47462, 1, 0, 'Fools!  You cannot defeat me!  I will only return again...', 14, 0, 100, 0, 0, 0, 47657, 0, 0, 0, 'Archduke Calcinder to 0');
INSERT INTO `creature_text` VALUES (47462, 6, 0, 'NOOOOOO!  THIS CANNOT BE!  I... AM... IMMORTAL...!', 14, 0, 100, 0, 0, 0, 47656, 0, 0, 0, 'Archduke Calcinder to Player');
INSERT INTO `creature_text` VALUES (47462, 8, 0, 'Все вы сгорите в пламени Рагнароса.', 14, 0, 100, 0, 0, 0, 50366, 0, 0, 0, 'Эрцгерцог Кальциндер to Player');
INSERT INTO `creature_text` VALUES (47462, 9, 0, '%s слабеет! Теперь отгоняй его!', 41, 0, 100, 0, 0, 0, 47654, 0, 0, 0, 'Эрцгерцог Кальциндер to 0');
INSERT INTO `creature_text` VALUES (47462, 10, 0, 'НЕЕЕТ! ЭТОГО НЕ МОЖЕТ БЫТЬ! Я... ЖЕ... БЕССМЕРТЕН...', 14, 0, 100, 0, 0, 0, 47656, 0, 0, 0, 'Эрцгерцог Кальциндер to Player');
INSERT INTO `creature_text` VALUES (47467, 3, 0, 'Очисти террасу для посадки! Стреляй в защитников Котлована!', 42, 0, 100, 0, 0, 0, 47787, 29000, 29000, 88645, 'Ветролет Братства Тория to Player');
INSERT INTO `creature_text` VALUES (47556, 0, 0, 'Time to do what I do best... Ruuuuuuuuuuuuuunnn!', 12, 0, 0, 1, 0, 0, 48677, 0, 0, 0, 'RandomMoveText');
