INSERT INTO `creature_text` VALUES (47621, 0, 0, 'Good luck to you. I\'m grateful.', 12, 0, 100, 0, 0, 0, 47875, 0, 0, 0, 'SmartText');
INSERT INTO `creature_text` VALUES (47626, 0, 0, 'You will...NOT find her...until it is too late...', 14, 0, 0, 0, 0, 19875, 48010, 0, 0, 0, 'VO_DM_Ripsnarl_Death01');
INSERT INTO `creature_text` VALUES (47626, 1, 0, 'Your blood only increases my hunger!', 14, 0, 0, 0, 0, 19876, 48009, 0, 0, 0, 'VO_DM_Ripsnarl_Kill01');
INSERT INTO `creature_text` VALUES (47626, 2, 0, 'Do you feel that chill running up your spine? The fog is rolling in...', 14, 0, 0, 0, 0, 19877, 0, 0, 0, 0, 'VO_DM_Ripsnarl_Spell01');
INSERT INTO `creature_text` VALUES (47626, 4, 0, 'I can smell your fear...', 14, 0, 0, 0, 0, 19879, 48006, 0, 0, 0, 'VO_DM_Ripsnarl_Spell03');
