INSERT INTO `creature_text` VALUES (47626, 7, 0, 'Ah, fresh meat!', 14, 0, 0, 0, 0, 19882, 48179, 0, 0, 0, 'VO_DM_Ripsnarl_Aggro01');
INSERT INTO `creature_text` VALUES (47684, 0, 0, 'Welcome to my lands, $r. The little one told me you seek information on the legend of Irmaat.', 12, 0, 100, 1, 0, 0, 104090, 0, 0, 0, 'Король Фаорис to Player');
INSERT INTO `creature_text` VALUES (47699, 0, 0, '%s attempts to run away in fear!', 2, 0, 100, 0, 0, 0, 1150, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` VALUES (47724, 1, 0, 'Бури небесные, повинуйтесь Сумеречному Молоту!', 12, 0, 100, 0, 0, 0, 50458, 0, 0, 0, 'Сумеречный заветренник to Player');
INSERT INTO `creature_text` VALUES (47730, 0, 0, '%s attempts to run away in fear!', 2, 0, 100, 0, 0, 0, 1150, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` VALUES (47733, 1, 0, 'Ты уверен в этом, охотник на демонов? Ты уверен, что действуешь по собственной воле?', 12, 0, 100, 0, 0, 0, 48017, 0, 0, 0, 'Arthas');
INSERT INTO `creature_text` VALUES (47733, 2, 0, 'Впрочем, подойди. Ты увидишь, что мы похожи.', 12, 0, 100, 0, 0, 0, 48019, 0, 0, 0, 'Arthas');
INSERT INTO `creature_text` VALUES (47733, 3, 0, 'Этой армией нежити командует повелитель ужаса Тихондрий. В его власти находится могущественный артефакт чернокнижников под названием череп Гул\'дана. Это он виновен в порче, оскверняющей этот лес.', 12, 0, 100, 0, 0, 0, 48267, 0, 0, 0, 'Arthas');
INSERT INTO `creature_text` VALUES (47733, 4, 0, 'Скажем так, я не питаю любви к Тихондрию. А моему повелителю... выгодно падение Легиона', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Arthas');
INSERT INTO `creature_text` VALUES (47735, 1, 0, 'По истечении десяти тысяч лет я свободен, хотя мой собственный брат до сих пор и считает меня злодеем! Я покажу ему мою истинную силу! Я покажу ему, что демоны больше не имеют надо мной власти!', 12, 0, 100, 0, 0, 0, 48016, 0, 0, 0, 'Illidan Stormrage');
INSERT INTO `creature_text` VALUES (47735, 2, 0, 'От тебя пахнет смертью, человек. Ты пожалеешь о том, что приблизился ко мне.', 12, 0, 100, 0, 0, 0, 48018, 0, 0, 0, 'Illidan Stormrage');
INSERT INTO `creature_text` VALUES (47735, 3, 0, 'Так мы можем драться целую вечность. Что тебе на самом деле нужно?', 12, 0, 100, 0, 0, 0, 48020, 0, 0, 0, 'Illidan Stormrage');
INSERT INTO `creature_text` VALUES (47735, 4, 0, 'И ты хочешь, чтобы я украл его. Зачем?', 12, 0, 100, 0, 0, 0, 48023, 0, 0, 0, 'Illidan Stormrage');
INSERT INTO `creature_text` VALUES (47735, 5, 0, 'Вот здесь хранится череп Гул\'дана! Эти жалкие демоны не помешают мне завладеть им!', 12, 0, 100, 0, 0, 0, 48080, 0, 0, 0, 'Illidan Stormrage');
INSERT INTO `creature_text` VALUES (47735, 6, 0, 'Теперь по крайней мере демоны не будут осквернять эти леса. Но если я завладею этой силой, то ни один из лейтенантов Архимонда не сравнится со мной.', 12, 0, 100, 0, 0, 0, 48081, 0, 0, 0, 'Illidan Stormrage');
INSERT INTO `creature_text` VALUES (47735, 7, 0, 'Да... эта сила должна быть моей!', 12, 0, 100, 398, 0, 0, 48082, 0, 0, 0, 'Illidan Stormrage');
INSERT INTO `creature_text` VALUES (47735, 8, 0, 'Теперь я закончил!', 14, 0, 100, 419, 0, 0, 48083, 0, 0, 0, 'Illidan Stormrage');
INSERT INTO `creature_text` VALUES (47762, 0, 0, '%s attempts to run away in fear!', 2, 0, 100, 0, 0, 0, 1150, 0, 0, 0, 'combat Flee');
INSERT INTO `creature_text` VALUES (47779, 3, 0, 'As you wish.  Just make sure they don\'t come to no harm.', 12, 0, 100, 1, 0, 0, 48781, 490438, 490438, 0, 'Mouton Flamestar to Player');
INSERT INTO `creature_text` VALUES (47779, 3, 0, 'Как хочешь. Просто смотри, чтобы они не пострадали.', 12, 0, 100, 1, 0, 0, 48781, 211000, 211000, 0, 'Мутон Пламя Звезд to Player');
