INSERT INTO `creature_text` VALUES (48964, 3, 0, 'А, Химерон – вот живое воплощение моих научных достижений! За эти годы я бессчетное число раз менял его внешний вид, и результат превзошел все ожидания.', 14, 0, 100, 0, 0, 23362, 49063, 3250, 3250, 0, 'Лорд Виктор Нефарий to Player');
INSERT INTO `creature_text` VALUES (48964, 3, 0, 'Битва становится какой-то пресной. Позвольте-ка мне оживить ее!', 14, 0, 100, 0, 0, 23365, 49066, 16891, 16891, 0, 'Лорд Виктор Нефарий to Химерон');
INSERT INTO `creature_text` VALUES (48964, 3, 0, 'Как жаль! Эксперимент не удался.', 14, 0, 100, 0, 0, 23361, 49067, 23891, 23891, 0, 'Лорд Виктор Нефарий to Player');
INSERT INTO `creature_text` VALUES (48964, 3, 0, 'Смертные, которые считают себя ГЕРОЯМИ, вошли в Разрушенный зал. Надеюсь, этот отряд повеселит меня больше, чем вчерашний.', 14, 0, 100, 0, 0, 23379, 49053, 3625, 3625, 0, 'Лорд Виктор Нефарий to Player');
INSERT INTO `creature_text` VALUES (49023, 1, 0, 'Прочь, грязная Орда!', 12, 0, 100, 0, 0, 0, 50165, 0, 0, 0, 'Стражник Высокого берега to Закаленный пехотинец');
