INSERT INTO `creature_text` VALUES (49141, 0, 0, 'Нет, не здесь.', 12, 0, 100, 0, 0, 0, 49300, 0, 0, 0, 'Дарнелл to Player');
INSERT INTO `creature_text` VALUES (49178, 3, 0, 'ЕДА для Гролнара!', 14, 0, 100, 0, 0, 0, 49256, 25147, 25147, 0, 'Гролнар Берсерк to Player');
INSERT INTO `creature_text` VALUES (49226, 0, 0, 'Welcome to my personal sanctum, heroes! I hope you find the accommodations to your liking. Herein you will find many of my failed experiments and ultimately my greatest triumph!', 14, 0, 0, 0, 0, 20090, 49415, 0, 0, 0, 'VO_BD_Nefarian_Event22');
INSERT INTO `creature_text` VALUES (49226, 1, 0, 'Mortals that fancy themselves HEROES have entered the Broken Hall! Oh, I do hope this \'raid\' will amuse me more than the last!', 14, 0, 0, 0, 0, 23379, 49053, 0, 0, 0, 'VO_BD_Nefarian_ZoneIntro01');
INSERT INTO `creature_text` VALUES (49226, 2, 0, 'I should\'ve known better than to rely on something so stupidly named... to entertain me for long.', 14, 0, 0, 0, 0, 23373, 49059, 0, 0, 0, 'VO_BD_Nefarian_OmnitronDead01');
INSERT INTO `creature_text` VALUES (49226, 3, 0, 'Hmm, the Omnotron Defense System. Centuries ago, these constructs were considered the dwarves greatest tactical achievements. With so many counters to each construct\'s attacks, I\'ll have to rectify these designs for them to provide me ANY entertainment!', 14, 0, 0, 0, 0, 23374, 49054, 0, 0, 0, 'VO_BD_Nefarian_OmnitronIntro01');
INSERT INTO `creature_text` VALUES (49226, 4, 0, 'You would like to move away from that, wouldn\'t you? Too bad.', 14, 0, 0, 0, 0, 23375, 49055, 0, 0, 0, 'VO_BD_Nefarian_OmnitronSpell01');
INSERT INTO `creature_text` VALUES (49226, 5, 0, 'Stupid dwarves and your fascination with runes. Why would you create something that would help your enemy?', 14, 0, 0, 0, 0, 23376, 49056, 0, 0, 0, 'VO_BD_Nefarian_OmnitronSpell02');
INSERT INTO `creature_text` VALUES (49226, 6, 0, 'Lightning Conductor works much better in a crowd, yes?', 14, 0, 0, 0, 0, 23377, 49057, 0, 0, 0, 'VO_BD_Nefarian_OmnitronSpell03');
INSERT INTO `creature_text` VALUES (49226, 7, 0, 'Were you planing on using Toxitron\'s chemicals to damage the other constructs? Clever plan. Let me ruin that for you.', 14, 0, 0, 0, 0, 23378, 49058, 0, 0, 0, 'VO_BD_Nefarian_OmnitronSpell04');
INSERT INTO `creature_text` VALUES (49226, 8, 0, 'That malfunctioning piece of junk was murder on the repair bills.', 14, 0, 0, 0, 0, 20086, 49411, 0, 0, 0, 'VO_BD_Nefarian_Event18');
INSERT INTO `creature_text` VALUES (49226, 9, 0, 'I found this fascinating speciman in the lava underneath this very room. Magmaw should provide an adequate challenge for your pathetic little band!', 14, 0, 0, 0, 0, 23367, 49060, 0, 0, 0, 'VO_BD_Nefarian_MagmawIntro01');
INSERT INTO `creature_text` VALUES (49226, 10, 0, 'Finally someone to deal with that unsightly pest! You have my thanks, \"heroes.\"', 14, 0, 0, 0, 0, 20085, 49410, 0, 0, 0, 'VO_BD_Nefarian_Event17');
INSERT INTO `creature_text` VALUES (49226, 11, 0, 'You won! How... disappointing. I could\'ve easily watched you all continue to stand in the fire.', 14, 0, 0, 0, 0, 23366, 49062, 0, 0, 0, 'VO_BD_Nefarian_MagmawDead01');
INSERT INTO `creature_text` VALUES (49226, 12, 0, 'INCONCEIVEABLE! You may actually defeat my lava worm! Perhaps I can help... TIP the scales.', 14, 0, 0, 0, 0, 23368, 49782, 0, 0, 0, 'VO_BD_Nefarian_MagmawLowHealth');
INSERT INTO `creature_text` VALUES (49226, 13, 0, 'On second thought, what fun is an adequate challenge?', 14, 0, 0, 0, 0, 23369, 49061, 0, 0, 0, 'VO_BD_Nefarian_MagmawSummon01');
INSERT INTO `creature_text` VALUES (49226, 14, 0, 'Maloriak, try not to lose to these mortals! Semi-competent help is SO hard to create.', 14, 0, 0, 0, 0, 23372, 49068, 0, 0, 0, 'VO_BD_Nefarian_MaloriakIntro01');
INSERT INTO `creature_text` VALUES (49226, 15, 0, 'Your mixtures are weak, Maloriak! They need a bit more... kick!', 14, 0, 0, 0, 0, 23370, 49069, 0, 0, 0, 'VO_BD_Nefarian_Maloriak01');
INSERT INTO `creature_text` VALUES (49226, 16, 0, 'Congratulations! Allow me to grant you a title befitting the amazing achievement you just performed! Henceforth, you shall be known as the Slayer of Incompetent, Stupid and Disappointing Minions!', 14, 0, 0, 0, 0, 23371, 49070, 0, 0, 0, 'VO_BD_Nefarian_MaloriakDead01');
INSERT INTO `creature_text` VALUES (49226, 17, 0, 'We never did see eye to eye... Good riddance I say!', 14, 0, 0, 0, 0, 20089, 49414, 0, 0, 0, 'VO_BD_Nefarian_Event21');
INSERT INTO `creature_text` VALUES (49226, 18, 0, 'You should\'ve been disposed of a long time ago.', 14, 0, 0, 0, 0, 23357, 49077, 0, 0, 0, 'VO_BD_Nefarian_AtramedesDead');
INSERT INTO `creature_text` VALUES (49226, 19, 0, 'These shields are proving quite the nuisance!', 14, 0, 0, 0, 0, 23358, 49076, 0, 0, 0, 'VO_BD_Nefarian_AtramedesGong');
INSERT INTO `creature_text` VALUES (49226, 20, 0, 'Atramedes, the heroes are RIGHT THERE!', 14, 0, 0, 0, 0, 23359, 49075, 0, 0, 0, 'VO_BD_Nefarian_AtramedesImp01');
INSERT INTO `creature_text` VALUES (49226, 21, 0, 'Atramedes, are you going deaf as well as blind? Hurry up and kill them all!', 14, 0, 0, 0, 0, 23360, 49074, 0, 0, 0, 'VO_BD_Nefarian_AtramedesIntro');
INSERT INTO `creature_text` VALUES (49226, 22, 0, 'Now that was really unnecessary.', 14, 0, 0, 0, 0, 23387, 49412, 0, 0, 0, 'VO_BD_Nefarian_Event19');
INSERT INTO `creature_text` VALUES (49226, 23, 0, 'Impressive! You managed to destroy one of my most horrific creations - a task I\'d thought impossible until now.', 14, 0, 0, 0, 0, 20088, 49413, 0, 0, 0, 'VO_BD_Nefarian_Event20');
INSERT INTO `creature_text` VALUES (49226, 24, 0, 'A shame to lose that experiment...', 14, 0, 0, 0, 0, 23361, 49067, 0, 0, 0, 'VO_BD_Nefarian_ChimaronDead01');
INSERT INTO `creature_text` VALUES (49226, 25, 0, 'Ah, Chimaeron! Truly a living testament to my scientific prowess. I reworked and twisted his form countless times over the years and the final result is truly something to behold.', 14, 0, 0, 0, 0, 23362, 49063, 0, 0, 0, 'VO_BD_Nefarian_ChimaronIntro01');
INSERT INTO `creature_text` VALUES (49226, 26, 0, 'Oh. You were expecting something else to happen just then, weren\'t you? Let\'s see how YOU adapt after a new set of adjustments, shall we?', 14, 0, 0, 0, 0, 23363, 49064, 0, 0, 0, 'VO_BD_Nefarian_ChimaronSpell01');
INSERT INTO `creature_text` VALUES (49226, 27, 0, 'Stop fighting yourself THIS INSTANT Chimaeron!', 14, 0, 0, 0, 0, 23364, 49065, 0, 0, 0, 'VO_BD_Nefarian_ChimaronSpell02');
INSERT INTO `creature_text` VALUES (49226, 28, 0, 'This fight has gone a bit stale. Allow me to spice things up!', 14, 0, 0, 0, 0, 23365, 49066, 0, 0, 0, 'VO_BD_Nefarian_ChimaronSpell03');
INSERT INTO `creature_text` VALUES (49337, 0, 0, 'Hello again.', 12, 0, 100, 3, 0, 0, 49455, 0, 0, 0, 'Darnell');
INSERT INTO `creature_text` VALUES (49337, 1, 0, 'I know the way to Deathknell. Come with me!', 12, 0, 100, 273, 0, 0, 0, 0, 0, 0, 'Darnell');
INSERT INTO `creature_text` VALUES (49337, 2, 0, 'Good, you\'re still here. Now, where\'s Deathguard Saltain?', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Darnell');
INSERT INTO `creature_text` VALUES (49337, 3, 0, 'Ah, here he is.', 12, 0, 100, 1, 0, 0, 49458, 0, 0, 0, 'Darnell');
INSERT INTO `creature_text` VALUES (49337, 4, 0, 'Let\'s get moving, $n. Saltain said that we\'d fine some corpses up here.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Darnell');
INSERT INTO `creature_text` VALUES (49337, 5, 0, 'I think I see some corpses up ahead. Let\'s go, $n! You do the searching and fighting. I\'ll do the lifting.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Darnell');
