INSERT INTO `creature_text` VALUES (49423, 0, 1, 'Look what they\'ve done to me!', 12, 0, 100, 0, 0, 0, 50325, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (49423, 0, 2, 'Who am I?', 12, 0, 100, 0, 0, 0, 50320, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (49423, 0, 3, 'You will die!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (49424, 0, 0, 'BLEEAAAGGHHH! I\'m a monster, don\'t look at me!', 14, 0, 100, 0, 0, 0, 50326, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (49426, 0, 0, 'Hi, $N! What do you think about my new gear?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (49426, 1, 0, 'They\'re making me into a Deathguard. Can you believe it?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (49426, 2, 0, 'I\'ll probably be staying here in Deathknell. If things work out, though... who knows? Maybe I\'ll get to meet the Dark Lady herself.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (49426, 3, 0, 'How about you? I\'ve heard that Sarvis is going to send you off to Calston Estate. Big assignment, eh?', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (49426, 4, 0, 'Well, I won\'t hold you up. It\'s been a pleasure, $N. I hope our paths cross again soon.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (49427, 0, 0, 'I found this amazing creature in the lava under this room. Magmar will be a worthy adversary for your little squad.', 14, 0, 100, 0, 0, 23367, 49060, 0, 0, 0, 'Нефариан to Магмарь');
INSERT INTO `creature_text` VALUES (49427, 1, 0, 'Непостижимо! Вы, кажется, можете уничтожить моего лавового червяка! Пожалуй, я помогу ему.', 14, 0, 100, 0, 0, 23368, 49782, 0, 0, 0, 'Нефариан to Магмарь');
INSERT INTO `creature_text` VALUES (49427, 2, 0, 'Вы победили? Вот... незадача. А мне было приятно смотреть, как вы жаритесь в огне.', 14, 0, 100, 0, 0, 23366, 49062, 0, 0, 0, 'Нефариан to Магмарь');
INSERT INTO `creature_text` VALUES (49429, 0, 0, 'Poor Glubtok. When his powers manifested, his own ogre mound was the first to burn.', 12, 0, 100, 0, 0, 24602, 49715, 0, 0, 0, 'Vanessa VanCleef glubtok 1');
INSERT INTO `creature_text` VALUES (49429, 1, 0, 'Deep within his soul, the one thing he feared most of all was...himself.', 12, 0, 100, 0, 0, 24603, 49716, 0, 0, 0, 'Vanessa VanCleef glubtok 2');
INSERT INTO `creature_text` VALUES (49429, 2, 0, 'Most rogues prefer to cloak themselves in the shadows, but not Helix.', 12, 0, 100, 0, 0, 24604, 49723, 0, 0, 0, 'Vanessa VanCleef helix 1');
INSERT INTO `creature_text` VALUES (49429, 3, 0, 'You never know what skitters in the darkness.', 12, 0, 100, 0, 0, 24605, 49724, 0, 0, 0, 'Vanessa VanCleef helix 2');
INSERT INTO `creature_text` VALUES (49429, 4, 0, 'Can you imagine the life of a machine?', 12, 0, 100, 0, 0, 24606, 49725, 0, 0, 0, 'Vanessa VanCleef foereaper 1');
INSERT INTO `creature_text` VALUES (49429, 5, 0, 'A simple spark can mean the difference between life...and death.', 12, 0, 100, 0, 0, 24607, 49726, 0, 0, 0, 'Vanessa VanCleef foereaper 2');
INSERT INTO `creature_text` VALUES (49429, 6, 0, 'Ripsnarl wasn\'\'t always a bloodthirsty savage. Once, he even had a family.', 12, 0, 100, 0, 0, 24608, 49742, 0, 0, 0, 'Vanessa VanCleef admiral 1');
INSERT INTO `creature_text` VALUES (49429, 7, 0, 'He was called James Harrington. A tragedy in three parts.', 12, 0, 100, 0, 0, 24609, 49745, 0, 0, 0, 'Vanessa VanCleef admiral 2');
INSERT INTO `creature_text` VALUES (49429, 8, 0, 'I\'ve been waiting a long time for this, you know.', 12, 0, 100, 0, 0, 24613, 49477, 0, 0, 0, 'Vanessa VanCleef intro 1');
INSERT INTO `creature_text` VALUES (49429, 9, 0, 'Biding my time, building my forces, studying the minds of my enemies.', 12, 0, 100, 0, 0, 24614, 49478, 0, 0, 0, 'Vanessa VanCleef intro 2');
INSERT INTO `creature_text` VALUES (49429, 10, 0, 'I was never very good at hand-to-hand combat, you know. Not like my father.', 12, 0, 100, 0, 0, 24615, 49479, 0, 0, 0, 'Vanessa VanCleef intro 3');
INSERT INTO `creature_text` VALUES (49429, 11, 0, 'But I always excelled at poisons.', 12, 0, 100, 0, 0, 24616, 49480, 0, 0, 0, 'Vanessa VanCleef intro 4');
INSERT INTO `creature_text` VALUES (49429, 12, 0, 'Especially venoms that affect the mind.', 12, 0, 100, 0, 0, 24617, 49481, 0, 0, 0, 'Vanessa VanCleef intro 5');
INSERT INTO `creature_text` VALUES (49448, 3, 0, 'Голдринн! Стало быть, это правда... Древние боги снова среди нас!', 12, 0, 100, 66, 0, 0, 49557, 19000, 19000, 0, 'Командир Джерод Песнь Теней to Player');
INSERT INTO `creature_text` VALUES (49448, 3, 0, 'Еще несколько минут... Кенарий вот-вот будет здесь!', 12, 0, 100, 5, 0, 0, 49555, 6000, 6000, 0, 'Командир Джерод Песнь Теней to Player');
INSERT INTO `creature_text` VALUES (49448, 3, 0, 'Скорее! Не позвольте им помешать магическому ритуалу Изеры!', 12, 0, 100, 1, 0, 0, 49598, 34000, 34000, 0, 'Командир Джерод Песнь Теней to Player');
INSERT INTO `creature_text` VALUES (49450, 3, 0, 'У вас не так много времени. Вражеское подкрепление уже в пути.', 12, 0, 100, 0, 0, 0, 49556, 11000, 11000, 92592, 'Голдринн to Player');
INSERT INTO `creature_text` VALUES (49452, 3, 0, 'Оставим празднование на потом, командир. Сумеречные драконы приближаются!', 12, 0, 100, 0, 0, 0, 49560, 25000, 25000, 0, 'Авиана to Player');
INSERT INTO `creature_text` VALUES (49476, 0, 0, 'О, НЕТ! РАЗ!!!', 14, 0, 100, 0, 0, 18902, 49597, 0, 0, 0, 'Айс Вентурон to Player');
INSERT INTO `creature_text` VALUES (49541, 0, 0, 'I will not share my father\'\'s fate! Your tale ends here!', 14, 0, 100, 0, 0, 24599, 49748, 0, 0, 0, 'Vanessa VanCleef aggro');
INSERT INTO `creature_text` VALUES (49541, 1, 0, 'ENOUGH! I will not give you the pleasure.', 14, 0, 100, 0, 0, 24610, 49753, 0, 0, 0, 'Vanessa VanCleef death 1');
INSERT INTO `creature_text` VALUES (49541, 1, 1, 'If I\'m going to die, I\'\'m taking you all with me!', 14, 0, 100, 0, 0, 24611, 49752, 0, 0, 0, 'Vanessa VanCleef death 2');
INSERT INTO `creature_text` VALUES (49541, 1, 2, 'MY FATE IS MY OWN!', 14, 0, 100, 0, 0, 24612, 49754, 0, 0, 0, 'Vanessa VanCleef death 3');
INSERT INTO `creature_text` VALUES (49541, 2, 0, 'The first of many!', 14, 0, 100, 0, 0, 24618, 49749, 0, 0, 0, 'Vanessa VanCleef kill');
INSERT INTO `creature_text` VALUES (49541, 3, 0, 'Did you really think I would come to this fight alone!?', 14, 0, 100, 0, 0, 24620, 49750, 0, 0, 0, 'Vanessa VanCleef adds');
INSERT INTO `creature_text` VALUES (49541, 4, 0, 'Fools! This entire ship is rigged with explosives! Enjoy your fiery deaths!', 14, 0, 100, 0, 0, 24621, 49751, 0, 0, 0, 'Vanessa VanCleef spell 1');
INSERT INTO `creature_text` VALUES (49541, 5, 0, 'You didn\'t honestly think I would only plant ONE set of explosives, did you?', 14, 0, 100, 0, 0, 24622, 50706, 0, 0, 0, 'Vanessa VanCleef spell 2');
INSERT INTO `creature_text` VALUES (49794, 3, 0, '%s будет активирована через 3 секунды!', 16, 0, 100, 0, 0, 0, 10894, 2000, 2000, 11817, 'Противовоздушная мина to Мини-дирижабль');
