INSERT INTO `creature_text` VALUES (49799, 0, 0, 'Малориак, постарайся, чтоб тебя не убили. Создать полуценного помощника ТАК сложно!', 14, 0, 100, 0, 0, 23372, 49068, 0, 0, 0, 'Лорд Виктор Нефарий to Player');
INSERT INTO `creature_text` VALUES (49799, 1, 0, 'Слабоваты твои настои, Малориак! Подбавить бы к ним... специй!', 14, 0, 100, 0, 0, 23370, 49069, 0, 0, 0, 'Лорд Виктор Нефарий to Малориак');
INSERT INTO `creature_text` VALUES (49799, 2, 0, '|TInterface\\Icons\\INV_ELEMENTAL_PRIMAL_SHADOW.BLP:20|t %s применяет |cFF660099темную|r магию к котлу!', 41, 0, 100, 0, 0, 0, 49793, 0, 0, 0, 'Лорд Виктор Нефарий to Малориак');
INSERT INTO `creature_text` VALUES (49799, 3, 0, 'Поздравляю! Позвольте наградить вас титулом, который вполне соответствует вашему великому свершению. Отныне вы станете зваться \"Убийцами глупых, неумелых и никчемных прислужников\"!', 14, 0, 100, 21, 0, 23371, 49070, 0, 0, 0, 'Лорд Виктор Нефарий to Player');
