INSERT INTO `creature_text` VALUES (52903, 0, 0, 'Посмотри, Анрен! $n $gпришел:пришла;, чтобы помочь нам!', 12, 0, 100, 0, 0, 0, 51521, 0, 0, 0, 'Толо Белое Копыто to Player');
INSERT INTO `creature_text` VALUES (52903, 1, 0, 'Я буду защищать тебя, Анрен!', 12, 0, 100, 0, 0, 0, 51549, 0, 0, 0, 'Толо Белое Копыто to Анрен Искатель Теней');
INSERT INTO `creature_text` VALUES (52903, 2, 0, 'I will stay with you, Anren!', 12, 0, 100, 0, 0, 0, 51547, 0, 0, 0, 'Толо Белое Копыто to Анрен Искатель Теней');
INSERT INTO `creature_text` VALUES (52903, 3, 0, 'Спасибо тебе, $n. Пока мы с Анреном вместе, с нами все будет в порядке. Проверь, не нужна ли помощь остальным.', 12, 0, 100, 0, 0, 0, 51531, 0, 0, 0, 'Толо Белое Копыто to Player');
INSERT INTO `creature_text` VALUES (52904, 0, 0, 'I\'m with you, Tolo!', 12, 0, 100, 0, 0, 0, 51542, 0, 0, 0, 'Анрен Искатель Теней');
