INSERT INTO `creature_text` VALUES (52906, 0, 0, 'Ты как раз вовремя, |3-6($r). У меня больше нет сил сражаться с этими чудовищами.', 12, 0, 100, 0, 0, 0, 51523, 0, 0, 0, 'Старокрон to Player');
INSERT INTO `creature_text` VALUES (52906, 1, 0, 'Дальше я могу их сдерживать и сам, |3-6($r). Встретимся у Святилища Малорна.', 12, 0, 100, 0, 0, 0, 51532, 0, 0, 0, 'Старокрон to Player');
INSERT INTO `creature_text` VALUES (52907, 0, 0, 'Эй, $n, ты здесь! Они берутся как будто бы из ниоткуда! Помоги мне и дай хоть маленькую передышку.', 12, 0, 100, 0, 0, 0, 51505, 0, 0, 0, 'Тизали Ворона to Player');
INSERT INTO `creature_text` VALUES (52907, 1, 0, 'Дальше я могу справиться сама. Встретимся в святилище, $n.', 12, 0, 100, 0, 0, 0, 51530, 0, 0, 0, 'Тизали Ворона to Player');
INSERT INTO `creature_text` VALUES (52924, 3, 0, 'Чтобы открыть врата, надо ударить в гонг вдвоем.', 14, 0, 100, 1, 0, 0, 51506, 43505, 43505, 0, 'Вол\'джин to Player');
INSERT INTO `creature_text` VALUES (52939, 3, 0, 'Вы убили тролля-медведя? Путь теперь свободен?', 14, 0, 100, 1, 0, 2850, 51556, 272197, 272197, 0, 'Хазлек to Player');
INSERT INTO `creature_text` VALUES (52945, 0, 0, 'Dey\'ve done something to me! I be needin\' help!', 14, 0, 0, 0, 0, 0, 51597, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (52945, 1, 0, 'Dat be so much better. I be weak, but I remember hearin\' da Amani and da Zandalari talkin\' about treasure hidden in dis very room.', 12, 0, 0, 0, 0, 0, 51649, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (52945, 2, 0, 'Maybe it be in dis urn over here. Stand back!', 12, 0, 0, 0, 0, 0, 51650, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (52945, 3, 0, 'Hmm. Dat\'s not da one. Let\'s try one o\' da other vases.', 12, 0, 0, 0, 0, 0, 51651, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (52945, 4, 0, 'Dere be somethin\' in dat one. Go take look.', 12, 0, 0, 0, 0, 0, 51652, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (52956, 0, 0, 'Ancestors be beside me, let \'dem hear yo\' voice!', 14, 14, 100, 0, 0, 0, 51625, 0, 0, 0, 'Zandalari Juggernaut');
