INSERT INTO `creature_text` VALUES (53014, 4, 0, 'It is already determined. You have no hope... the flames shall consume all!', 12, 0, 0, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (53014, 5, 0, 'Now, look before you! One of Azeroth\'s most powerful \"druids\", reduced to nothing but a charred husk. Such is the will of Fandral Staghelm. Such is the will of the flame!', 12, 0, 0, 0, 0, 0, 51647, 0, 0, 0, '');
