INSERT INTO `creature_text` VALUES (54544, 20, 0, 'A pool of dark moonlight appears nearby!', 41, 0, 100, 0, 0, 0, 53110, 0, 0, 0, 'Tyrande moonlight 6');
INSERT INTO `creature_text` VALUES (54544, 21, 0, 'The moonlight fades into darkness!', 41, 0, 100, 0, 0, 0, 53102, 0, 0, 0, 'Tyrande moonlight left');
INSERT INTO `creature_text` VALUES (54548, 1, 0, 'Берегитесь!', 12, 0, 100, 0, 0, 25873, 138096, 0, 0, 0, 'Тралл to Player');
INSERT INTO `creature_text` VALUES (54548, 3, 0, 'Герои, Душа Дракона у нас. Быстрее, в храм Драконьего Покоя! Аспекты ждут!', 12, 0, 100, 396, 0, 25870, 53046, 11386, 11386, 0, 'Тралл to Player');
INSERT INTO `creature_text` VALUES (54567, 3, 0, 'Eine Prüfung in Mitgefühl und Bescheidenheit. Indem Ihr Händler Lorvo geholfen habt, habt Ihr sie bestanden. Meister Shang wird erfreut sein.', 12, 0, 100, 2, 0, 27404, 60599, 8156, 8156, 116219, 'Aysa Wolkensänger to Player');
INSERT INTO `creature_text` VALUES (54567, 3, 0, 'Unser Pfad offenbart sich. Sprecht mit Meister Shang Xi - er wird wissen, was zu tun ist.', 12, 0, 100, 0, 0, 27399, 60046, 1532, 1532, 0, 'Aysa Wolkensänger to Player');
INSERT INTO `creature_text` VALUES (54567, 3, 0, 'Истинное испытание – иметь сострадание и смирение. Оказав помощь торговцу Лорво, ты выдержишь его с честью, |3-6($c). Мастер Шан будет доволен.', 12, 0, 100, 2, 0, 27404, 60599, 13000, 13000, 116219, 'Аиса Воспевающая Облака to Player');
INSERT INTO `creature_text` VALUES (54567, 3, 0, 'Итак, теперь мы знаем, что нам предстоит. Обратись к мастеру Шан Си. Он расскажет тебе, что делать дальше.', 12, 0, 100, 0, 0, 27399, 60046, 2000, 2000, 0, 'Аиса Воспевающая Облака to Player');
INSERT INTO `creature_text` VALUES (54568, 0, 0, 'Получи, обезьянья морда!', 12, 0, 100, 5, 0, 27344, 59757, 0, 0, 0, 'Цзи Огненная Лапа');
INSERT INTO `creature_text` VALUES (54586, 0, 0, 'I have never seen a trainee with skills such as yours. I must tell the others.', 12, 0, 100, 0, 0, 0, 56445, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54586, 0, 1, 'My skills are no match for yours. I admit defeat.', 12, 0, 100, 0, 0, 0, 56441, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54586, 0, 2, 'Thank you for reminding me that I must train more diligently.', 12, 0, 100, 0, 0, 0, 56439, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54586, 0, 3, 'That was a good match. Thank you.', 12, 0, 100, 0, 0, 0, 56444, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54586, 0, 4, 'You are an honorable opponent.', 12, 0, 100, 0, 0, 0, 56438, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54586, 0, 5, 'You fight honorably, friend.', 12, 0, 100, 0, 0, 0, 56440, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54586, 0, 6, 'You fought well. I must learn more from you in the future.', 12, 0, 100, 0, 0, 0, 56443, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54586, 0, 7, 'Your skills are too great. I yield.', 12, 0, 100, 0, 0, 0, 56442, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54587, 0, 0, 'Это был хороший бой. Благодарю тебя.', 12, 0, 100, 1, 0, 33643, 56444, 0, 0, 0, 'Ученик Тушуй');
INSERT INTO `creature_text` VALUES (54587, 0, 1, 'Твое мастерство не вызывает сомнений. Я сдаюсь.', 12, 0, 100, 1, 0, 33646, 56442, 0, 0, 0, 'Ученик Тушуй');
INSERT INTO `creature_text` VALUES (54587, 0, 2, 'Достойный поединок, друг.', 12, 0, 100, 1, 0, 33643, 56440, 0, 0, 0, 'Ученик Тушуй');
INSERT INTO `creature_text` VALUES (54587, 0, 3, 'Прекрасный бой. Мне можно будет многому у тебя научиться.', 12, 0, 100, 1, 0, 33645, 56443, 0, 0, 0, 'Ученик Тушуй');
INSERT INTO `creature_text` VALUES (54587, 0, 4, 'Спасибо за напоминание о том, что нужно тренироваться усерднее.', 12, 0, 100, 1, 0, 33643, 56439, 0, 0, 0, 'Ученик Тушуй');
INSERT INTO `creature_text` VALUES (54590, 0, 0, 'Give up the Dragon Soul, and I may yet allow you to live.', 14, 0, 100, 0, 0, 25796, 0, 0, 0, 0, 'arcurion aggro');
INSERT INTO `creature_text` VALUES (54590, 1, 0, 'Nothing! Nothing...', 14, 0, 100, 0, 0, 25797, 54502, 0, 0, 0, 'arcurion death');
INSERT INTO `creature_text` VALUES (54590, 2, 0, 'Shaman! The Dragon Soul is not yours. Give it up, and you may yet walk away with your life.', 14, 0, 100, 0, 0, 25798, 53797, 0, 0, 0, 'arcurion event 1');
INSERT INTO `creature_text` VALUES (54590, 3, 0, 'Destroy them all, but bring the Shaman to me!', 14, 0, 100, 0, 0, 25799, 53798, 0, 0, 0, 'arcurion event 2');
INSERT INTO `creature_text` VALUES (54590, 4, 0, 'You will go nowhere, Shaman.', 14, 0, 100, 0, 0, 25800, 53803, 0, 0, 0, 'arcurion event 3');
INSERT INTO `creature_text` VALUES (54590, 5, 0, 'The Hour of Twilight falls - the end of all things - you cannot stop it. You are nothing. NOTHING!', 14, 0, 100, 0, 0, 25801, 54097, 0, 0, 0, 'arcurion event 4');
INSERT INTO `creature_text` VALUES (54590, 6, 0, 'You are a fool if you think to take your place as the Aspect of Earth, Shaman!', 14, 0, 100, 0, 0, 25802, 53818, 0, 0, 0, 'arcurion intro 1');
INSERT INTO `creature_text` VALUES (54590, 7, 0, 'Mere mortals...', 14, 0, 100, 0, 0, 25803, 54499, 0, 0, 0, 'arcurion kill 1');
INSERT INTO `creature_text` VALUES (54590, 7, 1, 'Your shaman can\'t protect you.', 14, 0, 100, 0, 0, 25805, 54500, 0, 0, 0, 'arcurion kill 2');
INSERT INTO `creature_text` VALUES (54590, 7, 2, 'The Aspects misplaced their trust.', 14, 0, 100, 0, 0, 25806, 54501, 0, 0, 0, 'arcurion kill 3');
INSERT INTO `creature_text` VALUES (54590, 8, 0, 'You\'re a mere mortal. It is time you die like one.', 14, 0, 100, 0, 0, 25804, 54495, 0, 0, 0, 'arcurion intro 2');
INSERT INTO `creature_text` VALUES (54590, 9, 0, 'Enough, shaman!', 14, 0, 100, 0, 0, 25807, 54496, 0, 0, 0, 'arcurion spell 1');
INSERT INTO `creature_text` VALUES (54590, 9, 1, 'None will survive!', 14, 0, 100, 0, 0, 25808, 54497, 0, 0, 0, 'arcurion spell 2');
INSERT INTO `creature_text` VALUES (54590, 9, 2, 'The Shaman is mine, focus on his companions!', 14, 0, 100, 0, 0, 25809, 54498, 0, 0, 0, 'arcurion spell 3');
INSERT INTO `creature_text` VALUES (54590, 9, 3, 'Freeze!', 14, 0, 100, 0, 0, 25810, 109587, 0, 0, 0, 'arcurion spell 4');
INSERT INTO `creature_text` VALUES (54595, 0, 0, 'Ты что, это вовсе не драконья кость. Скорее, элекка или какого-то похожего животного.', 12, 0, 100, 1, 0, 0, 53063, 0, 0, 0, 'Мэгги to Раан');
INSERT INTO `creature_text` VALUES (54596, 0, 0, 'Это драконья? Или, может, она принадлежала гиганту!', 12, 0, 100, 1, 0, 0, 53061, 0, 0, 0, 'Раан');
INSERT INTO `creature_text` VALUES (54601, 0, 0, 'Время постучать молотками!', 12, 0, 100, 70, 0, 0, 53469, 0, 0, 0, 'Кротта to Player');
INSERT INTO `creature_text` VALUES (54601, 0, 1, 'Гноллобой! Подходите и стучите по гноллам на здоровье!', 12, 0, 100, 70, 0, 0, 53470, 0, 0, 0, 'Кротта to Player');
INSERT INTO `creature_text` VALUES (54601, 0, 2, 'Эх, размахнись рука, раззудись плечо! Ну? Кто не прочь размахнуться как следует?', 12, 0, 100, 70, 0, 4791, 53471, 0, 0, 0, 'Кротта to Player');
INSERT INTO `creature_text` VALUES (54601, 1, 0, 'Пожалуйста, держись за пределами игровой зоны.', 42, 0, 100, 0, 0, 0, 56891, 0, 0, 0, 'Кротта to Player');
INSERT INTO `creature_text` VALUES (54605, 0, 0, 'Tonks! We got tonks here!', 12, 0, 100, 396, 0, 0, 53461, 0, 0, 0, 'Finlay Coolshot - OCC Say Line (1)');
INSERT INTO `creature_text` VALUES (54605, 0, 1, 'Step right up and try a tonk!', 12, 0, 100, 396, 0, 0, 53462, 0, 0, 0, 'Finlay Coolshot - OCC Say Line (2)');
INSERT INTO `creature_text` VALUES (54605, 0, 2, 'We\'re under attack! Step up and do your part, $c!', 12, 0, 100, 396, 0, 0, 53463, 0, 0, 0, 'Finlay Coolshot - OCC Say Line (3)');
INSERT INTO `creature_text` VALUES (54605, 0, 3, 'Hey, hey! Command a tonk in glorious battle!', 12, 0, 100, 396, 0, 0, 53464, 0, 0, 0, 'Finlay Coolshot - OCC Say Line (4)');
INSERT INTO `creature_text` VALUES (54611, 0, 0, 'А вот и мой соперник.', 12, 0, 100, 113, 0, 0, 59692, 0, 0, 0, 'Цзяоминь Жо');
INSERT INTO `creature_text` VALUES (54611, 1, 0, 'Поразительно! Ты куда сильнее, чем кажешься.', 12, 0, 100, 2, 0, 0, 55207, 0, 0, 0, 'Цзяоминь Жо');
INSERT INTO `creature_text` VALUES (54611, 1, 1, 'Тебя хорошо обучили.', 12, 0, 100, 2, 0, 0, 55205, 0, 0, 0, 'Цзяоминь Жо');
INSERT INTO `creature_text` VALUES (54611, 1, 2, 'I bow to your skill. I lost in a fair fight.', 12, 0, 100, 2, 0, 0, 55209, 0, 0, 0, 'Цзяоминь Жо');
INSERT INTO `creature_text` VALUES (54611, 1, 3, 'Похоже, мне придется еще как следует потренироваться. Благодарю за урок.', 12, 0, 100, 2, 0, 0, 55208, 0, 0, 0, 'Цзяоминь Жо');
INSERT INTO `creature_text` VALUES (54611, 1, 4, 'Быть побежденным тобой – большая честь.', 12, 0, 100, 2, 0, 0, 55210, 0, 0, 0, 'Цзяоминь Жо');
INSERT INTO `creature_text` VALUES (54611, 1, 5, 'Прекрасный бой. Мастер Шан будет доволен.', 12, 0, 100, 2, 0, 0, 55206, 0, 0, 0, 'Цзяоминь Жо');
INSERT INTO `creature_text` VALUES (54614, 3, 0, 'Бинты грязные. Давай-ка тебя перевяжем заново.', 12, 0, 100, 0, 0, 0, 67777, 8657, 8657, 130971, 'Мишка');
INSERT INTO `creature_text` VALUES (54615, 3, 0, 'Clear. I guess what his next step will be.', 12, 0, 100, 0, 0, 0, 58269, 43000, 43000, 0, 'Узлик Шифролом to Player');
INSERT INTO `creature_text` VALUES (54615, 3, 0, 'Видел, как ты $gосматривал:осматривала; корабль и $gбился:билась; с монстрами. Ты просто молодец. Удалось что-нибудь найти?', 12, 0, 100, 1, 0, 0, 67491, 31000, 31000, 0, 'Узлик Шифролом to Player');
INSERT INTO `creature_text` VALUES (54615, 3, 0, 'Сюда, $n! Твой гирокоптер готов к вылету!', 12, 0, 100, 3, 0, 5763, 68502, 19000, 19000, 0, 'Узлик Шифролом to Player');
INSERT INTO `creature_text` VALUES (54617, 3, 0, 'Мы из Альянса и не причиним вам вреда. Скажи мне, что это была за тень, от которой ты меня избавил?', 12, 0, 100, 1, 0, 32672, 67741, 2000, 2000, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (54617, 3, 0, 'Ты не понимаешь... Здесь идет война.', 12, 0, 100, 1, 0, 32673, 67743, 20000, 20000, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (54620, 3, 0, 'Глупый дворф, он взял твой разум под контроль! А теперь он сбежал!', 12, 0, 100, 0, 0, 27474, 55168, 39000, 39000, 0, 'Эмбер Кеарнен to 56485');
INSERT INTO `creature_text` VALUES (54620, 3, 0, 'Надеюсь, ты сможешь объяснить произошедшее адмиралу Тейлору!', 12, 0, 100, 0, 0, 27475, 55169, 49000, 49000, 0, 'Эмбер Кеарнен to 56485');
INSERT INTO `creature_text` VALUES (54620, 3, 0, 'Салли! Как ты мог так просто его отпустить!', 12, 0, 100, 0, 0, 27473, 55166, 32000, 32000, 0, 'Эмбер Кеарнен to Андуин Ринн');
INSERT INTO `creature_text` VALUES (54628, 3, 0, 'Рядом материализуется порождение древних богов!', 41, 0, 100, 0, 0, 0, 53085, 38126, 38126, 102286, 'Темная дымка to Player');
INSERT INTO `creature_text` VALUES (54667, 3, 0, 'Может, они уже выбрались? Может, они ищут меня?', 12, 0, 100, 1, 0, 0, 53071, 121000, 241000, 0, 'Дозорный Джанг');
INSERT INTO `creature_text` VALUES (54667, 3, 0, 'Наги используют рабский труд? Они ответят за свои преступления!', 12, 0, 100, 1, 0, 0, 53072, 909000, 909000, 0, 'Дозорный Джанг');
INSERT INTO `creature_text` VALUES (54667, 3, 0, 'Надеюсь, мои друзья в порядке...', 12, 0, 100, 1, 0, 0, 53070, 422000, 422000, 0, 'Дозорный Джанг');
INSERT INTO `creature_text` VALUES (54692, 3, 0, 'Только посмотри на меня! Это называется \"все будет в порядке\"?! Я что, выгляжу так, будто я в порядке?', 12, 0, 100, 1, 0, 0, 53083, 601000, 601000, 0, 'Механолог Морфалиус');
INSERT INTO `creature_text` VALUES (54694, 3, 0, 'Я могу лишь показать тебе дверь, Морфалиус. Войти придется тебе самому.', 12, 0, 100, 1, 0, 0, 53084, 612000, 612000, 0, 'Мамди по кличке \"Олог\" to Механолог Морфалиус');
INSERT INTO `creature_text` VALUES (54751, 3, 0, 'Но когда-нибудь... меня все-таки поглотит безумие. И вы, герои... Сразите меня. Все повторится вновь...', 12, 0, 100, 0, 0, 25945, 55978, 247845, 247845, 0, 'Ноздорму to 54432');
INSERT INTO `creature_text` VALUES (54763, 0, 0, 'Are you Shao\'s friend? Is he still alive?', 12, 0, 50, 0, 0, 0, 53549, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (54763, 0, 1, 'Give \'em hell for me!', 12, 0, 50, 0, 0, 0, 53547, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (54763, 0, 2, 'I don\'t know who you are, but you have my gratitude.', 12, 0, 50, 0, 0, 0, 53145, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (54763, 0, 3, 'I won\'t let them take me!', 12, 0, 50, 0, 0, 0, 58469, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (54763, 0, 4, 'My dog?! Where\'s my dog?!', 12, 0, 50, 0, 0, 0, 58471, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (54763, 0, 5, 'My house! My things! It\'s all ablaze!', 12, 0, 50, 0, 0, 0, 58470, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (54763, 0, 6, 'Please tell me Hanae is safe!', 12, 0, 50, 0, 0, 0, 53548, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (54763, 0, 7, 'Thank you, I\'ll go now.', 12, 0, 50, 0, 0, 0, 53143, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (54763, 0, 8, 'Thank you... Now what?', 12, 0, 50, 0, 0, 0, 58472, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (54763, 0, 9, 'The whole town is gone!', 12, 0, 50, 0, 0, 0, 58473, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (54763, 0, 10, 'They\'re dragging people away!', 12, 0, 50, 0, 0, 0, 53544, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (54763, 0, 11, 'What are we going to do?!', 12, 0, 50, 0, 0, 0, 65691, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (54763, 0, 12, 'You don\'t have to tell me twice!', 12, 0, 50, 0, 0, 0, 53543, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (54786, 0, 0, 'Добро пожаловать, Хо. Наш народ соскучился по твоему теплу.', 12, 0, 100, 2, 0, 27788, 60608, 0, 0, 0, 'Мастер Шан Си');
INSERT INTO `creature_text` VALUES (54786, 1, 0, 'Тебе удалось справиться со всеми моими испытаниями. У тебя получилось найти Хо и благополучно привести его в храм.', 12, 0, 100, 1, 0, 27789, 60600, 0, 0, 0, 'Мастер Шан Си');
INSERT INTO `creature_text` VALUES (54786, 2, 0, 'Нам предстоит решить гораздо более сложную задачу, мои ученики. Шэнь-Цзынь Су страдает от боли. Если мы ничего не предпримем, сама земля, на которой мы стоим, может погибнуть. И мы вместе с ней.', 12, 0, 100, 1, 0, 27790, 60601, 0, 0, 0, 'Мастер Шан Си');
INSERT INTO `creature_text` VALUES (54786, 3, 0, 'Мы должны поговорить с Шэнь-Цзынь Су и узнать, как исцелить его. А для этого нужно вернуть на свои места четырех духов стихий. Хо был первым.', 12, 0, 100, 1, 0, 27791, 60602, 0, 0, 0, 'Мастер Шан Си');
INSERT INTO `creature_text` VALUES (54786, 4, 0, 'Цзи, я хочу, чтобы ты отправился на крестьянский двор Дай-Ло и разыскал Угоу, духа земли.', 12, 0, 100, 1, 0, 27792, 60603, 0, 0, 0, 'Мастер Шан Си');
INSERT INTO `creature_text` VALUES (54786, 5, 0, 'Аиса, я хочу, чтобы ты отправилась к Поющим прудам и разыскала Шу, духа воды.', 12, 0, 100, 1, 0, 27793, 60604, 0, 0, 0, 'Мастер Шан Си');
INSERT INTO `creature_text` VALUES (54786, 6, 0, 'Ты же будешь направлять наши усилия. Поговори со мной перед тем, как отправиться на восток, к Поющим прудам, и встретиться с Аисой.', 12, 0, 100, 1, 0, 27794, 60605, 0, 0, 0, 'Мастер Шан Си');
INSERT INTO `creature_text` VALUES (54786, 7, 0, 'Ты снова здесь, вместе с духами воды и земли. Старый учитель гордится тобой.', 12, 0, 100, 1, 0, 27776, 55295, 0, 0, 0, 'Мастер Шан Си');
INSERT INTO `creature_text` VALUES (54848, 3, 0, 'Их слишком много. Калитреш, похоже, собрал здесь целую армию.', 12, 0, 100, 0, 0, 0, 53137, 375877, 375877, 0, 'Дозорный Джанг to Призыватель ветров Коготь');
INSERT INTO `creature_text` VALUES (54852, 3, 0, 'Эй! Эй, там!', 15, 0, 100, 0, 0, 0, 53162, 356275, 356275, 0, 'Землепряд Гневвс to Player');
INSERT INTO `creature_text` VALUES (54853, 0, 0, 'Ah, welcome. You are here to join us in the coming celebration? No? A pity.', 14, 0, 100, 274, 0, 26013, 55919, 0, 0, 0, 'Queen Azhara');
INSERT INTO `creature_text` VALUES (54853, 1, 0, 'I have no time for such diversions. Keepers of Eternity, will you stand for your queen?', 14, 0, 100, 274, 0, 26027, 53300, 0, 0, 0, 'Queen Azhara');
INSERT INTO `creature_text` VALUES (54853, 2, 0, 'If you intend to play the fool, you may as well look the part.', 14, 0, 100, 273, 0, 26025, 56834, 0, 0, 0, 'Queen Azhara');
INSERT INTO `creature_text` VALUES (54853, 3, 0, '% s begins to turn all into puppets! You have to stop her!', 41, 0, 100, 273, 0, 0, 0, 0, 0, 0, 'Queen Azhara');
INSERT INTO `creature_text` VALUES (54853, 4, 0, 'Serve Azshara, puppets, and rejoice.', 14, 0, 100, 273, 0, 26026, 55920, 0, 0, 0, 'Queen Azhara');
INSERT INTO `creature_text` VALUES (54853, 5, 0, 'Enough! As much as I adore playing hostess, I have more pressing matters to attend to.', 14, 0, 100, 5, 0, 26014, 53280, 0, 0, 0, 'Queen Azhara');
INSERT INTO `creature_text` VALUES (54853, 6, 0, 'Riders, to me!', 14, 0, 100, 25, 0, 26018, 53281, 0, 0, 0, 'Queen Azhara');
INSERT INTO `creature_text` VALUES (54853, 7, 0, 'My noble Varo\'then, do return and dispose of this murderous band.', 12, 0, 100, 0, 0, 26019, 55910, 0, 0, 0, 'Queen Azhara');
INSERT INTO `creature_text` VALUES (54855, 0, 0, 'Master Shang has trained you well. Thank you, friend!', 12, 0, 100, 0, 0, 0, 53265, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54855, 1, 0, 'I couldn\'t have fought them off alone. Now, if you\'ll excuse me, I should go find my cart.', 12, 0, 100, 0, 0, 0, 56452, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54855, 2, 0, 'Cart!', 14, 0, 100, 0, 0, 0, 56453, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54856, 3, 0, 'Ihr hört das Echo von Meister Li Fei: \"Bewältigt diese Herausforderung und Huo wird Euch mit seiner Anwesenheit beehren. Lasst seine Flamme wieder aufleben, und wenn Euer Geist rein ist, wird er Euch folgen.\"', 16, 0, 100, 1, 0, 0, 53843, 69968, 69968, 0, 'Meister Li Fei to Player');
INSERT INTO `creature_text` VALUES (54856, 3, 0, 'Ihr hört das Echo von Meister Li Fei: \"Der Weg der Tushui... Erleuchtung durch Geduld und Meditation... ein Leben mit hohen Grundsätzen.\"', 16, 0, 100, 6, 0, 0, 53129, 25531, 25531, 0, 'Meister Li Fei to Player');
INSERT INTO `creature_text` VALUES (54856, 3, 0, 'Ihr hört das Echo von Meister Li Fei: \"Es freut mich, Euch wiederzusehen, Aysa. Ihr seid mir mit Respekt begegnet und so werde ich Euch die Antworten geben, nach denen Ihr sucht.\"', 16, 0, 100, 1, 0, 0, 53845, 36047, 36047, 0, 'Meister Li Fei to Player');
INSERT INTO `creature_text` VALUES (54856, 3, 0, 'Ihr hört das Echo von Meister Li Fei: \"Geht, Kinder. Wir werden uns bald wiedersehen.\"', 16, 0, 100, 2, 0, 0, 53844, 76406, 76406, 0, 'Meister Li Fei to Player');
INSERT INTO `creature_text` VALUES (54856, 3, 0, 'Ihr hört das Echo von Meister Li Fei: \"Huo, der Geist des Feuers, ist bekannt für seinen Hunger. Er braucht Zunder als Nahrung und die Liebkosung des Windes, um erweckt zu werden.\"', 16, 0, 100, 1, 0, 0, 53841, 48047, 48047, 0, 'Meister Li Fei to Player');
INSERT INTO `creature_text` VALUES (54856, 3, 0, 'Ihr hört das Echo von Meister Li Fei: \"Wenn Ihr diese Dinge findet und zu seiner Höhle auf der anderen Seite von Wu-Song bringt, wird Euch dort eine neue Herausforderung erwarten.\"', 16, 0, 100, 1, 0, 0, 53842, 59468, 59468, 0, 'Meister Li Fei to Player');
INSERT INTO `creature_text` VALUES (54856, 3, 0, 'Meister Li Fei verblasst.', 16, 0, 100, 0, 0, 0, 53130, 82843, 82843, 84034, 'Meister Li Fei to Player');
INSERT INTO `creature_text` VALUES (54856, 3, 0, 'Мастер Ли Фэй говорит: \"Если ты найдешь все это и принесешь к его пещере в дальнем конце деревни У-Сун, там тебя будет ждать испытание\".', 16, 0, 100, 1, 0, 0, 53842, 59000, 59000, 0, 'Мастер Ли Фэй to Player');
INSERT INTO `creature_text` VALUES (54856, 3, 0, 'Мастер Ли Фэй говорит: \"Приятно видеть тебя снова, Аиса. Ты отдала дань уважения, и посему я дарую тебе ответы, которые ты ищешь\".', 16, 0, 100, 1, 0, 0, 53845, 36000, 36000, 0, 'Мастер Ли Фэй to Player');
INSERT INTO `creature_text` VALUES (54856, 3, 0, 'Мастер Ли Фэй говорит: \"Пройди это испытание, и тебе явится Хо. Разожги его пламя, и если дух твой чист, Хо последует за тобой\".', 16, 0, 100, 1, 0, 0, 53843, 70000, 70000, 0, 'Мастер Ли Фэй to Player');
INSERT INTO `creature_text` VALUES (54856, 3, 0, 'Мастер Ли Фэй говорит: \"Путь Тушуй... это просветление через терпение и медитацию... жизнь, основанная на твердых принципах\".', 16, 0, 100, 6, 0, 0, 53129, 25000, 25000, 0, 'Мастер Ли Фэй to Player');
INSERT INTO `creature_text` VALUES (54856, 3, 0, 'Мастер Ли Фэй говорит: \"Ступайте же, дети мои. Мы встретимся вновь и очень скоро\".', 16, 0, 100, 2, 0, 0, 53844, 76000, 76000, 0, 'Мастер Ли Фэй to Player');
INSERT INTO `creature_text` VALUES (54856, 3, 0, 'Мастер Ли Фэй говорит: \"Хо, дух огня, известен своим чувством голода. Он нуждается в древесине – его пище. Он нуждается в ласке ветра, что пробуждает его\".', 16, 0, 100, 1, 0, 0, 53841, 48000, 48000, 0, 'Мастер Ли Фэй to Player');
INSERT INTO `creature_text` VALUES (54856, 3, 0, 'Мастер Ли Фэй растворяется в воздухе.', 16, 0, 100, 0, 0, 0, 53130, 83000, 83000, 84034, 'Мастер Ли Фэй to Player');
INSERT INTO `creature_text` VALUES (54867, 3, 0, 'Это все, что осталось от Святилища синих драконов. Нити времени, ведущие к этому забытому месту, были разорваны в великой битве. Соберите осколки прошлого и победите заключенного здесь духа, чтобы двигаться дальше.', 12, 0, 100, 0, 0, 0, 53138, 8, 8, 51347, 'Проекция Ноздорму to 209439');
INSERT INTO `creature_text` VALUES (54868, 4, 0, 'Посмотрим, что ты можешь без своих дружков-грязней.', 14, 0, 100, 0, 0, 0, 58598, 15000, 15000, 114149, 'Вождь Гукгут');
INSERT INTO `creature_text` VALUES (54868, 4, 0, 'Тупые, тупые, тупые, строганые дукеры!', 14, 0, 100, 0, 0, 0, 61260, 4000, 4000, 114140, 'Вождь Гукгут');
INSERT INTO `creature_text` VALUES (54882, 1, 0, 'О да, Светлейшая! Распоряжайся мной!', 14, 0, 100, 0, 0, 26513, 53286, 0, 0, 0, 'Зачарованный волхв to Зачарованный волхв');
INSERT INTO `creature_text` VALUES (54883, 1, 0, 'Свет тысячи лун, даруй мне эту честь.', 14, 0, 100, 0, 0, 26042, 53285, 0, 0, 0, 'Зачарованный волхв to Зачарованный волхв');
INSERT INTO `creature_text` VALUES (54884, 1, 0, 'Цветок Жизни взывает ко мне. Я не подведу тебя, моя королева!', 14, 0, 100, 0, 0, 26047, 53287, 0, 0, 0, 'Зачарованный волхв to Зачарованный волхв');
INSERT INTO `creature_text` VALUES (54894, 1, 0, 'Brother, I see great darkness looming before you.', 41, 0, 100, 0, 0, 0, 55032, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54894, 2, 0, 'Everywhere I look, the rivers have dried.', 41, 0, 100, 0, 0, 0, 55033, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54894, 3, 0, 'But there is yet one path, One river, that may carry you through these difficult times.', 41, 0, 100, 0, 0, 0, 55034, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54894, 4, 0, 'Powerful strangers come from across the Sea. Seek their strength. Add it to our own.', 41, 0, 100, 0, 0, 0, 55035, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54924, 0, 0, 'About time someone challenged Zhi-Zhi!', 14, 0, 100, 1, 0, 0, 53203, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (54924, 1, 0, 'Alright, enough, enough! STOP HITTING ZHI-ZHI!', 12, 0, 100, 1, 0, 0, 53204, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (54925, 0, 0, 'Very well!', 14, 0, 100, 1, 0, 0, 53212, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (54925, 1, 0, 'I knew I would lose. The rivers told me so.', 12, 0, 100, 1, 0, 0, 53213, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (54926, 1, 0, 'It\'s time for an open-faced $r sandwich!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54926, 2, 0, 'You win... this time. I\'d better get back to my size training.', 12, 0, 100, 0, 0, 0, 53220, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54927, 0, 0, 'В портал! Вперед! ВПЕРЕД!!!', 14, 0, 100, 0, 0, 26038, 53405, 0, 0, 0, 'Демон-стражник to Портал Легиона');
INSERT INTO `creature_text` VALUES (54936, 1, 0, 'Сдавайся или умри, глупец!', 14, 0, 100, 0, 0, 0, 53380, 0, 0, 0, 'Гормальский налетчик');
INSERT INTO `creature_text` VALUES (54938, 0, 0, 'Get inside, quickly! I\'ll hold them off!', 12, 0, 100, 396, 0, 25866, 53254, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54938, 1, 0, 'And now, Shaman, you will give the Dragon Soul to ME.', 12, 0, 100, 1, 0, 25867, 53255, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54938, 2, 0, 'I suppose it has to be this way, then. If only you\'d seen what I\'ve seen. THEN you\'d understand.', 12, 0, 100, 0, 0, 21410, 53283, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54938, 3, 0, 'There is no good. No evil. No Light. There is only POWER!', 12, 0, 100, 0, 0, 21411, 53278, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54938, 4, 0, 'We serve the world\'s TRUE masters! When their rule begins, we will share in their glory!', 12, 0, 100, 0, 0, 21412, 54755, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54938, 5, 0, 'And YOU! We will FEAST on your ashes! Now, DIE!', 14, 0, 100, 0, 0, 21413, 56544, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54938, 6, 0, 'The Light will consume you!', 14, 0, 100, 0, 0, 25868, 56542, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54938, 7, 0, 'Drown in Shadow!', 14, 0, 100, 0, 0, 0, 56543, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54938, 8, 0, 'Witness my POWER!', 14, 0, 100, 0, 0, 21416, 56541, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54943, 0, 0, 'Тс-с-с! Тихо. Она тренируется.', 12, 0, 100, 396, 0, 0, 59712, 0, 0, 0, 'Торговец Лорво');
INSERT INTO `creature_text` VALUES (54959, 0, 0, 'Как нужно махать этой штукой? Ай!', 12, 0, 100, 6, 0, 0, 55429, 0, 0, 0, 'Аквалит Жемчужного Плавника to Player');
INSERT INTO `creature_text` VALUES (54959, 3, 0, 'Именно то, что нужно.', 12, 0, 100, 15, 0, 0, 55424, 63000, 63000, 105710, 'Аквалит Жемчужного Плавника to Player');
INSERT INTO `creature_text` VALUES (54959, 3, 0, 'Мне нужно немедленно начать тренировку.', 12, 0, 100, 15, 0, 0, 55425, 131000, 131000, 105710, 'Аквалит Жемчужного Плавника to Player');
INSERT INTO `creature_text` VALUES (54959, 3, 0, 'Мне нужно снаряжение попрочнее.', 12, 0, 100, 274, 0, 0, 55428, 83000, 83000, 0, 'Аквалит Жемчужного Плавника to Player');
INSERT INTO `creature_text` VALUES (54959, 3, 0, 'Мне нужно что-то для защиты.', 12, 0, 100, 273, 0, 0, 55427, 59000, 59000, 0, 'Аквалит Жемчужного Плавника to Player');
INSERT INTO `creature_text` VALUES (54959, 3, 0, 'О да! Поскорее бы в бой!', 12, 0, 100, 274, 0, 0, 55545, 85000, 85000, 105710, 'Аквалит Жемчужного Плавника to Player');
INSERT INTO `creature_text` VALUES (54959, 3, 0, 'Это пригодится лекарю, а не мне!', 12, 0, 100, 1, 0, 0, 55426, 79000, 79000, 0, 'Аквалит Жемчужного Плавника to Player');
INSERT INTO `creature_text` VALUES (54959, 3, 0, 'Это... Это оружие для слабаков! Мне нужно побольше.', 12, 0, 100, 11, 0, 0, 55432, 125000, 125000, 0, 'Аквалит Жемчужного Плавника to Player');
INSERT INTO `creature_text` VALUES (54960, 0, 0, 'Ты когда-нибудь $gвидел:видела;, как камень падает в озеро? Каждое действие, даже предложение дружбы, создает рябь на поверхности. Я обдумаю твои слова.', 12, 0, 100, 0, 0, 0, 58066, 0, 0, 0, 'Старейшина Лушань to Player');
INSERT INTO `creature_text` VALUES (54960, 3, 0, 'Сегодня мы укрепим наши связи с Альянсом! Сегодня мы станем братьями и сестрами! За наше общее будущее!', 12, 0, 100, 0, 0, 0, 58231, 14000, 14000, 0, 'Старейшина Лушань to Player');
INSERT INTO `creature_text` VALUES (54968, 0, 0, 'Let\'s get to work, shall we?', 14, 0, 100, 0, 0, 25816, 53268, 0, 0, 0, 'asira aggro');
INSERT INTO `creature_text` VALUES (54968, 1, 0, 'You\'re much... better... than I thought...', 14, 0, 100, 0, 0, 25817, 54821, 0, 0, 0, 'asira death');
INSERT INTO `creature_text` VALUES (54968, 2, 0, 'Where do you think you\'re going, little lizard?', 14, 0, 100, 0, 0, 25818, 53825, 0, 0, 0, 'asira event 01');
INSERT INTO `creature_text` VALUES (54968, 3, 0, '...and with that out of the way, you and your flock of fumbling friends are next on my list. Mmm, I thought you\'d never get here!', 14, 0, 100, 0, 0, 25819, 53751, 0, 0, 0, 'asira event 02');
INSERT INTO `creature_text` VALUES (54968, 4, 0, 'Well, that was even easier than I thought.', 14, 0, 100, 0, 0, 25820, 54820, 0, 0, 0, 'asira event 03');
INSERT INTO `creature_text` VALUES (54968, 5, 0, 'So soon.', 14, 0, 100, 0, 0, 25821, 54822, 0, 0, 0, 'asira slay 01');
INSERT INTO `creature_text` VALUES (54968, 5, 1, 'I hope your friends can do better!', 14, 0, 100, 0, 0, 25822, 54823, 0, 0, 0, 'asira slay 02');
INSERT INTO `creature_text` VALUES (54968, 5, 2, 'Too much fun!', 14, 0, 100, 0, 0, 25823, 54824, 0, 0, 0, 'asira slay 03');
INSERT INTO `creature_text` VALUES (54968, 5, 3, 'Good night.', 14, 0, 100, 0, 0, 25824, 54825, 0, 0, 0, 'asira slay 04');
INSERT INTO `creature_text` VALUES (54968, 6, 0, 'Bye-bye.', 14, 0, 100, 0, 0, 25825, 54826, 0, 0, 0, 'asira spell 01');
INSERT INTO `creature_text` VALUES (54968, 6, 1, 'Now you see me...', 14, 0, 100, 0, 0, 25826, 54828, 0, 0, 0, 'asira spell 02');
INSERT INTO `creature_text` VALUES (54968, 6, 2, 'Surprise.', 14, 0, 100, 0, 0, 25827, 54827, 0, 0, 0, 'asira spell 03');
INSERT INTO `creature_text` VALUES (54969, 0, 0, 'Come Stormrage, and I will show you what happens to those that betray the lord of the Legion!', 14, 0, 100, 25, 0, 26478, 54223, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54969, 1, 0, '|TInterface\\Icons\\spell_fire_felrainoffire.blp:20|t %s begins to cast a spell |cFFFF0000|Hspell:88972|h[Огненная буря Скверны]|h|r!', 41, 0, 100, 0, 0, 0, 48107, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54969, 2, 0, '[Demonic] Amanare maev il azgalada zila ashj ashj zila enkil!', 14, 0, 100, 0, 0, 26488, 56644, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54969, 3, 0, 'Lord Sargeras, I will not fail you! Sweep your molten fist through this world, so that it may be reborn in flames and darkness!', 14, 0, 100, 0, 0, 26481, 54256, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54969, 4, 0, 'Yes...yes! I can feel his burning eyes upon me, he is close...so close! And then your world will be unmade, your lives as nothing!', 14, 0, 100, 0, 0, 26484, 54259, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54969, 5, 0, 'No...no! This victory will not be ripped from my grasp! I will not return to him in failure! I will not be torn from this pitiful world! No...NOOOOOOOO!!!', 14, 0, 100, 0, 0, 26479, 54271, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54971, 0, 0, 'I will NOT, Archbishop. It will never be yours.', 12, 0, 100, 396, 0, 25893, 53275, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54971, 1, 0, 'You were a figurehead of the Light, Benedictus. How could you betray your own people?', 12, 0, 100, 396, 0, 25894, 53279, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54972, 0, 0, 'Beware. Enemies Approach!', 14, 0, 100, 0, 0, 25883, 53553, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54972, 1, 0, 'I haven\'t come to be stopped by the likes of you.', 14, 0, 100, 0, 0, 25884, 53968, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54972, 2, 0, 'Let none stand in our way.', 14, 0, 100, 0, 0, 25886, 53821, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54972, 3, 0, 'Alexstrasza\'s drakes should meet us here...', 12, 0, 100, 0, 0, 25887, 53857, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54972, 4, 0, 'Up there, above us!', 12, 0, 100, 0, 0, 25888, 53875, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54972, 5, 0, 'An assassin! Quickly, ready yourselves for battle!', 12, 0, 100, 0, 0, 25889, 53913, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54972, 6, 0, 'Well done, Let us see to our friend...', 12, 0, 100, 0, 0, 25891, 53986, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54972, 7, 0, 'The rest of the drakes should be here shortly. I\'ll fly on ahead; catch up when you can.', 12, 0, 100, 0, 0, 25892, 53987, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (54975, 0, 0, 'Должна признать, за вами было интересно наблюдать!', 12, 0, 100, 0, 0, 27394, 66056, 0, 0, 0, 'Аиса Воспевающая Облака');
INSERT INTO `creature_text` VALUES (54975, 1, 0, 'И мне кажется, у тебя появился новый друг.', 12, 0, 100, 0, 0, 27395, 66057, 0, 0, 0, 'Аиса Воспевающая Облака');
INSERT INTO `creature_text` VALUES (54989, 3, 0, '$GДерзкий:Дерзкая; $gмаленький:маленькая; |3-6($r)!', 12, 0, 100, 0, 0, 0, 60987, 8000, 8000, 0, 'Гормальский поработитель to Player');
INSERT INTO `creature_text` VALUES (54989, 3, 0, 'I will teach you respect!', 12, 0, 100, 0, 0, 0, 60985, 8000, 8000, 0, 'Гормальский поработитель to Player');
INSERT INTO `creature_text` VALUES (54989, 3, 0, 'Знай свое место!', 12, 0, 100, 0, 0, 0, 133177, 14000, 14000, 0, 'Гормальский поработитель to Player');
INSERT INTO `creature_text` VALUES (54989, 3, 0, 'Кланяйся!', 12, 0, 100, 0, 0, 0, 60983, 91000, 91000, 0, 'Гормальский поработитель to Player');
INSERT INTO `creature_text` VALUES (54989, 3, 0, 'Мы научим тебя, как нужно служить!', 12, 0, 100, 0, 0, 0, 60982, 5000, 5000, 0, 'Гормальский поработитель to Player');
INSERT INTO `creature_text` VALUES (54989, 3, 0, 'Слабым нет места в этом мире!', 12, 0, 100, 0, 0, 0, 60984, 7000, 7000, 0, 'Гормальский поработитель to Player');
INSERT INTO `creature_text` VALUES (54990, 3, 0, 'Большое спасибо!', 12, 0, 100, 0, 0, 0, 53554, 9000, 9000, 0, 'Связанный фермер Сладкого Ветерка to Player');
INSERT INTO `creature_text` VALUES (54990, 3, 0, 'Когда все закончится, я вернусь в столицу.', 12, 0, 100, 0, 0, 0, 53559, 3372000, 3372000, 0, 'Связанный фермер Сладкого Ветерка to Player');
INSERT INTO `creature_text` VALUES (54990, 3, 0, 'Надеюсь, сад не погиб...', 12, 0, 100, 0, 0, 0, 53555, 34000, 34000, 0, 'Связанный фермер Сладкого Ветерка to Player');
INSERT INTO `creature_text` VALUES (55003, 0, 0, 'They\'\'ve killed the Greench!', 14, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (55003, 0, 1, 'Run!', 14, 0, 100, 1, 0, 0, 118743, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (55016, 0, 0, 'Вы всего лишь жалкие крестьяне с палками! Знайте свое место!', 14, 0, 100, 0, 0, 0, 58560, 0, 0, 0, 'Поработитель Гормал to Ополченец с фермы Сладкого Ветерка');
INSERT INTO `creature_text` VALUES (55016, 1, 0, 'Еще один доброволец хочет служить Властелину Грома.', 12, 0, 100, 0, 0, 0, 53338, 0, 0, 0, 'Поработитель Гормал');
INSERT INTO `creature_text` VALUES (55016, 1, 0, 'Из тебя получится отличный каменотес!', 12, 0, 100, 0, 0, 0, 58556, 0, 0, 0, 'Поработитель Гормал');
INSERT INTO `creature_text` VALUES (55016, 1, 0, 'Наша империя будет купаться в лучах славы! Но ты этого не увидишь!', 14, 0, 100, 0, 0, 0, 60989, 0, 0, 0, 'Поработитель Гормал to Ополченец с фермы Сладкого Ветерка');
INSERT INTO `creature_text` VALUES (55016, 3, 0, 'I will teach you humility!', 14, 0, 100, 0, 0, 0, 58558, 891000, 891000, 0, 'Поработитель Гормал to Ополченец с фермы Сладкого Ветерка');
INSERT INTO `creature_text` VALUES (55016, 3, 0, 'Неповиновения мы не потерпим!', 14, 0, 100, 0, 0, 0, 60990, 3000, 3000, 0, 'Поработитель Гормал to Ополченец с фермы Сладкого Ветерка');
INSERT INTO `creature_text` VALUES (55016, 4, 0, 'Дерзкие щенки! Сдавайтесь или умрите!', 14, 0, 100, 0, 0, 0, 58557, 17000, 33000, 0, 'Поработитель Гормал to Ополченец с фермы Сладкого Ветерка');
INSERT INTO `creature_text` VALUES (55016, 4, 0, 'Как ты смеешь дерзить мне! Знай свое место!', 14, 0, 100, 0, 0, 0, 58559, 10000, 10000, 0, 'Поработитель Гормал to Ополченец с фермы Сладкого Ветерка');
INSERT INTO `creature_text` VALUES (55016, 4, 0, 'Ты $gпосмел:посмела; бросить вызов могу? Тогда ты умрешь!', 14, 0, 100, 0, 0, 0, 60988, 2000, 2000, 0, 'Поработитель Гормал to Ополченец с фермы Сладкого Ветерка');
INSERT INTO `creature_text` VALUES (55025, 3, 0, 'Дело сделано, но здесь нам оставаться нельзя. Встретимся на восточной границе лагеря.', 12, 0, 100, 0, 0, 0, 53333, 26000, 26000, 0, 'Дерзкий Шао');
INSERT INTO `creature_text` VALUES (55085, 0, 0, 'None will reach the palace without besting Peroth\'arn, first of fel-touched!', 12, 0, 100, 0, 0, 26128, 54834, 0, 0, 0, 'Peroth\'arn');
