INSERT INTO `creature_text` VALUES (55103, 0, 0, 'Вытащите свой приз прямо из озера! Закидывайте удочку – и, может быть, вам улыбнется удача!', 12, 0, 100, 70, 0, 0, 95551, 0, 0, 0, 'Галисса Солнечная Роса to Player');
