INSERT INTO `creature_text` VALUES (55193, 1, 0, 'Ой-ой!.. Вождь будет недоволен...', 12, 0, 100, 397, 0, 0, 57970, 0, 0, 0, 'Шаман из племени Ловкого Хвоста to Player');
