INSERT INTO `creature_text` VALUES (55193, 4, 0, 'Дукеры!', 12, 0, 100, 25, 0, 0, 57980, 4000, 4000, 0, 'Шаман из племени Ловкого Хвоста to Player');
INSERT INTO `creature_text` VALUES (55196, 3, 0, 'Твой друг храбро сражался. Ему почти удалось сбежать, но все же он попал в плен, как и все мы.', 12, 0, 100, 0, 0, 30637, 57950, 10000, 10000, 0, 'Здоровяк Кажаши to Player');
INSERT INTO `creature_text` VALUES (55201, 3, 0, 'Духи воды ответили на мои молитвы. Спасибо тебе.', 12, 0, 100, 2, 0, 0, 53949, 112000, 112000, 0, 'Пленный цзинь-юй to Player');
INSERT INTO `creature_text` VALUES (55201, 3, 0, 'Я буду жить – и все благодаря тебе.', 12, 0, 100, 2, 0, 0, 53952, 112000, 112000, 0, 'Пленный цзинь-юй to Player');
INSERT INTO `creature_text` VALUES (55234, 3, 0, 'Эй, господин Железное Брюхо! Ведьма превратила моих друзей в глыбы нефрита. Ты поможешь их выручить?', 12, 0, 100, 0, 0, 0, 55746, 57000, 57000, 0, 'Ань Воздушный Мех');
INSERT INTO `creature_text` VALUES (55247, 0, 0, 'Огонь – это, конечно, страшно... но огонь, летающий по воздуху – вдвойне страшнее!', 12, 0, 100, 396, 0, 0, 53630, 0, 0, 0, 'Поот');
INSERT INTO `creature_text` VALUES (55249, 0, 0, 'Спорим, я бы приземлился точно в цель?', 12, 0, 100, 396, 0, 0, 53608, 0, 0, 0, 'Боуди');
