INSERT INTO `creature_text` VALUES (55250, 3, 0, 'Я думаю, я тоже мог бы жонглировать огнем.', 12, 0, 100, 396, 0, 0, 53633, 3350000, 3350000, 0, 'Дюкан');
INSERT INTO `creature_text` VALUES (55251, 3, 0, 'В песочнице играют только малыши.', 12, 0, 100, 396, 0, 0, 53596, 416000, 416000, 0, 'Намонд');
INSERT INTO `creature_text` VALUES (55251, 3, 0, 'Да ну, я мог бы их всех побить.', 12, 0, 100, 396, 0, 0, 53606, 3849000, 3849000, 0, 'Намонд');
