INSERT INTO `creature_text` VALUES (55252, 3, 0, 'Нам было велено играть здесь, в песочнице... Но, мне кажется, мы вполне можем побродить вокруг и не попасть в беду.', 12, 0, 100, 396, 0, 0, 53592, 848000, 848000, 0, 'Мишель');
INSERT INTO `creature_text` VALUES (55252, 3, 0, 'У меня был танк, но моя сестра его поломала...', 12, 0, 100, 396, 0, 0, 53601, 1999000, 1999000, 0, 'Мишель');
INSERT INTO `creature_text` VALUES (55253, 3, 0, 'А зачем нужны пушечные ядра? Может, они выдают их?..', 12, 0, 100, 396, 0, 0, 53612, 2613000, 2613000, 0, 'Рэнди');
