INSERT INTO `creature_text` VALUES (55268, 3, 0, 'Говорят, это совсем простая игра.', 12, 0, 100, 396, 0, 0, 53685, 569922, 569922, 0, 'Аджа');
INSERT INTO `creature_text` VALUES (55269, 3, 0, 'Обожаю торты!', 12, 0, 100, 396, 0, 0, 53695, 679609, 679609, 0, 'Джеррика');
INSERT INTO `creature_text` VALUES (55270, 3, 0, 'Воржей, наверно, добрый...', 12, 0, 100, 396, 0, 0, 53737, 530235, 530235, 0, 'Кимбер');
INSERT INTO `creature_text` VALUES (55270, 3, 0, 'Надеюсь, с ними все будет в порядке.', 12, 0, 100, 396, 0, 0, 53727, 131612, 131612, 0, 'Кимбер');
INSERT INTO `creature_text` VALUES (55271, 3, 0, 'Она такая сильная!', 12, 0, 100, 396, 0, 0, 53713, 766688, 766688, 0, 'Шана');
INSERT INTO `creature_text` VALUES (55274, 0, 0, 'Надеюсь, Нефритовая ведьма не сцапала всех моих друзей. Она такая страшная!', 12, 0, 100, 0, 0, 0, 54173, 0, 0, 0, 'Ань Воздушный Мех to Player');
INSERT INTO `creature_text` VALUES (55274, 3, 0, 'I\'m with you. You can be my bodyguard!', 12, 0, 100, 0, 0, 0, 54174, 0, 0, 0, 'Ань Воздушный Мех to Player');
INSERT INTO `creature_text` VALUES (55274, 3, 0, 'Ты $gтакой:такая; $gсмелый:смелая;! Научишь меня, как стать смелой?', 12, 0, 100, 0, 0, 0, 54175, 0, 0, 0, 'Ань Воздушный Мех to Player');
INSERT INTO `creature_text` VALUES (55282, 0, 0, 'I ... <sob>> I can\'t go on! Baby Lou, tell me what happened next.', 12, 0, 100, 18, 0, 27885, 53997, 0, 0, 0, 'Салли \"Рассольный\" Маклири to Player');
INSERT INTO `creature_text` VALUES (55283, 0, 0, 'Потеря енота – не самая страшная беда. Больше всего нас обеспокоил тот окаменевший орк... Я могу рассказать, что было дальше.', 12, 0, 100, 1, 0, 27464, 54129, 0, 0, 0, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` VALUES (55283, 0, 0, 'Релл, пожалуйста, побереги силы. О том, что было дальше, может рассказать Салли.', 12, 0, 100, 1, 0, 27457, 53989, 0, 0, 0, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` VALUES (55286, 3, 0, 'В северном святилище я никого не нашел – ни Белой Пешки, ни хозенов... никого.', 42, 0, 100, 0, 0, 27879, 54038, 232000, 232000, 0, 'Салли \"Рассольный\" Маклири to Player');
INSERT INTO `creature_text` VALUES (55286, 3, 0, 'Вперед, Стелька. Уходим отсюда, пока тебя не пристрелили.', 12, 0, 100, 1, 0, 27878, 54035, 194000, 194000, 0, 'Салли \"Рассольный\" Маклири to Player');
INSERT INTO `creature_text` VALUES (55286, 3, 0, 'Вскоре после этого со мной подружился еще один енот.', 42, 0, 100, 0, 0, 27876, 54033, 185000, 185000, 0, 'Салли \"Рассольный\" Маклири to Player');
INSERT INTO `creature_text` VALUES (55286, 3, 0, 'Есть!', 12, 0, 100, 66, 0, 27859, 114071, 43000, 43000, 0, 'Салли \"Рассольный\" Маклири to Player');
INSERT INTO `creature_text` VALUES (55286, 3, 0, 'Зато я встретил дружелюбного енота, который увязался за мной.', 42, 0, 100, 0, 0, 27871, 54027, 163000, 163000, 0, 'Салли \"Рассольный\" Маклири to Player');
INSERT INTO `creature_text` VALUES (55286, 3, 0, 'Когда я вернулся, то обнаружил, что на Релла снова напали хозены!', 42, 0, 100, 0, 0, 27726, 54067, 319000, 319000, 0, 'Салли \"Рассольный\" Маклири to Player');
INSERT INTO `creature_text` VALUES (55286, 3, 0, 'Мы двигались на север и вышли на опушку.', 42, 0, 100, 0, 0, 27856, 54019, 2000, 2000, 0, 'Салли \"Рассольный\" Маклири to Player');
INSERT INTO `creature_text` VALUES (55286, 3, 0, 'Недолго думая я назвал его Стелька.', 42, 0, 100, 0, 0, 27877, 54034, 191000, 191000, 0, 'Салли \"Рассольный\" Маклири to Player');
INSERT INTO `creature_text` VALUES (55286, 3, 0, 'Нет, но я могу его сделать... Командир, а топор нужен для рубки деревьев или врагов?', 12, 0, 100, 6, 0, 27858, 54001, 15000, 15000, 0, 'Салли \"Рассольный\" Маклири to Player');
INSERT INTO `creature_text` VALUES (55286, 3, 0, 'Нет! Штуковина!', 14, 0, 100, 5, 0, 27874, 54029, 176000, 176000, 104024, 'Салли \"Рассольный\" Маклири to Player');
INSERT INTO `creature_text` VALUES (55286, 3, 0, 'Нет... Релл...', 12, 0, 100, 0, 0, 27884, 54044, 327000, 327000, 103722, 'Салли \"Рассольный\" Маклири to 55589');
INSERT INTO `creature_text` VALUES (55286, 3, 0, 'Осмотрев три святилища, я повернул обратно к лагерю.', 42, 0, 100, 0, 0, 27880, 54039, 239000, 239000, 103533, 'Салли \"Рассольный\" Маклири to Player');
INSERT INTO `creature_text` VALUES (55286, 3, 0, 'Стелька пошел следом.', 42, 0, 100, 0, 0, 27881, 54040, 244000, 244000, 0, 'Салли \"Рассольный\" Маклири to Player');
INSERT INTO `creature_text` VALUES (55286, 3, 0, 'У юго-западного святилища я не обнаружил никаких следов Белой Пешки.', 42, 0, 100, 0, 0, 27870, 54026, 158000, 158000, 103995, 'Салли \"Рассольный\" Маклири to Player');
INSERT INTO `creature_text` VALUES (55286, 3, 0, 'Эй, малыш! Я назову тебя Штуковина.', 12, 0, 100, 3, 0, 27873, 54031, 171000, 171000, 0, 'Салли \"Рассольный\" Маклири to Player');
INSERT INTO `creature_text` VALUES (55286, 3, 0, 'Эмбер, зачем?! Я ведь его не подсвечивал!', 14, 0, 100, 5, 0, 27875, 54032, 180000, 180000, 0, 'Салли \"Рассольный\" Маклири to Player');
INSERT INTO `creature_text` VALUES (55286, 3, 0, 'Я назвал его Штуковина.', 42, 0, 100, 0, 0, 27872, 54028, 169000, 169000, 0, 'Салли \"Рассольный\" Маклири to Player');
INSERT INTO `creature_text` VALUES (55286, 4, 0, 'Да уж, Эмбер лучше не злить!', 12, 0, 100, 0, 0, 27865, 54013, 3000, 3000, 103866, 'Салли \"Рассольный\" Маклири');
INSERT INTO `creature_text` VALUES (55286, 4, 0, 'Отличный выстрел, Эмбер!', 12, 0, 100, 0, 0, 27862, 54010, 3000, 3000, 103866, 'Салли \"Рассольный\" Маклири');
INSERT INTO `creature_text` VALUES (55286, 4, 0, 'Хотел подкрасться к старине Салли, а?', 12, 0, 100, 0, 0, 27867, 54015, 5000, 8000, 103866, 'Салли \"Рассольный\" Маклири');
INSERT INTO `creature_text` VALUES (55290, 4, 0, 'Тьма...', 12, 0, 100, 0, 0, 0, 105798, 1375, 1375, 0, 'Своенравный предок');
INSERT INTO `creature_text` VALUES (55291, 1, 0, 'Мой сосуд ждет тебя!', 12, 0, 100, 0, 0, 0, 66899, 0, 0, 0, 'Кромсатель духов Шань\'цзэ to Player');
INSERT INTO `creature_text` VALUES (55291, 3, 0, 'Не шевелись, осталось недолго...', 12, 0, 100, 0, 0, 0, 66903, 11000, 11000, 0, 'Кромсатель духов Шань\'цзэ to Player');
INSERT INTO `creature_text` VALUES (55291, 3, 0, 'Хороший дух... думаю, мне такой пригодится.', 12, 0, 100, 0, 0, 0, 66905, 7320000, 7320000, 0, 'Кромсатель духов Шань\'цзэ to Player');
INSERT INTO `creature_text` VALUES (55294, 0, 0, 'Now is the Hour of Twilight!', 14, 0, 100, 0, 0, 26314, 55329, 0, 0, 0, 'Ultraxion aggro');
INSERT INTO `creature_text` VALUES (55294, 1, 0, 'I WILL DRAG YOU WITH ME INTO FLAME AND DARKNESS!', 14, 0, 100, 0, 0, 26315, 55337, 0, 0, 0, 'Ultraxion berserk');
