INSERT INTO `creature_text` VALUES (55294, 4, 0, 'For this moment ALONE was I made. Look upon your death, mortals, and despair!', 14, 0, 100, 0, 0, 26318, 55323, 0, 0, 0, 'Ultraxion intro 02');
