INSERT INTO `creature_text` VALUES (55308, 7, 0, 'Victory for Deathwing. For the glory of N\'Zoth!', 42, 0, 100, 0, 0, 0, 56757, 0, 0, 0, 'zonozz aggro whisper');
INSERT INTO `creature_text` VALUES (55308, 8, 0, 'To have waited so long... for this...', 42, 0, 100, 0, 0, 0, 56765, 0, 0, 0, 'zonozz death whisper');
INSERT INTO `creature_text` VALUES (55308, 9, 0, 'Once more shall the twisted flesh-banners of N\'Zoth chitter and howl above the fly-blown corpse of this world. After millennia, we have returned.', 42, 0, 100, 0, 0, 0, 56755, 0, 0, 0, 'zonozz intro whisper');
INSERT INTO `creature_text` VALUES (55308, 10, 0, 'Your souls shall sate N\'Zoth\'s endless hunger.', 42, 0, 100, 0, 0, 0, 56782, 0, 0, 0, 'zonozz slay 1 whisper');
INSERT INTO `creature_text` VALUES (55308, 10, 1, 'Your deaths shall sing of N\'Zoth\'s unending glory.', 42, 0, 100, 0, 0, 0, 56784, 0, 0, 0, 'zonozz slay 2 whisper');
INSERT INTO `creature_text` VALUES (55308, 10, 2, 'Your skulls shall adorn N\'Zoth\'s writhing throne.', 42, 0, 100, 0, 0, 0, 56779, 0, 0, 0, 'zonozz slay 3 whisper');
INSERT INTO `creature_text` VALUES (55308, 11, 0, 'Your fear drives me.', 42, 0, 100, 0, 0, 0, 56800, 0, 0, 0, 'zonozz spell 1 whisper');
