INSERT INTO `creature_text` VALUES (55312, 0, 0, 'Iilth qi\'uothk shn\'ma yeh\'glu Shath\'Yar! H\'IWN IILTH!', 14, 0, 100, 0, 0, 26326, 56804, 0, 0, 0, 'yor\'sahj aggro');
INSERT INTO `creature_text` VALUES (55312, 1, 0, 'Ez, Shuul\'wah! Sk\'woth\'gl yu\'gaz yog\'ghyl ilfah!', 14, 0, 100, 0, 0, 26327, 56806, 0, 0, 0, 'yor\'sahj death');
INSERT INTO `creature_text` VALUES (55312, 2, 0, 'Ak\'agthshi ma uhnish, ak\'uq shg\'cul vwahuhn! H\'iwn iggksh Phquathi gag OOU KAAXTH SHUUL!', 14, 0, 100, 0, 0, 26328, 56802, 0, 0, 0, 'yor\'sahj intro');
INSERT INTO `creature_text` VALUES (55312, 3, 0, 'Sk\'yahf qi\'plahf PH\'MAGG!', 14, 0, 100, 0, 0, 26329, 56809, 0, 0, 0, 'yor\'sahj slay 1');
INSERT INTO `creature_text` VALUES (55312, 3, 1, 'H\'iwn zaix Shuul\'wah, PHQUATHI!', 14, 0, 100, 0, 0, 26330, 56811, 0, 0, 0, 'yor\'sahj slay 2');
INSERT INTO `creature_text` VALUES (55312, 3, 2, 'Shkul an\'zig qvsakf KSSH\'GA, ag\'THYZAK agthu!', 14, 0, 100, 0, 0, 26331, 56814, 0, 0, 0, 'yor\'sahj slay 3');
INSERT INTO `creature_text` VALUES (55312, 4, 0, 'KYTH ag\'xig yyg\'far IIQAATH ONGG!', 14, 0, 100, 0, 0, 26332, 56816, 0, 0, 0, 'yor\'sahj spell 1');
INSERT INTO `creature_text` VALUES (55312, 4, 1, 'UULL lwhuk H\'IWN!', 14, 0, 100, 0, 0, 26333, 56818, 0, 0, 0, 'yor\'sahj spell 2');
INSERT INTO `creature_text` VALUES (55312, 5, 0, 'You will drown in the blood of the Old Gods! ALL OF YOU!', 42, 0, 100, 0, 0, 0, 56805, 0, 0, 0, 'yor\'sahj aggro whisper');
INSERT INTO `creature_text` VALUES (55312, 6, 0, 'O, Deathwing! Your faithful servant has failed you!', 42, 0, 100, 0, 0, 0, 56808, 0, 0, 0, 'yor\'sahj death whisper');
INSERT INTO `creature_text` VALUES (55312, 7, 0, 'Our numbers are endless, our power beyond reckoning! All who oppose the Destroyer will DIE A THOUSAND DEATHS!', 42, 0, 100, 0, 0, 0, 56803, 0, 0, 0, 'yor\'sahj intro whisper');
INSERT INTO `creature_text` VALUES (55312, 8, 0, 'Your soul will know ENDLESS TORMENT!', 42, 0, 100, 0, 0, 0, 56810, 0, 0, 0, 'yor\'sahj slay 1 whisper');
INSERT INTO `creature_text` VALUES (55312, 8, 1, 'All praise Deathwing, THE DESTROYER!', 42, 0, 100, 0, 0, 0, 56812, 0, 0, 0, 'yor\'sahj slay 2 whisper');
INSERT INTO `creature_text` VALUES (55312, 8, 2, 'From its BLEAKEST DEPTHS, we RECLAIM this world!', 42, 0, 100, 0, 0, 0, 56815, 0, 0, 0, 'yor\'sahj slay 3 whisper');
INSERT INTO `creature_text` VALUES (55312, 9, 0, 'SEE how we pour from the CURSED EARTH!', 42, 0, 100, 0, 0, 0, 56817, 0, 0, 0, 'yor\'sahj spell 1 whisper');
INSERT INTO `creature_text` VALUES (55312, 9, 1, 'The DARKNESS devours ALL!', 42, 0, 100, 0, 0, 0, 56819, 0, 0, 0, 'yor\'sahj spell 2 whisper');
