INSERT INTO `creature_text` VALUES (55335, 3, 0, '|TInterface\\Icons\\inv_weapon_shortblade_37.blp:32|t Мощный удар$b$b|TInterface\\Icons\\inv_weapon_shortblade_92.blp:32|t Более мощный удар$b$b|TInterface\\Icons\\ability_rogue_shadowstrikes.blp:32|t Самый мощный удар', 42, 0, 100, 0, 0, 0, 68789, 67000, 67000, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (55335, 3, 0, 'I got ready for the battle.', 42, 0, 100, 0, 0, 27719, 54022, 64000, 64000, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (55335, 3, 0, 'Внимание! Хозены устроили нам засаду!', 12, 0, 100, 22, 0, 27716, 53838, 49000, 49000, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (55335, 3, 0, 'Малыш Лу, не дай Эмбер погибнуть! А ты, Салли, сделай так, чтобы самолет не взорвался!', 12, 0, 100, 397, 0, 27717, 53839, 54000, 54000, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (55335, 3, 0, 'Несколько часов мы шли по лесу... на север.', 42, 0, 100, 0, 0, 27713, 53990, 0, 0, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (55335, 3, 0, 'Салли, как думаешь, что случилось с этим деревом?', 12, 0, 100, 6, 0, 27715, 53831, 11000, 11000, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (55335, 3, 0, 'Увидев странное поваленное дерево, мы остановились.', 42, 0, 100, 0, 0, 27714, 53994, 8000, 8000, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (55335, 3, 0, 'Я буду сдерживать их, сколько смогу!', 12, 0, 100, 22, 0, 27718, 53840, 61000, 61000, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (55337, 3, 0, 'Нет, это самолет. Погоди-ка... САМОЛЕТ?!', 12, 0, 100, 5, 0, 27852, 53834, 28000, 28000, 0, 'Салли \"Рассольный\" Маклири to Релл Ночной Ветер');
INSERT INTO `creature_text` VALUES (55337, 3, 0, 'Она еще дышит! Сюда! Поговорите с ней!', 14, 0, 100, 5, 0, 27854, 53837, 45000, 45000, 0, 'Салли \"Рассольный\" Маклири to Релл Ночной Ветер');
INSERT INTO `creature_text` VALUES (55337, 3, 0, 'Ты что, шутишь? Посмотри на себя – ты же едва жива!', 12, 0, 100, 5, 0, 27855, 53884, 138000, 138000, 0, 'Салли \"Рассольный\" Маклири to Релл Ночной Ветер');
INSERT INTO `creature_text` VALUES (55337, 3, 0, 'Что-то в него врезалось, сэр. Его явно не рубили и оно не падало само.', 12, 0, 100, 1, 0, 27849, 53832, 17000, 17000, 0, 'Салли \"Рассольный\" Маклири to Релл Ночной Ветер');
INSERT INTO `creature_text` VALUES (55337, 3, 0, 'Это самолет Эмбер и Нимм! Может, они еще живы!', 14, 0, 100, 5, 0, 27853, 53835, 33000, 33000, 0, 'Салли \"Рассольный\" Маклири to Релл Ночной Ветер');
INSERT INTO `creature_text` VALUES (55338, 3, 0, 'А что это вон там? Какая-то птица?', 12, 0, 100, 6, 0, 29869, 53833, 22000, 22000, 0, 'Малыш Лу to Релл Ночной Ветер');
INSERT INTO `creature_text` VALUES (55338, 3, 0, 'Белую Пешку? Я думал, мы охотимся на хозенов.', 12, 0, 100, 6, 0, 29871, 53886, 150000, 150000, 0, 'Малыш Лу to Релл Ночной Ветер');
INSERT INTO `creature_text` VALUES (55338, 3, 0, 'Нам за ним идти? И кто такие Эмбер и Нимм?', 12, 0, 100, 6, 0, 29870, 53836, 39000, 39000, 0, 'Малыш Лу to Релл Ночной Ветер');
INSERT INTO `creature_text` VALUES (55339, 0, 0, 'Game tokens for sale! Come get your game tokens!', 12, 0, 100, 396, 0, 0, 53664, 0, 0, 0, 'Sazz Coincatcher - OCC Yell Line (1)');
INSERT INTO `creature_text` VALUES (55339, 0, 1, 'You\'ll need game tokens to play the games. Come and get your game tokens!', 12, 0, 100, 396, 0, 0, 53665, 0, 0, 0, 'Sazz Coincatcher - OCC Yell Line (2)');
INSERT INTO `creature_text` VALUES (55339, 0, 2, 'Tokens! Tokens! Tokens!', 12, 0, 100, 396, 0, 0, 53661, 0, 0, 0, 'Sazz Coincatcher - OCC Yell Line (3)');
INSERT INTO `creature_text` VALUES (55339, 0, 3, 'Tokens! Get your tokens!', 12, 0, 100, 396, 0, 0, 53662, 0, 0, 0, 'Sazz Coincatcher- OCC Yell Line (4)');
INSERT INTO `creature_text` VALUES (55339, 0, 4, 'Hurry, hurry, hurry! Step right up and purchase your game tokens!', 12, 0, 100, 396, 0, 0, 53666, 0, 0, 0, 'Sazz Coincatcher - OCC Yell Line (4)');
INSERT INTO `creature_text` VALUES (55339, 0, 5, 'Step right up and get your game tokens!', 12, 0, 100, 396, 0, 0, 53660, 0, 0, 0, 'Sazz Coincatcher - OCC Yell Line (5)');
INSERT INTO `creature_text` VALUES (55339, 0, 6, 'You gotta pay to play, folks! Come and get your game tokens!', 12, 0, 100, 396, 0, 0, 53663, 0, 0, 0, 'Sazz Coincatcher - OCC Yell Line (6)');
INSERT INTO `creature_text` VALUES (55343, 3, 0, 'В этом нет необходимости. Я пойду с вами.', 12, 0, 100, 1, 0, 27455, 53883, 133000, 133000, 0, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` VALUES (55343, 3, 0, 'Все нормально, Салли. Мои раны затянутся. Сейчас наша задача – найти Белую Пешку.', 12, 0, 100, 1, 0, 27456, 53885, 144000, 144000, 0, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` VALUES (55354, 3, 0, 'М-м-м... Он назвал меня агентом.', 12, 0, 100, 1, 0, 29872, 53999, 7000, 7000, 0, 'Малыш Лу to Салли \"Рассольный\" Маклири');
INSERT INTO `creature_text` VALUES (55355, 3, 0, 'Деревьев, Салли. Малыш Лу, возьмешь топор и нарубишь дров.', 12, 0, 100, 1, 0, 27723, 54002, 21000, 21000, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (55355, 3, 0, 'Помогите!', 14, 0, 100, 1, 0, 27726, 140174, 0, 6000, 0, 'Релл Ночной Ветер');
INSERT INTO `creature_text` VALUES (55355, 3, 0, 'Салли, осмотри святилища на западе. Постарайся найти хоть какие-нибудь следы Белой Пешки.', 12, 0, 100, 25, 0, 27724, 54004, 30000, 30000, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (55355, 3, 0, 'Салли! Малыш Лу! Кто-нибудь!', 14, 0, 100, 1, 0, 27728, 58590, 12000, 12000, 0, 'Релл Ночной Ветер');
INSERT INTO `creature_text` VALUES (55355, 3, 0, 'Так, агенты, слушайте меня.', 12, 0, 100, 1, 0, 27721, 53998, 5000, 5000, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (55355, 3, 0, 'Уже поздно, нам нужно разбить лагерь. Салли, у тебя топор есть?', 12, 0, 100, 1, 0, 27722, 54000, 10000, 10000, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (55355, 3, 0, 'Эмбер, а ты заберись повыше и прикрой Салли огнем. Встречаемся здесь.', 12, 0, 100, 1, 0, 27725, 54005, 36000, 36000, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (55368, 1, 0, 'Присоединяйся к нашей семье. В нефрите ты сохранишься навеки!', 12, 0, 100, 1, 0, 0, 55542, 0, 0, 0, 'Вдова Зеленая Лапа to Player');
INSERT INTO `creature_text` VALUES (55368, 4, 0, 'Кис-кис-кис.', 12, 0, 100, 1, 0, 0, 55541, 13000, 13000, 0, 'Вдова Зеленая Лапа');
INSERT INTO `creature_text` VALUES (55370, 0, 0, 'Портал сработал, и нас не разорвало на части. Хорошо, а то я боялся, что еще один маяк перегрузит сеть.', 12, 0, 100, 0, 0, 78232, 123883, 0, 0, 0, 'Главный телемант Окулет to Player');
INSERT INTO `creature_text` VALUES (55371, 3, 0, 'Агенты! Это дом пандаренов. А пандарены – наши союзники...', 12, 0, 100, 1, 0, 29875, 53899, 20000, 20000, 0, 'Малыш Лу to Player');
INSERT INTO `creature_text` VALUES (55371, 3, 0, 'Вся лужайка была заставлена небольшими нефритовыми статуями. Они были...почти как живые.', 42, 0, 100, 0, 0, 29877, 54084, 51000, 51000, 0, 'Малыш Лу to Player');
INSERT INTO `creature_text` VALUES (55371, 3, 0, 'Знак был не очень-то... располагающим ко входу.', 42, 0, 100, 0, 0, 29876, 54083, 40000, 40000, 0, 'Малыш Лу to Player');
INSERT INTO `creature_text` VALUES (55371, 3, 0, 'И вдруг я понял, что происходит... но на секунду опоздал.', 42, 0, 100, 0, 0, 29880, 54094, 114000, 114000, 0, 'Малыш Лу to Player');
INSERT INTO `creature_text` VALUES (55371, 3, 0, 'Мы осмотрели статуи, и тут из дома вышла женщина-пандарен.', 42, 0, 100, 0, 0, 29879, 54115, 79000, 79000, 0, 'Малыш Лу to Player');
INSERT INTO `creature_text` VALUES (55371, 3, 0, 'Релл выжил, но дела его были плохи.', 42, 0, 100, 0, 0, 29874, 54071, 5000, 5000, 0, 'Малыш Лу to Player');
INSERT INTO `creature_text` VALUES (55371, 3, 0, 'Салли... это не статуи! Она хочет превратить тебя в камень!', 14, 0, 100, 1, 0, 29881, 54093, 120000, 120000, 0, 'Малыш Лу to Вдова Зеленая Лапа');
INSERT INTO `creature_text` VALUES (55371, 3, 0, 'Среди них была одна особенная. Никогда подобного не видел.', 42, 0, 100, 0, 0, 29878, 54085, 68000, 68000, 0, 'Малыш Лу to Player');
INSERT INTO `creature_text` VALUES (55372, 3, 0, 'Да... и енота тоже. Я позабочусь о нашем командире.', 12, 0, 100, 1, 0, 27463, 53902, 32000, 32000, 0, 'Эмбер Кеарнен to Малыш Лу');
INSERT INTO `creature_text` VALUES (55372, 3, 0, 'Реллу становится хуже. Мы должны остановиться, чтобы он мог отдохнуть.', 12, 0, 100, 1, 0, 27461, 53897, 9000, 9000, 0, 'Эмбер Кеарнен to Малыш Лу');
INSERT INTO `creature_text` VALUES (55372, 3, 0, 'Хорошая мысль, Малыш Лу. Пойди и узнай, кто здесь живет. Возьми с собой Салли.', 12, 0, 100, 1, 0, 27462, 53900, 26000, 26000, 0, 'Эмбер Кеарнен to Малыш Лу');
INSERT INTO `creature_text` VALUES (55374, 3, 0, 'Беги, Малыш Лу! Не дай ей до тебя добраться!', 14, 0, 100, 5, 0, 27894, 54127, 133000, 133000, 0, 'Салли \"Рассольный\" Маклири to Малыш Лу');
INSERT INTO `creature_text` VALUES (55374, 3, 0, 'Если бы Мишка была здесь, она бы его мигом заштопала.', 12, 0, 100, 6, 0, 27886, 53898, 13000, 13000, 0, 'Салли \"Рассольный\" Маклири to Малыш Лу');
INSERT INTO `creature_text` VALUES (55374, 3, 0, 'И Стельку!', 12, 0, 100, 6, 0, 27887, 53901, 30000, 30000, 0, 'Салли \"Рассольный\" Маклири to Малыш Лу');
INSERT INTO `creature_text` VALUES (55374, 3, 0, 'Минуточку! Это кто – орк?! Значит... и здесь Орда?!', 12, 0, 100, 6, 0, 27890, 54088, 73000, 73000, 0, 'Салли \"Рассольный\" Маклири to Малыш Лу');
INSERT INTO `creature_text` VALUES (55374, 3, 0, 'О чем ты говоришь?', 12, 0, 100, 274, 0, 27892, 54092, 111000, 111000, 0, 'Салли \"Рассольный\" Маклири to Малыш Лу');
INSERT INTO `creature_text` VALUES (55374, 3, 0, 'Ого! Сколько тут статуй...', 12, 0, 100, 6, 0, 27889, 54087, 58000, 58000, 0, 'Салли \"Рассольный\" Маклири to Малыш Лу');
INSERT INTO `creature_text` VALUES (55374, 3, 0, 'Стелька! НЕ-Е-ЕТ!', 14, 0, 100, 5, 0, 27893, 54096, 130000, 130000, 0, 'Салли \"Рассольный\" Маклири to Малыш Лу');
INSERT INTO `creature_text` VALUES (55374, 3, 0, 'Сударыня, я дворф. Можно одолжить у вас еды?', 12, 0, 100, 24, 0, 27891, 54090, 100000, 100000, 0, 'Салли \"Рассольный\" Маклири to Малыш Лу');
INSERT INTO `creature_text` VALUES (55374, 3, 0, 'Ха! Какой-то знак не остановит меня и моего лучшего друга Стельку!', 12, 0, 100, 4, 0, 27888, 54086, 44000, 44000, 0, 'Салли \"Рассольный\" Маклири to Малыш Лу');
INSERT INTO `creature_text` VALUES (55381, 3, 0, 'А-ХА-ХА-ХА-ХА!', 14, 0, 100, 0, 0, 31401, 89650, 49000, 49000, 0, 'Вдова Зеленая Лапа to Малыш Лу');
INSERT INTO `creature_text` VALUES (55381, 3, 0, 'Мы охотно примем бородатого духа в семью!', 12, 0, 100, 0, 0, 31400, 54091, 24000, 24000, 0, 'Вдова Зеленая Лапа to Малыш Лу');
INSERT INTO `creature_text` VALUES (55381, 3, 0, 'Поглядите, кто пришел... Цзинь-юй и... Кто же это? Какой-то бородатый дух?', 12, 0, 100, 6, 0, 31399, 54089, 10000, 10000, 0, 'Вдова Зеленая Лапа to Малыш Лу');
INSERT INTO `creature_text` VALUES (55387, 3, 0, 'Мой народ не любит чужаков, но тебе я очень обязан. Надеюсь, старейшины не накажут меня за этот поступок.', 12, 0, 100, 0, 0, 4674, 53796, 8000, 8000, 0, 'Здоровяк Кажаши to Player');
INSERT INTO `creature_text` VALUES (55387, 3, 0, 'Нам предстоит пересечь поле битвы. Займись хозенами, а я побегу вперед. Если все получится, встретимся в моей деревне.', 12, 0, 100, 0, 0, 30640, 58025, 19000, 19000, 0, 'Здоровяк Кажаши to Player');
INSERT INTO `creature_text` VALUES (55393, 3, 0, 'Эта магия слишком сильна!', 14, 0, 100, 0, 0, 26646, 55967, 20111, 20111, 0, 'Изера to Player');
INSERT INTO `creature_text` VALUES (55394, 3, 0, 'За мной, сестра, пора покончить с этим. Мы очистим Душу Дракона от скверны древних богов!', 14, 0, 100, 0, 0, 26508, 55966, 8318, 8318, 0, 'Алекстраза to Player');
INSERT INTO `creature_text` VALUES (55400, 3, 0, 'ОНА МОЯ!', 14, 0, 100, 0, 0, 26108, 55970, 13077, 13077, 0, 'Нелтарион to Player');
INSERT INTO `creature_text` VALUES (55402, 3, 0, 'Внимание! Дорогие гости! До начала схватки Новолуния остались считанные секунды! Сундук с призом вот-вот появится в центре арены!', 14, 0, 100, 0, 0, 0, 53813, 2661000, 2661000, 0, 'Коргол Череподроб');
INSERT INTO `creature_text` VALUES (55402, 3, 0, 'Итак, схватка Новолуния закончилась! $GПобедитель:Победительница; – $n!', 14, 0, 100, 22, 0, 0, 53944, 4216000, 4216000, 0, 'Коргол Череподроб to Player');
INSERT INTO `creature_text` VALUES (55405, 3, 0, 'I climbed to the top of the mountain and prepared to shoot with my sniper rifle.', 42, 0, 100, 0, 0, 27466, 54133, 13000, 13000, 0, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` VALUES (55405, 3, 0, 'I strained my eyesight and began to read lips. He said:', 42, 0, 100, 0, 0, 27469, 54137, 28000, 28000, 101438, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` VALUES (55405, 3, 0, 'Как прикажете, командир.', 12, 0, 100, 66, 0, 27467, 74770, 10000, 10000, 0, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` VALUES (55405, 3, 0, 'Как я и предполагала... Орда уже здесь, в Пандарии. И они заключили союз с хозенами.', 42, 0, 100, 0, 0, 27471, 54144, 202000, 202000, 103789, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` VALUES (55405, 3, 0, 'Мы разбили лагерь в горах. Малыш Лу следил за Реллом.', 42, 0, 100, 0, 0, 27465, 54132, 0, 0, 0, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` VALUES (55405, 3, 0, 'Нам нужно было сообщить об этом жителям деревни Жемчужного Плавника... но сначала мы решили подстрелить парочку орков.', 42, 0, 100, 0, 0, 27472, 54145, 209000, 209000, 103789, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` VALUES (55405, 3, 0, 'Салли расставил бочки со взрывчаткой – а я должна была по ним стрелять.', 42, 0, 100, 0, 0, 27470, 54140, 110000, 110000, 103789, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` VALUES (55405, 3, 0, 'Салли уже был на краю деревни.', 42, 0, 100, 0, 0, 27468, 54136, 25000, 25000, 103789, 'Эмбер Кеарнен to Player');
INSERT INTO `creature_text` VALUES (55407, 3, 0, 'Убей побольше хозенов, Эмбер.', 12, 0, 100, 0, 0, 27730, 54134, 3000, 3000, 0, 'Релл Ночной Ветер to Player');
INSERT INTO `creature_text` VALUES (55408, 3, 0, 'А это за Штуковину и за Стельку!', 12, 0, 100, 5, 0, 27900, 54150, 107000, 107000, 103789, 'Салли \"Рассольный\" Маклири to Салли \"Рассольный\" Маклири');
INSERT INTO `creature_text` VALUES (55408, 3, 0, 'Ах! Осторожно!', 14, 0, 100, 5, 0, 27901, 54147, 97000, 97000, 0, 'Салли \"Рассольный\" Маклири to Взрывной бочонок Салли');
INSERT INTO `creature_text` VALUES (55408, 3, 0, 'Вижу проход! Эмбер, я иду!', 12, 0, 100, 5, 0, 27904, 54146, 207000, 207000, 0, 'Салли \"Рассольный\" Маклири to Салли \"Рассольный\" Маклири');
INSERT INTO `creature_text` VALUES (55408, 3, 0, 'Застрели всех стражников, и тогда я смогу пройти дальше!', 12, 0, 100, 5, 0, 27895, 54475, 7000, 7000, 0, 'Салли \"Рассольный\" Маклири to Эмбер Кеарнен');
INSERT INTO `creature_text` VALUES (55408, 3, 0, 'Здесь ничего нет! Идем дальше!', 12, 0, 100, 5, 0, 27902, 54142, 120000, 120000, 0, 'Салли \"Рассольный\" Маклири to Салли \"Рассольный\" Маклири');
INSERT INTO `creature_text` VALUES (55408, 3, 0, 'Отлично получилось! А теперь еще раз!', 12, 0, 100, 5, 0, 27896, 54139, 62000, 62000, 103852, 'Салли \"Рассольный\" Маклири to Салли \"Рассольный\" Маклири');
INSERT INTO `creature_text` VALUES (55408, 3, 0, 'У меня подарочки для обезьян! Эмбер, постарайся меня не взорвать!', 12, 0, 100, 5, 0, 27897, 54141, 81000, 81000, 0, 'Салли \"Рассольный\" Маклири to Салли \"Рассольный\" Маклири');
INSERT INTO `creature_text` VALUES (55408, 3, 0, 'Что-то есть... Ящик Орды! Прикрой меня, я выйду на открытое место!', 12, 0, 100, 5, 0, 27903, 54143, 138000, 138000, 0, 'Салли \"Рассольный\" Маклири to Салли \"Рассольный\" Маклири');
INSERT INTO `creature_text` VALUES (55408, 3, 0, 'Это вам за Релла!', 12, 0, 100, 5, 0, 27899, 54149, 100000, 100000, 103789, 'Салли \"Рассольный\" Маклири to Салли \"Рассольный\" Маклири');
INSERT INTO `creature_text` VALUES (55408, 3, 0, 'Это вам за Эмбер!', 12, 0, 100, 5, 0, 27898, 54148, 95000, 95000, 103789, 'Салли \"Рассольный\" Маклири to Салли \"Рассольный\" Маклири');
INSERT INTO `creature_text` VALUES (55413, 3, 0, 'Тебя ищет Тоя. Он собирает весь лишний нефрит, который у нас есть.', 12, 0, 100, 0, 0, 0, 61152, 38000, 38000, 0, 'Ань Воздушный Мех to Player');
INSERT INTO `creature_text` VALUES (55419, 0, 0, 'For you, Azshara.', 12, 0, 100, 0, 0, 26134, 54225, 0, 0, 0, 'Капитан Варо\'\'тен');
INSERT INTO `creature_text` VALUES (55419, 1, 0, 'Highguard, to arms! For your queen! For Azshara!', 14, 0, 100, 0, 0, 26137, 54119, 0, 0, 0, 'Капитан Варо\'\'тен');
INSERT INTO `creature_text` VALUES (55419, 2, 0, 'Light of lights...I have failed you. I am sorry...my Azshara...', 12, 0, 100, 0, 0, 26135, 54253, 0, 0, 0, 'Капитан Варо\'\'тен');
INSERT INTO `creature_text` VALUES (55461, 3, 0, 'Ты умрешь! Таков приказ Орды!', 12, 0, 100, 0, 0, 0, 61250, 42000, 42000, 0, 'Резервист Грукин to Player');
INSERT INTO `creature_text` VALUES (55470, 1, 0, 'Нет империалистическим шнуропсам!', 12, 0, 100, 407, 0, 0, 62313, 0, 0, 0, 'Хозен-задира to Player');
INSERT INTO `creature_text` VALUES (55470, 3, 0, 'Вбей шнуропсов в землю по самые финтипоны!', 12, 0, 100, 407, 0, 0, 62312, 9000, 9000, 0, 'Хозен-задира to Player');
INSERT INTO `creature_text` VALUES (55470, 3, 0, 'Вторжение шнуропсов! Небесный огонь!', 12, 0, 100, 407, 0, 0, 62314, 27108000, 27108000, 0, 'Хозен-задира to Player');
INSERT INTO `creature_text` VALUES (55470, 3, 0, 'Это земля хозенов! Нет чужакам!', 12, 0, 100, 407, 0, 0, 62115, 1130000, 1130000, 0, 'Хозен-задира to Player');
INSERT INTO `creature_text` VALUES (55476, 3, 0, 'Вперед, в наступление!', 14, 0, 100, 0, 0, 26533, 56276, 51531, 51531, 0, 'Лорд Афрасастраз to Player');
INSERT INTO `creature_text` VALUES (55476, 3, 0, 'Драконий союз, в атаку!', 14, 0, 100, 0, 0, 26269, 56269, 41765, 41765, 0, 'Лорд Афрасастраз to Player');
INSERT INTO `creature_text` VALUES (55476, 3, 0, 'Нужно снять осаду. Драконий союз, не сдавать позиции!', 14, 0, 100, 0, 0, 26534, 56277, 28000, 28000, 0, 'Лорд Афрасастраз');
INSERT INTO `creature_text` VALUES (55476, 3, 0, 'Сумеречный Молот отступает! Храм наш, укрепляйте позиции.', 14, 0, 100, 0, 0, 26533, 56312, 52047, 52047, 0, 'Лорд Афрасастраз to Player');
