INSERT INTO `creature_text` VALUES (55483, 1, 0, 'Du nicht nehmen Möhre! Du stattdessen Rübe nehmen!', 12, 0, 100, 0, 0, 0, 54877, 0, 0, 0, 'Rundlicher Shed-Ling to Schildkröte');
