INSERT INTO `creature_text` VALUES (55490, 3, 0, 'Они посадили адмирала в клетку – словно дикого зверя! Пожалуйста, спаси и его!', 12, 0, 100, 2, 0, 0, 53956, 122000, 122000, 0, 'Пленный боец Альянса to Player');
INSERT INTO `creature_text` VALUES (55490, 3, 0, 'Спасибо, |3-6($c). До лагеря я доберусь.', 12, 0, 100, 2, 0, 0, 53955, 116000, 116000, 0, 'Пленный боец Альянса to Player');
