INSERT INTO `creature_text` VALUES (55500, 1, 0, 'Мои клинки жаждут крови.', 12, 0, 100, 0, 0, 26057, 97641, 0, 0, 0, 'Иллидан Ярость Бури to Демон Легиона');
INSERT INTO `creature_text` VALUES (55500, 3, 0, 'Закройте последний портал и покончим с этим.', 12, 0, 100, 0, 0, 26072, 54736, 96294, 96294, 0, 'Иллидан Ярость Бури to Демон Легиона');
INSERT INTO `creature_text` VALUES (55500, 4, 0, 'Твоя жалкая магия не сравнится с моей.', 12, 0, 100, 0, 0, 26053, 54775, 3858, 3858, 104906, 'Иллидан Ярость Бури to Перот\'арн');
