INSERT INTO `creature_text` VALUES (55524, 0, 0, 'I will handle the demons. Elune, guide my arrows!', 12, 0, 100, 6, 0, 25997, 54227, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (55524, 1, 0, 'Light of Elune, save me!', 14, 0, 100, 6, 0, 25998, 54229, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (55524, 2, 0, 'Tyrande did not cope with the onslaught of the enemy! Use the blessing of Elune to throw demons!', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (55524, 3, 0, 'I will hold them back for now!', 14, 0, 100, 6, 0, 25999, 54231, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (55524, 4, 0, 'There are too many of them!', 14, 0, 100, 22, 0, 26004, 100209, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (55524, 5, 0, 'No! Illidan!', 12, 0, 100, 0, 0, 26001, 54265, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (55524, 6, 0, 'Malfurion, he has done it! The portal is collapsing!', 12, 0, 100, 6, 0, 26003, 54270, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (55532, 0, 0, 'You are not the sole wielder of Sargeras\' power, Mannoroth! Behold!', 14, 0, 100, 0, 0, 26096, 54261, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (55532, 1, 0, 'I will be the savior of our people! I WILL FULFILL MY DESTINY!', 14, 0, 100, 0, 0, 26098, 54264, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (55532, 2, 0, '[Demonic] Revos ill ok mordanas archim maz naztheros! Archim xi ante maz-re mishun te nagas!', 14, 0, 100, 0, 0, 26097, 54266, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (55539, 3, 0, '%s громко всхрапывает и продолжает спать.', 16, 0, 100, 0, 0, 0, 54991, 556000, 556000, 0, 'Угоу to Player');
INSERT INTO `creature_text` VALUES (55549, 3, 0, 'Смотрите! Кажется, культ Сумеречного Молота привел древних богов и их порчу к Храму Драконьего Покоя. Держитесь крепче!', 15, 0, 100, 0, 0, 0, 54036, 33887, 33887, 0, 'Страж жизни to Player');
INSERT INTO `creature_text` VALUES (55564, 3, 0, 'Ануб\'aрак пал, братья! Восстаньте же из глубин и возрадуйтесь, ибо вновь обрели мы то, что по праву принадлежит нам!', 14, 0, 100, 0, 0, 14069, 54151, 0, 0, 0, 'Завоеватель А\'зак to Player');
INSERT INTO `creature_text` VALUES (55570, 3, 0, 'I will not be able to deal with him alone ...', 12, 0, 100, 0, 0, 26495, 54107, 85454, 85454, 0, 'Малфурион Ярость Бури to Player');
INSERT INTO `creature_text` VALUES (55595, 3, 0, 'Дафэн! Что случилось? Почему ты прячешься?', 12, 0, 100, 1, 0, 27403, 54362, 1000, 1000, 0, 'Аиса Воспевающая Облака to Player');
INSERT INTO `creature_text` VALUES (55597, 3, 0, 'На Лек благодарит |3-3($p) и возвращается в водоем.', 16, 0, 100, 0, 0, 0, 56967, 52000, 52000, 0, 'На Лек');
INSERT INTO `creature_text` VALUES (55611, 3, 0, 'Ви-ви-ви!', 12, 0, 100, 5, 0, 0, 54128, 124000, 124000, 0, 'Стелька to Малыш Лу');
INSERT INTO `creature_text` VALUES (55622, 0, 0, 'Шлепаный шнуропс! Я тебе банцы в хрюзию затолкать!', 12, 0, 100, 0, 0, 0, 54161, 0, 0, 0, 'Дук Укем');
INSERT INTO `creature_text` VALUES (55624, 3, 0, 'Вот-вот случится Великий Раскол – Источник Вечности будет разрушен, и мир навсегда изменится.', 12, 0, 100, 0, 0, 25961, 55271, 248, 248, 0, 'Ноздорму to Player');
INSERT INTO `creature_text` VALUES (55672, 0, 0, 'Вот мы и на месте. Мне понадобится твоя помощь в приготовлениях.', 12, 0, 100, 1, 0, 27805, 56485, 0, 0, 0, 'Мастер Шан Си');
INSERT INTO `creature_text` VALUES (55689, 0, 0, 'Heh! Even with the aspect of time on your side, you stumble foolishly into a trap.', 14, 0, 100, 0, 0, 26223, 56614, 0, 0, 0, 'hagara adds 1');
INSERT INTO `creature_text` VALUES (55689, 1, 0, 'Don\'t preen just yet little pups, we\'ll cleanse this world of your kind.', 14, 0, 100, 0, 0, 26224, 56615, 0, 0, 0, 'hagara adds 2');
INSERT INTO `creature_text` VALUES (55689, 2, 0, 'You won\'t leave this place alive.', 14, 0, 100, 0, 0, 26225, 56616, 0, 0, 0, 'hagara adds 3');
INSERT INTO `creature_text` VALUES (55689, 3, 0, 'Not one of you will live to see the final Cataclysm! Finish them!', 14, 0, 100, 0, 0, 26226, 56617, 0, 0, 0, 'hagara adds 4');
INSERT INTO `creature_text` VALUES (55689, 4, 0, 'You cross the Stormbinder! I\'ll slaughter you all.', 14, 0, 100, 0, 0, 26227, 55647, 0, 0, 0, 'hagara aggro');
INSERT INTO `creature_text` VALUES (55689, 5, 0, 'What are you doing?', 14, 0, 100, 0, 0, 26228, 104081, 0, 0, 0, 'hagara circuit 01');
INSERT INTO `creature_text` VALUES (55689, 5, 1, 'You\'re toying with death.', 14, 0, 100, 0, 0, 26229, 55669, 0, 0, 0, 'hagara circuit 02');
INSERT INTO `creature_text` VALUES (55689, 5, 2, 'You think you can play with my lightning?', 14, 0, 100, 0, 0, 26230, 55670, 0, 0, 0, 'hagara circuit 03');
INSERT INTO `creature_text` VALUES (55689, 5, 3, 'No! More... lightning...', 14, 0, 100, 0, 0, 26232, 55672, 0, 0, 0, 'hagara circuit 05');
INSERT INTO `creature_text` VALUES (55689, 5, 4, 'Enough of your games! You won\'t live to do it again.', 14, 0, 100, 0, 0, 26233, 55673, 0, 0, 0, 'hagara circuit 06');
INSERT INTO `creature_text` VALUES (55689, 5, 5, 'I\'ll finish you now pups!', 14, 0, 100, 0, 0, 26234, 55674, 0, 0, 0, 'hagara circuit 07');
