INSERT INTO `creature_text` VALUES (55689, 6, 1, 'You\'ll PAY for that.', 14, 0, 100, 0, 0, 26236, 89187, 0, 0, 0, 'hagara crystal dead 02');
INSERT INTO `creature_text` VALUES (55689, 6, 2, 'Enough!', 14, 0, 100, 0, 0, 26237, 108414, 0, 0, 0, 'hagara crystal dead 03');
