INSERT INTO `creature_text` VALUES (55689, 6, 4, 'Impudent pup!', 14, 0, 100, 0, 0, 26240, 55666, 0, 0, 0, 'hagara crystal dead 06');
INSERT INTO `creature_text` VALUES (55689, 6, 5, 'The one remaining is still enough to finish you.', 14, 0, 100, 0, 0, 26241, 55667, 0, 0, 0, 'hagara crystal dead 07');
INSERT INTO `creature_text` VALUES (55689, 7, 0, 'Cowards! You pack of weakling...dogs...', 14, 0, 100, 0, 0, 26243, 55715, 0, 0, 0, 'hagara dead');
INSERT INTO `creature_text` VALUES (55689, 8, 0, 'You face more than my axes, this close.', 14, 0, 100, 0, 0, 26244, 55658, 0, 0, 0, 'hagara frostray 01');
