INSERT INTO `creature_text` VALUES (55689, 9, 1, 'Die beneath the ice.', 14, 0, 100, 0, 0, 26248, 55655, 0, 0, 0, 'hagara glacier 02');
INSERT INTO `creature_text` VALUES (55689, 10, 0, 'Stay, pup.', 14, 0, 100, 0, 0, 26249, 55652, 0, 0, 0, 'hagara icetomb 01');
INSERT INTO `creature_text` VALUES (55689, 10, 1, 'Hold still.', 14, 0, 100, 0, 0, 26250, 90600, 0, 0, 0, 'hagara icetomb 02');
INSERT INTO `creature_text` VALUES (55689, 11, 0, 'Swagger all you like; you pups don\'t stand a chance. Flee now, while you can.', 14, 0, 100, 0, 0, 26251, 55646, 0, 0, 0, 'hagara intro 01');
