INSERT INTO `creature_text` VALUES (55689, 12, 1, 'Thunder and lightning dance at my call!', 14, 0, 100, 0, 0, 26253, 55657, 0, 0, 0, 'hagara lightning 02');
INSERT INTO `creature_text` VALUES (55689, 13, 0, 'Feh!', 14, 0, 100, 0, 0, 26254, 55649, 0, 0, 0, 'hagara slay 02');
INSERT INTO `creature_text` VALUES (55689, 13, 1, 'You should have run, dog!', 14, 0, 100, 0, 0, 26255, 55648, 0, 0, 0, 'hagara slay 01');
INSERT INTO `creature_text` VALUES (55689, 13, 2, 'Down, pup.', 14, 0, 100, 0, 0, 26256, 55650, 0, 0, 0, 'hagara slay 03');
INSERT INTO `creature_text` VALUES (55689, 13, 3, 'A waste of my time.', 14, 0, 100, 0, 0, 26257, 55651, 0, 0, 0, 'hagara slay 04');
INSERT INTO `creature_text` VALUES (55694, 0, 0, 'Эй! Деревня Утреннего Бриза – это сюда.', 12, 0, 100, 0, 0, 27294, 54196, 0, 0, 0, 'Цзи Огненная Лапа');
INSERT INTO `creature_text` VALUES (55744, 0, 0, 'Стой!', 12, 0, 100, 1, 0, 27401, 141400, 0, 0, 0, 'Аиса Воспевающая Облака');
INSERT INTO `creature_text` VALUES (55744, 1, 0, 'Когда ветер стихнет, нам нужно добежать до перехода между залами!', 12, 0, 100, 1, 0, 27401, 54356, 0, 0, 0, 'Аиса Воспевающая Облака');
INSERT INTO `creature_text` VALUES (55744, 2, 0, 'Дождись следующего удачного момента. Встретимся на той стороне зала.', 12, 0, 100, 1, 0, 27402, 54357, 0, 0, 0, 'Аиса Воспевающая Облака');
INSERT INTO `creature_text` VALUES (55779, 0, 0, 'Show yourself!', 12, 0, 100, 0, 0, 25877, 69421, 0, 0, 0, 'Thrall');
INSERT INTO `creature_text` VALUES (55779, 1, 0, 'We\'re surrounded - dispatch the ascendant while I keep the ambushers at bay.', 12, 0, 100, 0, 0, 25878, 54177, 0, 0, 0, 'Thrall');
INSERT INTO `creature_text` VALUES (55779, 2, 0, 'You\'ve almost got him! Og\'nor ka Lok\'tar - Now we finish this!', 14, 0, 100, 0, 0, 25881, 53963, 0, 0, 0, 'Thrall');
INSERT INTO `creature_text` VALUES (55779, 3, 0, 'We\'ve been discovered. I know you\'re tired, but we cannot keep the Aspects waiting!', 12, 0, 100, 0, 0, 25882, 54294, 0, 0, 0, 'Thrall');
INSERT INTO `creature_text` VALUES (55837, 3, 0, 'Магический меч Варо\'тена падает на землю!', 41, 0, 100, 0, 0, 0, 54255, 74, 74, 104819, 'Магический меч Варо\'тена to Капитан Варо\'тен');
INSERT INTO `creature_text` VALUES (55869, 0, 0, 'I hate adventurers.', 14, 0, 0, 0, 0, 25777, 56322, 0, 0, 0, 'VO_BH_ALIZABAL_AGGRO_01');
INSERT INTO `creature_text` VALUES (55869, 1, 0, 'I hate... every one of you...', 14, 0, 0, 0, 0, 25778, 56323, 0, 0, 0, 'VO_BH_ALIZABAL_DEATH_01');
INSERT INTO `creature_text` VALUES (55869, 2, 0, 'How I HATE this place. My captors may be long-dead, but don\'t think I won\'t take it all out on you miserable treasure-hunters.', 14, 0, 0, 0, 0, 25779, 56331, 0, 0, 0, 'VO_BH_ALIZABAL_INTRO_01');
INSERT INTO `creature_text` VALUES (55869, 3, 0, 'I hate incompetent raiders.', 14, 0, 0, 0, 0, 25780, 56317, 0, 0, 0, 'VO_BH_ALIZABAL_RESET_01');
INSERT INTO `creature_text` VALUES (55869, 4, 0, 'I still hate you.', 14, 0, 0, 0, 0, 25781, 56318, 0, 0, 0, 'VO_BH_ALIZABAL_SLAY_01');
INSERT INTO `creature_text` VALUES (55869, 4, 1, 'Do you hate me? Good.', 14, 0, 0, 0, 0, 25782, 56319, 0, 0, 0, 'VO_BH_ALIZABAL_SLAY_02');
INSERT INTO `creature_text` VALUES (55869, 4, 2, 'I hate mercy.', 14, 0, 0, 0, 0, 25783, 56320, 0, 0, 0, 'VO_BH_ALIZABAL_SLAY_03');
INSERT INTO `creature_text` VALUES (55869, 4, 3, 'I didn\'t hate that.', 14, 0, 0, 0, 0, 25784, 56321, 0, 0, 0, 'VO_BH_ALIZABAL_SLAY_04');
INSERT INTO `creature_text` VALUES (55869, 5, 0, 'Alizabal skewers $N to the ground.', 14, 0, 0, 0, 0, 25785, 56367, 0, 0, 0, 'VO_BH_ALIZABAL_SPELL_01');
INSERT INTO `creature_text` VALUES (55869, 6, 0, 'I hate you all!', 14, 0, 0, 0, 0, 25790, 65835, 0, 0, 0, 'VO_BH_ALIZABAL_SPELL_06');
INSERT INTO `creature_text` VALUES (55869, 6, 1, 'I hate standing still.', 14, 0, 0, 0, 0, 25791, 56325, 0, 0, 0, 'VO_BH_ALIZABAL_SPELL_07');
INSERT INTO `creature_text` VALUES (55870, 0, 0, 'Little hand says it\'s time to rock and roll! Everyone into the hole, we\'ve got a dragon to kill!', 14, 0, 100, 0, 0, 26289, 0, 0, 0, 0, 'Sky Captain Swayze blackhorn victory');
INSERT INTO `creature_text` VALUES (55870, 1, 0, 'The plates! He\'s coming apart! Tear up the plates and we\'ve got a shot at bringing him down!', 14, 0, 100, 0, 0, 26290, 56618, 0, 0, 0, 'Sky Captain Swayze deathwing back 01');
INSERT INTO `creature_text` VALUES (55870, 2, 0, 'Don\'t worry about me. Stop the Destroyer! Enshu-falah-nah, dorini.', 14, 0, 100, 0, 0, 26291, 56619, 0, 0, 0, 'Sky Captain Swayze');
INSERT INTO `creature_text` VALUES (55870, 3, 0, 'All ahead full. Everything depends on our speed! We can\'t let the Destroyer get away.', 14, 0, 100, 0, 0, 26292, 55430, 0, 0, 0, 'Sky Captain Swayze event 01');
INSERT INTO `creature_text` VALUES (55870, 4, 0, 'All hands to battle stations; get those monsters away from the ship!', 14, 0, 100, 0, 0, 26293, 55431, 0, 0, 0, 'Sky Captain Swayze event 02');
INSERT INTO `creature_text` VALUES (55870, 5, 0, 'Concentrate everything on the armored drake!', 14, 0, 100, 0, 0, 26295, 55433, 0, 0, 0, 'Sky Captain Swayze event 03');
INSERT INTO `creature_text` VALUES (55870, 6, 0, 'An Enemy Sapper has breached the Engine Room!', 14, 0, 100, 0, 0, 26296, 55434, 0, 0, 0, 'Sky Captain Swayze event 04');
INSERT INTO `creature_text` VALUES (55870, 7, 0, 'The Skyfire can\'t take much more of this!', 14, 0, 100, 0, 0, 26297, 55435, 0, 0, 0, 'Sky Captain Swayze event 05');
INSERT INTO `creature_text` VALUES (55870, 8, 0, 'Were going down, abandon the ship!', 14, 0, 100, 0, 0, 26298, 55436, 0, 0, 0, 'Sky Captain Swayze event 06');
INSERT INTO `creature_text` VALUES (55870, 9, 0, 'Welcome aboard the Skyfire. You ready to chase down the end of the world?', 14, 0, 100, 0, 0, 26302, 56304, 0, 0, 0, 'Sky Captain Swayze pre deathwing back 01');
INSERT INTO `creature_text` VALUES (55870, 10, 0, 'Our engines are damaged! We\'re sitting ducks up here!', 14, 0, 100, 0, 0, 26303, 56079, 0, 0, 0, 'Sky Captain Swayze pre deathwing back 02');
INSERT INTO `creature_text` VALUES (55873, 0, 0, 'А-А-АЙ!', 12, 0, 100, 0, 0, 0, 103761, 0, 0, 0, 'Косозуб-смутьян to Player');
INSERT INTO `creature_text` VALUES (55873, 1, 0, 'Морковь не брать! Репу брать!', 12, 0, 100, 53, 0, 0, 54877, 0, 0, 128602, 'Косозуб-смутьян to Player');
INSERT INTO `creature_text` VALUES (55873, 3, 0, 'Вонючий хозинь!', 12, 0, 100, 25, 0, 0, 67312, 1229000, 1229000, 0, 'Косозуб-смутьян');
INSERT INTO `creature_text` VALUES (55873, 3, 0, 'Гнать тупых хозиней с нашей фермы. Вон!', 12, 0, 100, 25, 0, 0, 67315, 1304000, 1304000, 0, 'Косозуб-смутьян');
INSERT INTO `creature_text` VALUES (55873, 3, 0, 'Ешь репу, Муно!', 12, 0, 100, 25, 0, 0, 67314, 1159000, 1159000, 0, 'Косозуб-смутьян');
INSERT INTO `creature_text` VALUES (55873, 3, 0, 'Тут жить гну-сини!', 12, 0, 100, 0, 0, 0, 54876, 116000, 116000, 0, 'Косозуб-смутьян to Player');
INSERT INTO `creature_text` VALUES (55873, 3, 0, 'У тебя тупое лицо, Муно!', 12, 0, 100, 25, 0, 0, 67313, 64000, 64000, 0, 'Косозуб-смутьян');
INSERT INTO `creature_text` VALUES (55943, 3, 0, 'Никогда в жизни не встречал их. Впрочем, пандарены не бывают плохими.', 12, 0, 100, 0, 0, 28090, 59554, 19000, 19000, 0, 'Вэй Бледная Ярость to Player');
INSERT INTO `creature_text` VALUES (55971, 0, 0, 'It\'s good to see you again, Alexstrasza. I have been busy in my absence.', 14, 0, 100, 0, 0, 26360, 56080, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (55971, 1, 0, 'Twisting your pitiful whelps into mindless abominations, bent only to my will. It was a very...painful process.', 14, 0, 100, 0, 0, 26361, 56646, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (55971, 2, 0, 'Mere whelps, experiments, a means to a greater end. You will see what the research of my clutch has yielding.', 14, 0, 100, 0, 0, 26362, 55322, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (55971, 3, 0, 'Nefarian? Onyxia? Sinestra? They were nothing! Now you face my ultimate creation!', 14, 0, 100, 0, 0, 26363, 56647, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (55971, 4, 0, 'The Hour of Twilight is nigh. The sun sets on your pitiful, mortal existence.', 14, 0, 100, 0, 0, 26364, 56648, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56010, 0, 0, 'L\'ordre doit tomber $N mais attention voici les Princes en héroïque !!', 14, 0, 100, 4, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56013, 0, 0, 'Вместе вы сделали большое дело – спасли Шэнь-Цзынь Су. Мы гордимся вами.', 12, 0, 100, 1, 0, 27784, 60632, 0, 0, 0, 'Дух мастера Шан Си');
INSERT INTO `creature_text` VALUES (56013, 1, 0, 'Но ваши приключения только начинаются. Эти новые союзники пришли из мира, которому требуется наша помощь.', 12, 0, 100, 1, 0, 27785, 60633, 0, 0, 0, 'Дух мастера Шан Си');
INSERT INTO `creature_text` VALUES (56013, 2, 0, 'А ты, Цзи? Ты присоединишься к Орде, когда они отправятся домой?', 12, 0, 100, 6, 0, 27786, 60629, 0, 0, 0, 'Дух мастера Шан Си');
INSERT INTO `creature_text` VALUES (56013, 3, 0, 'А ты? Каков твой выбор?', 12, 0, 100, 6, 0, 27787, 60631, 0, 0, 0, 'Дух мастера Шан Си');
INSERT INTO `creature_text` VALUES (56041, 0, 0, '|Cffff0000ОСТОРОЖНО:|r В лесу появилась мать стаи Лунная волчица!', 41, 0, 100, 0, 0, 0, 74595, 0, 0, 0, 'Рона Зеленозуб to Player');
INSERT INTO `creature_text` VALUES (56041, 3, 0, 'Лунная волчица, мать стаи Острова Новолуния, убита!', 41, 0, 100, 0, 0, 0, 74596, 366000, 366000, 0, 'Рона Зеленозуб to Player');
INSERT INTO `creature_text` VALUES (56056, 0, 0, 'C\'est parti $N et ton groupe, que le combat commence !', 14, 0, 100, 4, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56060, 0, 0, 'Si vous êtes bloqué dans gilnéas alors que vous avez déjà fait toutes les quettes, venez me voir !!!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56062, 0, 0, 'Вдова Зеленая Лапа – не ведьма. Она просто делает очень правдоподобные статуи из нефрита.', 12, 0, 100, 0, 0, 0, 55754, 0, 0, 0, 'Тцзу Железное Брюхо to Ань Воздушный Мех');
INSERT INTO `creature_text` VALUES (56099, 0, 0, 'Excellent work. The fire of my heart glows with a brilliant purity unmatched; every spark of it I will channel into the Dragon Soul.', 14, 0, 100, 0, 0, 26497, 56773, 0, 0, 0, 'Alexstrasza - dwbattle 1');
INSERT INTO `creature_text` VALUES (56099, 1, 0, 'I will cleanse whatever corruption I can; my fire will not harm you.', 14, 0, 100, 0, 0, 26498, 56772, 0, 0, 0, 'Alexstrasza - dwbattle 2');
INSERT INTO `creature_text` VALUES (56099, 2, 0, 'He\'s completely mad!', 14, 0, 100, 0, 0, 26499, 56774, 0, 0, 0, 'Alexstrasza - dwbattle 3');
INSERT INTO `creature_text` VALUES (56099, 3, 0, 'No! Such power! Deathwing\'s summoning of the final Cataclysm will destroy all life on Azeroth. Quickly, we must interrupt him!', 14, 0, 100, 0, 0, 26500, 55773, 0, 0, 0, 'Alexstrasza - dwbattle 4');
INSERT INTO `creature_text` VALUES (56099, 4, 0, 'You have accomplished the impossible, succeeded where all Azeroth feared you would fail.', 14, 0, 100, 0, 0, 26501, 55769, 0, 0, 0, 'Alexstrasza - dwvicrory 1');
INSERT INTO `creature_text` VALUES (56099, 5, 0, 'Before you is the Cache of the Aspects. Take from it what you will, for you have more than earned the right.', 14, 0, 100, 0, 0, 26502, 56771, 0, 0, 0, 'Alexstrasza - dwvicrory 2');
INSERT INTO `creature_text` VALUES (56100, 0, 0, ' We are one step closer. The unknowable, transcendent power of the Emerald Dream I now give unto the Dragon Soul.', 14, 0, 100, 0, 0, 26141, 56775, 0, 0, 0, 'Ysera - dwbattle 1');
INSERT INTO `creature_text` VALUES (56100, 1, 0, 'I will bring you closer to the Emerald Dream. Seek safety there when the fight becomes too intense.', 14, 0, 100, 0, 0, 26142, 56776, 0, 0, 0, 'Ysera - dwbattle 2');
INSERT INTO `creature_text` VALUES (56100, 2, 0, 'Such rage I have never seen...', 14, 0, 100, 0, 0, 26143, 56777, 0, 0, 0, 'Ysera - dwbattle 3');
INSERT INTO `creature_text` VALUES (56100, 3, 0, 'Deathwing is conjuring the final Cataclysm; even the Emerald Dream trembles. If we are to stop the spell, we must attack him together.', 14, 0, 100, 0, 0, 26144, 56781, 0, 0, 0, 'Ysera - dwbattle 4');
INSERT INTO `creature_text` VALUES (56101, 0, 0, 'Well done! I will realign the flow of mana and fill the Dragon Soul with all my arcane might.', 14, 0, 100, 0, 0, 26258, 56751, 0, 0, 0, 'Kalecgos - dwbattle 1');
INSERT INTO `creature_text` VALUES (56101, 1, 0, 'I will charge you with arcane energy to blast your foes.', 14, 0, 100, 0, 0, 26259, 56752, 0, 0, 0, 'Kalecgos - dwbattle 2');
INSERT INTO `creature_text` VALUES (56101, 2, 0, 'Is he... coming apart?!', 14, 0, 100, 0, 0, 26260, 56753, 0, 0, 0, 'Kalecgos - dwbattle 3');
INSERT INTO `creature_text` VALUES (56101, 3, 0, 'The Destroyer is gathering all his might for a blow that will split the world. Attack him, now! We must stop the final Cataclysm!', 14, 0, 100, 0, 0, 26261, 55776, 0, 0, 0, 'Kalecgos - dwbattle 4');
INSERT INTO `creature_text` VALUES (56102, 0, 0, 'It is time. I will expend everything to bind every thread here, now, around the Dragon Soul. What comes to pass will NEVER be undone.', 14, 0, 100, 0, 0, 25948, 56769, 0, 0, 0, 'Nozdormu - dwbattle 1');
INSERT INTO `creature_text` VALUES (56102, 1, 0, 'I will slow the Destroyer\'s attacks when I can.', 14, 0, 100, 0, 0, 25949, 55786, 0, 0, 0, 'Nozdormu - dwbattle 2');
INSERT INTO `creature_text` VALUES (56102, 2, 0, 'Press the attack, heroes!', 14, 0, 100, 0, 0, 25950, 56770, 0, 0, 0, 'Nozdormu - dwbattle 3');
INSERT INTO `creature_text` VALUES (56102, 3, 0, 'Hurry, heroes. In mere moments Deathwing\'s Cataclysm will complete what he begun and end the world. Join me in the attack, now!', 14, 0, 100, 0, 0, 25951, 55774, 0, 0, 0, 'Nozdormu - dwbattle 4');
INSERT INTO `creature_text` VALUES (56102, 4, 0, '...although, you may want to hurry.', 14, 0, 100, 0, 0, 25952, 55770, 0, 0, 0, 'Nozdormu - dwvictory 1');
INSERT INTO `creature_text` VALUES (56103, 0, 0, 'It is done; at last the Destroyer has met his end. Now, we can begin to heal our world.', 14, 0, 100, 0, 0, 25901, 55768, 0, 0, 0, 'Thrall - Maelstorm 01');
INSERT INTO `creature_text` VALUES (56103, 1, 0, 'Only you can give us our opening!', 14, 0, 100, 0, 0, 25902, 55844, 0, 0, 0, 'Thrall - Maelstorm 02');
INSERT INTO `creature_text` VALUES (56103, 2, 0, 'Avenge the fallen! Continue the fight!', 14, 0, 100, 0, 0, 25903, 56738, 0, 0, 0, 'Thrall - Maelstorm Slay 01');
INSERT INTO `creature_text` VALUES (56110, 3, 0, 'Итак, друзья... Как мы найдем тайного мастера?', 12, 0, 100, 6, 0, 31908, 60483, 392000, 392000, 0, 'Сяо to Player');
INSERT INTO `creature_text` VALUES (56110, 3, 0, 'Линь, вот как ты думаешь, почему его называют \"тайным мастером\"?', 12, 0, 100, 1, 0, 31909, 56384, 409000, 409000, 0, 'Сяо to Player');
INSERT INTO `creature_text` VALUES (56110, 3, 0, 'Отлично. А я, как самый старший и самый толстый, буду ждать вас здесь.', 12, 0, 100, 273, 0, 31910, 56394, 456000, 456000, 0, 'Сяо to Player');
INSERT INTO `creature_text` VALUES (56110, 3, 0, 'Угу.', 12, 0, 100, 273, 0, 32054, 82258, 74000, 74000, 0, 'Сяо to Ли Ли');
INSERT INTO `creature_text` VALUES (56115, 3, 0, 'Выпей вот это.', 12, 0, 100, 0, 0, 0, 57157, 681000, 681000, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` VALUES (56115, 3, 0, 'Да... Бесстрашный... Звучи хорошо.', 12, 0, 100, 0, 0, 0, 57172, 1180000, 1180000, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` VALUES (56115, 3, 0, 'Какого бортеля?', 12, 0, 100, 0, 0, 0, 57169, 1157000, 1157000, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` VALUES (56115, 3, 0, 'Никто не грустить, если носи такая маска! На ком бы проверить?', 12, 0, 100, 0, 0, 0, 57162, 1131000, 1131000, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` VALUES (56115, 3, 0, 'Ой, бесхозенечно! Это отсюда убирай! Кен-Кен слишком много умный, не умирать!', 12, 0, 100, 0, 0, 0, 57170, 1161000, 1161000, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` VALUES (56115, 3, 0, 'Так, теперь надо проверяй на кого-то...', 12, 0, 100, 0, 0, 0, 57149, 660000, 660000, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` VALUES (56115, 3, 0, 'Ты, калама, подойти! У Кен-Кена тебе есть штука-дрюка!', 12, 0, 100, 0, 0, 0, 57150, 669000, 669000, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` VALUES (56115, 3, 0, 'Ха-ха-ха! Получайся! Кен-Кен так и знавай, что сработать! Ты же лучше ощущай?', 14, 0, 100, 0, 0, 0, 57158, 698000, 698000, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` VALUES (56115, 3, 0, 'Хорошо! Дружок, сюда ходи. За тот раз извинять! Надевай надо это вот.', 12, 0, 100, 0, 0, 0, 57163, 1144000, 1144000, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` VALUES (56115, 3, 0, 'Эй, $p, ходи сюда! Кен-Кен придумать, как делай негрустный панда!', 14, 0, 100, 0, 0, 0, 57441, 327000, 327000, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` VALUES (56115, 3, 0, 'Это... это убирай?', 12, 0, 100, 0, 0, 0, 57171, 1165000, 1165000, 0, 'Кен-Кен to Player');
INSERT INTO `creature_text` VALUES (56133, 0, 0, 'Very well, $n. I will need your help making this beer.', 12, 0, 100, 0, 0, 0, 56407, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56133, 1, 0, 'When I yell out the names of ingredients, you throw them in the pot...', 12, 0, 100, 0, 0, 0, 56408, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56133, 2, 0, 'I am kidding!', 14, 0, 100, 0, 0, 0, 56409, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56133, 3, 0, 'I am the best brewer across three continents! I think I can handle this part.', 12, 0, 100, 0, 0, 0, 56410, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56133, 4, 0, 'If you wouldn\'t mind waiting, $n... this might take a long time.', 12, 0, 100, 0, 0, 0, 56412, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56138, 0, 0, 'Мы уже можем идти? Ты допил свое пиво?', 12, 0, 100, 1, 0, 30379, 55070, 0, 0, 0, 'Ли Ли');
INSERT INTO `creature_text` VALUES (56138, 0, 0, 'Невероятно! Он бросил меня с этим странным грязным типом!', 12, 0, 100, 0, 0, 30420, 55485, 0, 0, 0, 'Ли Ли to Player');
INSERT INTO `creature_text` VALUES (56138, 0, 0, 'Неудивительно, что это заняло столько времени. Сложно что-то исследовать, сидя на одном месте.', 12, 0, 100, 0, 0, 0, 54995, 0, 0, 0, 'Ли Ли to Ли Ли');
INSERT INTO `creature_text` VALUES (56138, 3, 0, 'Подожди, ты сказал... Буйных Портеров?', 12, 0, 100, 6, 0, 30418, 55475, 13000, 13000, 0, 'Ли Ли to Player');
INSERT INTO `creature_text` VALUES (56138, 3, 0, 'Потому что рядом с этими двумя ты кажешься себе худым.', 12, 0, 100, 25, 0, 30382, 54998, 7000, 7000, 0, 'Ли Ли to Ли Ли');
INSERT INTO `creature_text` VALUES (56159, 0, 0, 'Идем, дитя. Нам осталось совершить вместе последнее путешествие, и твое обучение будет завершено.', 12, 0, 100, 1, 0, 27797, 55520, 0, 0, 0, 'Мастер Шан Си');
INSERT INTO `creature_text` VALUES (56159, 1, 0, 'Путь Старейшин приведет нас в Лес Посохов – священную землю, куда дозволено ступать лишь достойным.', 12, 0, 100, 1, 0, 27800, 55521, 0, 0, 0, 'Мастер Шан Си');
INSERT INTO `creature_text` VALUES (56159, 2, 0, 'Есть много способов проявить себя. Я же прошу от тебя простейшего. Я должен знать, что ты без колебаний будешь сражаться за наш народ и что с тобой наши люди будут в безопасности.', 12, 0, 100, 1, 0, 27801, 55522, 0, 0, 0, 'Мастер Шан Си');
INSERT INTO `creature_text` VALUES (56159, 3, 0, 'Одержи победу над Стражем Старейшин, и мы сможем пройти.', 12, 0, 100, 1, 0, 27802, 55523, 0, 0, 0, 'Мастер Шан Си');
INSERT INTO `creature_text` VALUES (56159, 4, 0, 'Теперь ты гораздо сильнее, чем раньше. Это хорошо. Вскоре тебе понадобится эта сила.', 12, 0, 100, 1, 0, 27803, 56484, 0, 0, 0, 'Мастер Шан Си');
INSERT INTO `creature_text` VALUES (56165, 3, 0, 'Проводящий кристалл перегружен!', 41, 0, 100, 0, 0, 26228, 54780, 14000, 14000, 105487, 'Хрустальный громоотвод to Укрощенный элементаль молний');
INSERT INTO `creature_text` VALUES (56167, 0, 0, '|TInterface\\Icons\\ability_warrior_bloodnova.blp:20|t %s ранено, из него показываются |cFFFE9A2Eраскаленные щупальца|r!', 41, 0, 100, 0, 0, 26142, 54846, 0, 0, 0, 'Громадное щупальце to 0');
INSERT INTO `creature_text` VALUES (56168, 0, 0, '|TInterface\\Icons\\ability_warrior_bloodnova.blp:20|t %s ранено, из него показываются |cFFFE9A2Eраскаленные щупальца|r!', 41, 0, 100, 0, 0, 0, 54846, 0, 0, 0, 'Крыло to 0');
INSERT INTO `creature_text` VALUES (56173, 0, 0, 'You have done NOTHING. I will tear your world APART.', 14, 0, 100, 0, 0, 26527, 55860, 0, 0, 0, 'Madness of Deathwing - aggro');
INSERT INTO `creature_text` VALUES (56173, 1, 0, 'I AM DEATHWING, THE DESTROYER, THE END OF ALL THINGS, INEVITABLE, INDOMITABLE; I AM THE CATACLYSM!', 14, 0, 100, 0, 0, 26353, 55785, 0, 0, 0, 'Madness of Deathwing - phase 2');
INSERT INTO `creature_text` VALUES (56173, 2, 0, 'There is no shelter from my fury.', 14, 0, 100, 0, 0, 26354, 55779, 0, 0, 0, 'Madness of Deathwing - arrow 1');
INSERT INTO `creature_text` VALUES (56173, 2, 1, 'Your armor means nothing, your faith even less.', 14, 0, 100, 0, 0, 26355, 55780, 0, 0, 0, 'Madness of Deathwing - arrow 2');
INSERT INTO `creature_text` VALUES (56173, 2, 2, 'The sea will swallow your smoldering remains.', 14, 0, 100, 0, 0, 26356, 55781, 0, 0, 0, 'Madness of Deathwing - arrow 3');
INSERT INTO `creature_text` VALUES (56173, 3, 0, 'Mutated Corruption begins to impale the Aspect! Stop it!', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Madness of Deathwing - Impale Aspect');
INSERT INTO `creature_text` VALUES (56173, 4, 0, '|TInterfaceIconsability_deathwing_assualtaspects.blp:20|t %s attacks |cFFFF0000|Hspell:107018|h[Alexstrasza]|h|r!', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Madness of Deathwing - Attack Ysera');
INSERT INTO `creature_text` VALUES (56173, 5, 0, '|TInterfaceIconsability_deathwing_assualtaspects.blp:20|t %s attacks |cFFFFFF00|Hspell:107018|h[Nozdormu]|h|r!', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Madness of Deathwing - Attack Ysera');
INSERT INTO `creature_text` VALUES (56173, 6, 0, '|TInterfaceIconsability_deathwing_assualtaspects.blp:20|t %s attacks |cFF04FC32|Hspell:107018|h[Ysera]|h|r!', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Madness of Deathwing - Attack Ysera');
INSERT INTO `creature_text` VALUES (56173, 7, 0, '|TInterfaceIconsability_deathwing_assualtaspects.blp:20|t %s attacks |cFF5858FA|Hspell:107018|h[Kalecgos]|h|r!', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Madness of Deathwing - Attack Ysera');
INSERT INTO `creature_text` VALUES (56184, 1, 0, 'Отдавай все овощи!', 12, 0, 100, 15, 0, 0, 54873, 0, 0, 0, 'Косозуб-вредитель to Player');
INSERT INTO `creature_text` VALUES (56184, 3, 0, 'А-А-АЙ!', 12, 0, 100, 53, 0, 0, 103761, 46000, 46000, 0, 'Косозуб-вредитель to Player');
INSERT INTO `creature_text` VALUES (56184, 4, 0, '%s is trying to escape in a panic!', 16, 0, 100, 53, 0, 0, 1150, 7000, 7000, 118597, 'Косозуб-вредитель');
INSERT INTO `creature_text` VALUES (56184, 4, 0, 'Теперь это земля гну-синей, уходи!', 12, 0, 100, 53, 0, 0, 60661, 7000, 7000, 118597, 'Косозуб-вредитель to Player');
INSERT INTO `creature_text` VALUES (56184, 4, 0, 'Уходи!', 12, 0, 100, 53, 0, 0, 101910, 6000, 6000, 118597, 'Косозуб-вредитель to Player');
INSERT INTO `creature_text` VALUES (56185, 3, 0, 'У нас лучше овощи! Ваши хуже!', 12, 0, 100, 0, 0, 0, 66821, 45000, 45000, 0, 'Косозуб-трудяга to Player');
INSERT INTO `creature_text` VALUES (56185, 4, 0, '%s is trying to escape in a panic!', 16, 0, 100, 0, 0, 0, 1150, 2000, 2000, 0, 'Косозуб-трудяга');
INSERT INTO `creature_text` VALUES (56194, 3, 0, '%s убегает!', 16, 0, 100, 0, 0, 0, 89989, 2000, 2000, 0, 'Паучонок');
INSERT INTO `creature_text` VALUES (56204, 0, 0, 'Greetings, Chen, Li Li. You may stay, but I do need any of your ale.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56206, 0, 0, 'You would champion the cause of such a lowly street rat? Very well, best me in combat and I shall consider giving you my blessing.', 12, 0, 100, 1, 0, 0, 54781, 0, 0, 0, 'EvStartText text');
INSERT INTO `creature_text` VALUES (56206, 1, 0, 'Enough! You win... champion. Tell that upstart that he has my blessing.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'EvEndText text');
INSERT INTO `creature_text` VALUES (56208, 3, 0, 'Не трогай моих овец!', 14, 0, 100, 0, 0, 3580, 55079, 2234, 2234, 0, 'Пастух Франсис to Волк-налетчик');
INSERT INTO `creature_text` VALUES (56209, 0, 0, ' At last! A challenge! Let us test your mettle, \"champion\".', 14, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'EvStartText text');
INSERT INTO `creature_text` VALUES (56209, 1, 0, ' That was a fine battle! The likes of which I have not enjoyed in many decades. Go and give the boy my blessing.', 14, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'EvEndText text');
INSERT INTO `creature_text` VALUES (56210, 0, 0, 'A Wanderbrew wants to marry an Goldendraft?! Preposterous.', 12, 0, 100, 1, 0, 0, 54788, 0, 0, 0, 'EvStartText text');
INSERT INTO `creature_text` VALUES (56210, 1, 0, 'I yield! The boy chose his champion well. Give him my blessing and make sure he does not squander it.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'EvEndText text');
INSERT INTO `creature_text` VALUES (56236, 3, 0, 'I ... I owe you one.', 12, 0, 100, 0, 0, 0, 55139, 112000, 112000, 117987, 'Раненый матрос to Player');
INSERT INTO `creature_text` VALUES (56236, 3, 0, 'Благодарю за спасение.', 12, 0, 100, 2, 0, 0, 131568, 14000, 14000, 0, 'Раненый матрос to Player');
INSERT INTO `creature_text` VALUES (56236, 3, 0, 'Мне удалось выжить только благодаря тебе... Я этого не забуду.', 12, 0, 100, 18, 0, 0, 64342, 12000, 12000, 0, 'Раненый матрос to Player');
INSERT INTO `creature_text` VALUES (56236, 3, 0, 'Смерть была совсем близко! Спасибо, $gнезнакомец:незнакомка;.', 12, 0, 100, 18, 0, 0, 64344, 11000, 11000, 117987, 'Раненый матрос to Player');
INSERT INTO `creature_text` VALUES (56248, 1, 0, 'Вас разыскивают Очи Перот\'арна.', 41, 0, 100, 0, 0, 0, 55984, 0, 0, 105353, 'Hunting Summon Stalker to Перот\'арн');
INSERT INTO `creature_text` VALUES (56267, 3, 0, 'Похоже, увиденное укрепило твою решимость. Значит, я сделал все, что было в моих силах.', 12, 0, 100, 4, 0, 30062, 64690, 182000, 182000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (56268, 3, 0, 'Искра должна находиться в центре!', 42, 0, 100, 0, 0, 30055, 64687, 2000, 2000, 88885, 'Медитирующий to Player');
INSERT INTO `creature_text` VALUES (56269, 3, 0, 'А как насчет... ЭТОГО?!', 12, 0, 100, 274, 0, 30059, 64130, 73000, 73000, 0, 'Хранитель истории Чо to Хранитель истории Чо');
INSERT INTO `creature_text` VALUES (56269, 3, 0, 'А теперь старайся сохранять нужный настрой. Ты можешь сосредоточиться или расслабиться, но что бы ни случилось – не теряй внутреннего равновесия.', 12, 0, 100, 0, 0, 30055, 64127, 3000, 3000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (56269, 3, 0, 'Ах, извини. Но ведь я тебя не напугал, да? Мы, пандарены, ценим спокойствие в любой ситуации.', 12, 0, 100, 1, 0, 30058, 64129, 48000, 48000, 86515, 'Хранитель истории Чо to Хранитель истории Чо');
INSERT INTO `creature_text` VALUES (56269, 3, 0, 'Негативные эмоции: злость, сомнение – имеют странную силу здесь, в Пандарии. Скоро ты это поймешь.', 12, 0, 100, 6, 0, 0, 64146, 91000, 91000, 0, 'Хранитель истории Чо to Хранитель истории Чо');
INSERT INTO `creature_text` VALUES (56269, 3, 0, 'О, хорошо. Похоже, тебя нелегко отвлечь. Мы, пандарены, живем настоящим. Порой даже во вред себе.', 12, 0, 100, 1, 0, 30057, 64128, 18000, 18000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (56269, 3, 0, 'Ты только посмотри! Просто образец спокойствия. Я так и знал, что в тебе это есть.', 12, 0, 100, 1, 0, 30060, 64131, 81000, 81000, 0, 'Хранитель истории Чо to Хранитель истории Чо');
INSERT INTO `creature_text` VALUES (56269, 3, 0, 'Чувствуешь абсолютный покой в душе? Напиток начал действовать.', 12, 0, 100, 5, 0, 30061, 64148, 108000, 108000, 0, 'Хранитель истории Чо to Хранитель истории Чо');
INSERT INTO `creature_text` VALUES (56310, 0, 0, 'Ты... не уйдешь!', 14, 0, 100, 0, 0, 0, 55203, 0, 0, 0, 'Туманный ужас to Player');
INSERT INTO `creature_text` VALUES (56312, 0, 0, 'Хорошо вам провести время у Грязной Кружки! И спасибо еще раз!', 14, 0, 100, 2, 0, 31200, 55372, 0, 0, 0, 'Шан Громовая Пята to Player');
INSERT INTO `creature_text` VALUES (56333, 1, 0, 'Let\'s move out, friend! There\'s no time to lose.', 12, 0, 100, 3, 0, 0, 54902, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (56333, 2, 0, 'Corastrasza is understandably upset. Under her care was the one black dragon egg that we believe was free of the taint of Deathwing\'s corruption...', 12, 0, 100, 3, 0, 0, 54903, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (56333, 3, 0, '...and it was stolen away from us by thieves in the night!', 12, 0, 100, 3, 0, 0, 54904, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (56333, 4, 0, 'You\'ve found for us our only lead. The rogues from Ravenholdt are behind this.', 12, 0, 100, 3, 0, 0, 54905, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (56333, 5, 0, 'Ravenholdt? It just doesn\'t make sense! A mongrel band of petty thieves. How did they even KNOW about the egg?', 12, 0, 100, 3, 0, 0, 54906, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (56333, 6, 0, 'We\'ll get to the bottom of this. Someone with your skills is exactly what we need.', 12, 0, 100, 3, 0, 0, 54907, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (56333, 7, 0, 'What\'s the expression? \"To catch a thief,\" they say?', 12, 0, 100, 3, 0, 0, 54908, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (56333, 8, 0, 'Look at this devastation. These foothills have never recovered from the campaigns of the second and third wars.', 12, 0, 100, 3, 0, 0, 54909, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (56333, 9, 0, 'Ruins and debris litter the landscape... and I consider the thieves and assassins of Ravenholdt to be part of that debris.', 12, 0, 100, 3, 0, 0, 54910, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (56333, 10, 0, 'Without a state... without a cause... what drives them, do you wonder?', 12, 0, 100, 3, 0, 0, 54911, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (56333, 11, 0, 'And what possessed them to strike out against the red dragonflight? Madness. We must learn more!', 12, 0, 100, 3, 0, 0, 54912, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (56333, 12, 0, 'We\'re almost to Ravenholdt Manor.', 12, 0, 100, 3, 0, 0, 54913, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (56333, 13, 0, 'If they have the egg, it\'s too dangerous to risk an all-out assault. You\'ll need to sneak in there, find out what they\'re up to... And retrieve the egg at any cost.', 12, 0, 100, 3, 0, 0, 54914, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (56333, 14, 0, 'Quiet now! We\'ll form up a plan within this cave...', 12, 0, 100, 3, 0, 0, 54915, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (56343, 0, 0, 'I am called Chen, and this is my niece, Li Li.', 12, 0, 100, 0, 0, 0, 57066, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56343, 1, 0, 'May we stay at your farmhouse for a while? We do not have much in the way of money, but I will gladly share some of my famous ale with you.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56343, 2, 0, 'Thank you! We will not trouble you for long.', 12, 0, 100, 0, 0, 0, 57069, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56344, 0, 0, 'Ну наконец-то!', 12, 0, 100, 5, 0, 30373, 57065, 0, 0, 0, 'Ли Ли to Player');
INSERT INTO `creature_text` VALUES (56344, 1, 0, 'Еще одна остановка? Дядя Чэнь, мы же не прошли и десяти метров!', 12, 0, 100, 5, 0, 30374, 57071, 0, 0, 0, 'Ли Ли to Чэнь Буйный Портер');
INSERT INTO `creature_text` VALUES (56345, 3, 0, 'Наберитесь терпения, госпожа Манн. Эти чужаки не перестают меня удивлять.', 12, 0, 100, 1, 0, 30091, 61071, 40000, 40000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (56345, 3, 0, 'Смотрите, кто здесь! Госпожа Манн, я хочу вас кое с кем познакомить.', 12, 0, 100, 0, 0, 30088, 61030, 15000, 15000, 0, 'Хранитель истории Чо to Player');
INSERT INTO `creature_text` VALUES (56346, 0, 0, 'Honey-Bear! I mean... it is about time.', 12, 0, 100, 0, 0, 0, 61221, 0, 0, 0, 'Штейгер Манн to Player');
INSERT INTO `creature_text` VALUES (56346, 1, 0, 'Don\'t you \"snow lily\" me! I was- WE- were worried sick about you.', 12, 0, 100, 0, 0, 0, 61222, 0, 0, 0, 'Штейгер Манн to Player');
INSERT INTO `creature_text` VALUES (56346, 2, 0, 'And the delivery is not for ME, Hao! The honorable Lorewalker has been waitin- oh...', 12, 0, 100, 0, 0, 0, 61225, 0, 0, 0, 'Штейгер Манн to Player');
INSERT INTO `creature_text` VALUES (56346, 3, 0, 'Я бы с удовольствием помогла тебе, хранитель истории, но сейчас мне трудно!', 12, 0, 100, 0, 0, 29533, 61028, 6000, 6000, 0, 'Штейгер Манн to Player');
INSERT INTO `creature_text` VALUES (56346, 3, 0, 'Я все понимаю. Но работа над Змеиным Сердцем идет с запаздыванием, и у нас нет лишнего нефрита.', 12, 0, 100, 1, 0, 29534, 61066, 34000, 34000, 0, 'Штейгер Манн to Player');
INSERT INTO `creature_text` VALUES (56347, 3, 0, 'Сюда!', 12, 0, 100, 0, 0, 32863, 143887, 10000, 10000, 0, 'Хао Манн to Player');
INSERT INTO `creature_text` VALUES (56348, 3, 0, 'Я буду и дальше искать нефрит. Если хочешь, обойди местных и предложи им свою помощь.', 12, 0, 100, 0, 0, 0, 63569, 78000, 78000, 0, 'Тоя to Player');
INSERT INTO `creature_text` VALUES (56349, 3, 0, 'Мое!', 12, 0, 100, 0, 0, 0, 61341, 55000, 55000, 0, 'Злобный дух to Злобный дух');
INSERT INTO `creature_text` VALUES (56349, 3, 0, 'У-у-у!', 12, 0, 100, 0, 0, 0, 131861, 54000, 54000, 0, 'Злобный дух');
INSERT INTO `creature_text` VALUES (56349, 3, 0, 'Хи-хи!', 12, 0, 100, 0, 0, 0, 111861, 64000, 64000, 105871, 'Злобный дух');
INSERT INTO `creature_text` VALUES (56398, 1, 0, 'Thank you. Let us begin.', 12, 0, 100, 0, 0, 0, 58129, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56398, 2, 0, 'Ancient spirits of water.', 12, 0, 100, 0, 0, 0, 55045, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56398, 3, 0, 'Hear my echo through the silent deeps.', 12, 0, 100, 0, 0, 0, 55046, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56398, 4, 0, 'Awaken from the great waters beyond.', 12, 0, 100, 0, 0, 0, 55047, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56398, 5, 0, 'Aid your children in this time of need!', 12, 0, 100, 0, 0, 0, 55048, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56398, 6, 0, 'A great darkness? Could he mean that we must trust these strangers... these... Alliance... after all?', 12, 0, 100, 0, 0, 0, 55049, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56416, 0, 0, 'Хвала Шэнь-Цзынь Су, что ты здесь. Твоя помощь мне очень пригодится.', 12, 0, 100, 0, 0, 27384, 64848, 0, 0, 0, 'Аиса Воспевающая Облака');
INSERT INTO `creature_text` VALUES (56416, 1, 0, 'Отходи в сторону, когда он поднимет оружие. Он еще опаснее, чем кажется.', 12, 0, 100, 0, 0, 27385, 64849, 0, 0, 0, 'Аиса Воспевающая Облака');
INSERT INTO `creature_text` VALUES (56416, 2, 0, 'Он призывает помощников! Я отвлеку его, пока ты расправляешься с ними.', 12, 0, 100, 0, 0, 27386, 64850, 0, 0, 0, 'Аиса Воспевающая Облака');
INSERT INTO `creature_text` VALUES (56416, 3, 0, 'Мы изматываем его. Еще немного!', 12, 0, 100, 0, 0, 27387, 64852, 0, 0, 0, 'Аиса Воспевающая Облака');
INSERT INTO `creature_text` VALUES (56416, 4, 0, 'Не знаю, что бы я делала без тебя. Твоя помощь очень кстати.', 12, 0, 100, 0, 0, 0, 64853, 0, 0, 0, 'Аиса Воспевающая Облака');
INSERT INTO `creature_text` VALUES (56418, 0, 0, 'Оставайся поблизости и защищай лекарей! От этого зависят наши жизни!', 14, 0, 100, 0, 0, 27287, 66062, 0, 0, 0, 'Цзи Огненная Лапа');
INSERT INTO `creature_text` VALUES (56418, 0, 1, 'Если лекари погибнут, Шэнь-Цзынь Су умрет, и все мы вместе с ним. Защищай их!', 14, 0, 100, 0, 0, 27288, 66063, 0, 0, 0, 'Цзи Огненная Лапа');
INSERT INTO `creature_text` VALUES (56418, 0, 2, 'Найди лекарей и помоги им в бою. Они должны заниматься раной.', 14, 0, 100, 0, 0, 27285, 66061, 0, 0, 0, 'Цзи Огненная Лапа');
INSERT INTO `creature_text` VALUES (56427, 0, 0, 'You won\'t get near the Master. Dragonriders, attack!', 14, 0, 100, 0, 0, 26210, 55443, 0, 0, 0, 'Warmaster Blackhorn aggro');
INSERT INTO `creature_text` VALUES (56427, 1, 0, 'Well...done, heh. But I wonder if you\'re good enough...to best him.', 14, 0, 100, 0, 0, 26211, 55444, 0, 0, 0, 'Warmaster Blackhorn death');
INSERT INTO `creature_text` VALUES (56427, 2, 0, 'Looks like I\'m doing this myself. Good!', 14, 0, 100, 0, 0, 26212, 55456, 0, 0, 0, 'Warmaster Blackhorn event 01');
INSERT INTO `creature_text` VALUES (56427, 3, 0, 'We\'re flying a little too close. It\'s been a good fight, but I\'m ending it, now.', 14, 0, 100, 0, 0, 26213, 55457, 0, 0, 0, 'Warmaster Blackhorn event 02');
INSERT INTO `creature_text` VALUES (56427, 4, 0, 'Hah! I was hoping you\'d make it this far. You\'d best be ready for a real fight.', 14, 0, 100, 0, 0, 26214, 55442, 0, 0, 0, 'Warmaster Blackhorn intro');
INSERT INTO `creature_text` VALUES (56427, 5, 0, 'Mess with the bull....', 14, 0, 100, 0, 0, 26215, 55446, 0, 0, 0, 'Warmaster Blackhorn slay 01');
INSERT INTO `creature_text` VALUES (56427, 5, 1, 'Warmaster Blackhorn laughs.', 16, 0, 100, 0, 0, 26216, 0, 0, 0, 0, 'Warmaster Blackhorn slay 02');
INSERT INTO `creature_text` VALUES (56427, 5, 2, 'Down you go!', 14, 0, 100, 0, 0, 26217, 55447, 0, 0, 0, 'Warmaster Blackhorn slay 03');
INSERT INTO `creature_text` VALUES (56427, 5, 3, 'Get up! Oh... weakling!', 14, 0, 100, 0, 0, 26218, 55449, 0, 0, 0, 'Warmaster Blackhorn slay 04');
INSERT INTO `creature_text` VALUES (56427, 6, 0, 'Goriona! Give them hell!', 14, 0, 100, 0, 0, 26219, 55453, 0, 0, 0, 'Warmaster Blackhorn spell 01');
INSERT INTO `creature_text` VALUES (56427, 7, 0, 'Warmaster Blackhorn roars happily.', 16, 0, 100, 0, 0, 26220, 0, 0, 0, 0, 'Warmaster Blackhorn spell 02');
INSERT INTO `creature_text` VALUES (56427, 8, 0, 'How\'s THIS?', 14, 0, 100, 0, 0, 26221, 55454, 0, 0, 0, 'Warmaster Blackhorn spell 03');
INSERT INTO `creature_text` VALUES (56427, 8, 1, 'Come on!', 14, 0, 100, 0, 0, 26222, 55455, 0, 0, 0, 'Warmaster Blackhorn spell 04');
INSERT INTO `creature_text` VALUES (56427, 9, 0, 'A drake swoops down to drop a Twilight Sapper onto the deck!', 41, 0, 100, 0, 0, 0, 56264, 0, 0, 0, 'Warmaster Blackhorn sapper yell');
INSERT INTO `creature_text` VALUES (56439, 0, 0, 'Die or surrender. You cannot defeat me.', 14, 0, 100, 0, 0, 29633, 62908, 0, 0, 0, 'VO_TJS_DOUBT_AGGRO');
INSERT INTO `creature_text` VALUES (56439, 1, 0, 'You cannot escape me. I am... in... every... breath...', 14, 0, 100, 0, 0, 29634, 62912, 0, 0, 0, 'VO_TJS_DOUBT_DEATH');
INSERT INTO `creature_text` VALUES (56439, 2, 0, 'See how effortlessly you become unraveled.', 14, 0, 100, 0, 0, 29635, 62913, 0, 0, 0, 'VO_TJS_DOUBT_FIGMENT_01');
INSERT INTO `creature_text` VALUES (56439, 3, 0, 'Succumb to the darkness inside your soul.', 14, 0, 100, 0, 0, 29636, 62914, 0, 0, 0, 'VO_TJS_DOUBT_FIGMENT_02');
INSERT INTO `creature_text` VALUES (56439, 4, 0, 'All is lost.', 14, 0, 100, 0, 0, 29637, 62909, 0, 0, 0, 'VO_TJS_DOUBT_RESET');
INSERT INTO `creature_text` VALUES (56439, 5, 0, 'Abandon all hope.', 14, 0, 100, 0, 0, 29638, 62910, 0, 0, 0, 'VO_TJS_DOUBT_SLAY_01');
INSERT INTO `creature_text` VALUES (56439, 6, 0, 'Embrace your despair.', 14, 0, 100, 0, 0, 29639, 62911, 0, 0, 0, 'VO_TJS_DOUBT_SLAY_02');
INSERT INTO `creature_text` VALUES (56440, 3, 0, 'Вы, гну-сини, воняете как тухлая репа! Хуже дукера!', 12, 0, 100, 25, 0, 0, 67317, 4989000, 4989000, 0, 'Батрак Муно');
INSERT INTO `creature_text` VALUES (56440, 3, 0, 'Неплохо, Мисс Фанни! Отличный удар!', 12, 0, 100, 0, 0, 0, 55066, 916000, 916000, 0, 'Батрак Муно to Player');
INSERT INTO `creature_text` VALUES (56440, 3, 0, 'Ого! Неплохо стреляешь, Мисс Фанни!', 12, 0, 100, 0, 0, 0, 55064, 896000, 896000, 0, 'Батрак Муно to Player');
INSERT INTO `creature_text` VALUES (56440, 3, 0, 'Отлично, Мисс Фанни! Прекрасно двигаешься!', 12, 0, 100, 0, 0, 0, 55065, 907000, 907000, 0, 'Батрак Муно to Player');
INSERT INTO `creature_text` VALUES (56440, 3, 0, 'Полегче, Мисс Фанни. Ты бьешь слишком сильно... Арбузы взрываются!', 12, 0, 100, 0, 0, 0, 55062, 851000, 851000, 0, 'Батрак Муно to Player');
INSERT INTO `creature_text` VALUES (56440, 3, 0, 'Сейчас получишь лопатой по башке!', 12, 0, 100, 25, 0, 0, 67316, 2104000, 5054000, 0, 'Батрак Муно');
INSERT INTO `creature_text` VALUES (56440, 3, 0, 'Фанни, ты же сильная! В следующий раз постарайся как следует!', 12, 0, 100, 0, 0, 0, 55061, 845000, 845000, 0, 'Батрак Муно to Player');
INSERT INTO `creature_text` VALUES (56440, 3, 0, 'Ха-ха-ха! Даже полпути не прошел!', 12, 0, 100, 0, 0, 0, 55063, 864000, 864000, 0, 'Батрак Муно to Player');
INSERT INTO `creature_text` VALUES (56442, 3, 0, '\"Песнь Долины\" висит на стене. Возьми ее. Я слышу, меня зовут небеса. Спасибо тебе...', 12, 0, 100, 0, 0, 0, 58191, 39000, 39000, 0, 'Привидение Лина Стального Кулака to Player');
INSERT INTO `creature_text` VALUES (56442, 3, 0, 'I shudder at the very thought of it. Ancient evil. Enemy of Pandaria. I dare not pronounce his name.', 12, 0, 100, 0, 0, 0, 55132, 35000, 35000, 0, 'Привидение Лина Стального Кулака to Player');
INSERT INTO `creature_text` VALUES (56442, 3, 0, 'Даже после того как сгнила наша плоть, надежду высасывало из наших душ. Эта была бесконечная пытка!', 12, 0, 100, 0, 0, 0, 55131, 27000, 27000, 0, 'Привидение Лина Стального Кулака to Player');
INSERT INTO `creature_text` VALUES (56442, 3, 0, 'Затем пришла тьма. Она просачивалась из земли, медленно заражала... душила нас!', 12, 0, 100, 0, 0, 0, 55130, 21000, 21000, 0, 'Привидение Лина Стального Кулака to Player');
INSERT INTO `creature_text` VALUES (56442, 3, 0, 'Мы охраняли воды. В них заключена слишком большая сила – они не должны попасть в чужие руки.', 12, 0, 100, 0, 0, 0, 55129, 15000, 15000, 0, 'Привидение Лина Стального Кулака to Player');
INSERT INTO `creature_text` VALUES (56442, 3, 0, 'Наконец-то мы освободились и вышли из тьмы! О, прекрасный, благословенный свет!', 12, 0, 100, 0, 0, 0, 55127, 2000, 2000, 0, 'Привидение Лина Стального Кулака to Player');
INSERT INTO `creature_text` VALUES (56442, 3, 0, 'Тысячу жизней назад я принес сюда эти воды – дар Жемчужному Плавнику за подвиги, совершенные в незапамятные времена.', 12, 0, 100, 0, 0, 0, 55128, 10000, 10000, 0, 'Привидение Лина Стального Кулака to Player');
INSERT INTO `creature_text` VALUES (56443, 3, 0, 'ММ-М!!!', 12, 0, 100, 0, 0, 32864, 55211, 2000, 2000, 0, 'Хао Манн to Player');
INSERT INTO `creature_text` VALUES (56443, 3, 0, 'Уверен, ты прекрасно здесь справишься. Я буду снаружи.', 12, 0, 100, 1, 0, 32866, 55213, 11000, 11000, 0, 'Хао Манн to Player');
INSERT INTO `creature_text` VALUES (56443, 3, 0, 'Я же говорил, что у меня все под контролем!', 12, 0, 100, 0, 0, 32865, 55212, 6000, 6000, 0, 'Хао Манн to Player');
INSERT INTO `creature_text` VALUES (56444, 1, 0, 'В конце концов остается только тишина.', 12, 0, 100, 0, 0, 0, 55089, 0, 0, 0, 'Мучимый дух to Player');
INSERT INTO `creature_text` VALUES (56444, 1, 0, 'Достойное жертвоприношение для нашего господина.', 12, 0, 100, 0, 0, 0, 55085, 0, 0, 0, 'Мучимый дух to Player');
INSERT INTO `creature_text` VALUES (56444, 1, 0, 'Никто не смеет приближаться к этим водам!', 12, 0, 100, 0, 0, 0, 55088, 0, 0, 0, 'Мучимый дух to Player');
INSERT INTO `creature_text` VALUES (56444, 1, 0, 'Отчаяние...', 12, 0, 100, 0, 0, 0, 55083, 0, 0, 0, 'Мучимый дух to Player');
INSERT INTO `creature_text` VALUES (56444, 1, 0, 'Тьма ждет...', 12, 0, 100, 0, 0, 0, 55086, 0, 0, 0, 'Мучимый дух to Player');
INSERT INTO `creature_text` VALUES (56444, 3, 0, 'Живым здесь не место!', 12, 0, 100, 0, 0, 0, 55090, 262000, 262000, 0, 'Мучимый дух to Player');
INSERT INTO `creature_text` VALUES (56444, 3, 0, 'Порча поглотит тебя, как когда-то поглотила нас...', 12, 0, 100, 0, 0, 0, 55087, 59000, 59000, 0, 'Мучимый дух to Player');
INSERT INTO `creature_text` VALUES (56444, 3, 0, 'Ты не захватишь наши воды!', 12, 0, 100, 0, 0, 0, 55084, 44000, 44000, 0, 'Мучимый дух to Player');
INSERT INTO `creature_text` VALUES (56448, 0, 0, 'The waters whisper to me. Enemies approach.', 12, 0, 100, 0, 0, 28265, 55723, 0, 0, 0, 'Wise mari - Intro');
INSERT INTO `creature_text` VALUES (56448, 1, 0, 'You dare to disturb these waters? You will drown!', 12, 0, 100, 0, 0, 28257, 55118, 0, 0, 0, 'Wise mari - Aggro');
INSERT INTO `creature_text` VALUES (56448, 2, 0, 'Water reacts violently to your contact!', 41, 0, 100, 0, 0, 0, 59470, 0, 0, 0, 'Wise mari - Aggro Boss Emote');
INSERT INTO `creature_text` VALUES (56448, 3, 0, 'Living water, bring them death!', 12, 0, 33, 0, 0, 28261, 62960, 0, 0, 0, 'Wise Mari Call Water');
INSERT INTO `creature_text` VALUES (56448, 3, 1, 'Spring to life!', 12, 0, 100, 0, 0, 28259, 55606, 0, 0, 0, 'Wise Mari Call Water 2');
INSERT INTO `creature_text` VALUES (56448, 4, 0, 'Behold the POWER of torrential waters!', 12, 0, 100, 0, 0, 28266, 55605, 0, 0, 0, 'Wise Mari Call Phase 2');
INSERT INTO `creature_text` VALUES (56448, 5, 0, 'The darkness clears...', 12, 0, 100, 0, 0, 28262, 55719, 0, 0, 0, 'Wise Mari death 1');
INSERT INTO `creature_text` VALUES (56448, 5, 1, 'I peer into these waters and my reflection is empty...', 12, 0, 100, 0, 0, 28263, 55720, 0, 0, 0, 'Wise Mari death 2');
INSERT INTO `creature_text` VALUES (56448, 5, 2, 'Waters... claim me...', 12, 0, 100, 0, 0, 28264, 55721, 0, 0, 0, 'Wise Mari death 3');
INSERT INTO `creature_text` VALUES (56448, 6, 0, 'Your cause is hopeless!', 12, 0, 100, 0, 0, 28268, 62989, 0, 0, 0, 'Wise Mari death 1');
INSERT INTO `creature_text` VALUES (56448, 6, 1, 'As the waters foretold!', 12, 0, 100, 0, 0, 28267, 62988, 0, 0, 0, 'Wise Mari kill player 2');
INSERT INTO `creature_text` VALUES (56461, 3, 0, 'Быть может, этот дух что-то знает о стихотворении. Очистись, дух!', 12, 0, 100, 0, 0, 0, 55135, 126000, 126000, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (56461, 3, 0, 'Какая злая сила осквернила вас?', 12, 0, 100, 0, 0, 0, 55134, 160000, 160000, 0, 'Андуин Ринн to Привидение Лина Стального Кулака');
INSERT INTO `creature_text` VALUES (56461, 3, 0, 'Мне все больше хочется увидеть этот Вечноцветущий дол. Я должен там побывать.', 12, 0, 100, 0, 0, 0, 58192, 175000, 175000, 0, 'Андуин Ринн to 56442');
INSERT INTO `creature_text` VALUES (56461, 3, 0, 'Очистись от скверны, дух.', 12, 0, 100, 0, 0, 0, 55101, 61000, 107000, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (56461, 3, 0, 'Тени, прочь!', 12, 0, 100, 0, 0, 0, 62438, 52000, 212000, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (56461, 3, 0, 'Успокойся, дух.', 12, 0, 100, 0, 0, 0, 55103, 61000, 201000, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (56461, 3, 0, 'Что с вами произошло?', 12, 0, 100, 0, 0, 0, 55133, 136000, 136000, 0, 'Андуин Ринн to Привидение Лина Стального Кулака');
INSERT INTO `creature_text` VALUES (56462, 1, 0, 'А-А-АЙ!', 12, 0, 100, 53, 0, 0, 103761, 0, 0, 0, 'Косозуб-хулиган to Player');
INSERT INTO `creature_text` VALUES (56462, 3, 0, 'Атакуйте репу!', 14, 0, 100, 0, 0, 0, 55093, 236000, 236000, 0, 'Косозуб-хулиган to Generic Bunny - PRK');
INSERT INTO `creature_text` VALUES (56462, 3, 0, 'Моя лодка побеждает!', 12, 0, 100, 0, 0, 0, 55105, 94000, 94000, 0, 'Косозуб-хулиган to Generic Bunny - PRK');
INSERT INTO `creature_text` VALUES (56462, 3, 0, 'Пора умирать, репа!', 14, 0, 100, 0, 0, 0, 55092, 185000, 185000, 0, 'Косозуб-хулиган to Generic Bunny - PRK');
INSERT INTO `creature_text` VALUES (56462, 4, 0, '%s is trying to escape in a panic!', 16, 0, 100, 0, 0, 0, 1150, 22000, 22000, 129088, 'Косозуб-хулиган');
INSERT INTO `creature_text` VALUES (56462, 4, 0, 'Смотри, как медленно плывет твоя лодка.', 12, 0, 100, 0, 0, 0, 55111, 7000, 7000, 0, 'Косозуб-хулиган to Generic Bunny - PRK');
INSERT INTO `creature_text` VALUES (56463, 3, 0, 'Мы защитили воды от сил тьмы.', 12, 0, 100, 2, 0, 0, 55114, 1000, 1000, 69676, 'Освобожденный дух to Мучимый дух');
INSERT INTO `creature_text` VALUES (56463, 3, 0, 'Наконец-то я... обрету свободу...', 12, 0, 100, 2, 0, 0, 55104, 1000, 1000, 69676, 'Освобожденный дух to Мучимый дух');
INSERT INTO `creature_text` VALUES (56463, 3, 0, 'Не трать эту воду зря. Она бесценна...', 12, 0, 100, 2, 0, 0, 55109, 1000, 1000, 69676, 'Освобожденный дух to Мучимый дух');
INSERT INTO `creature_text` VALUES (56463, 3, 0, 'Свет... возвращается...', 12, 0, 100, 2, 0, 0, 55106, 1000, 1000, 69676, 'Освобожденный дух to Мучимый дух');
INSERT INTO `creature_text` VALUES (56463, 3, 0, 'Спасибо, $gнезнакомец:незнакомка;...', 12, 0, 100, 2, 0, 0, 55110, 2000, 2000, 0, 'Освобожденный дух to Мучимый дух');
INSERT INTO `creature_text` VALUES (56463, 3, 0, 'Тени исчезли... Порча снята.', 12, 0, 100, 2, 0, 0, 55112, 1000, 1000, 69676, 'Освобожденный дух to Мучимый дух');
INSERT INTO `creature_text` VALUES (56463, 3, 0, 'Теперь защищать воды Дола придется тебе...', 12, 0, 100, 2, 0, 0, 58186, 2000, 2000, 0, 'Освобожденный дух to Мучимый дух');
INSERT INTO `creature_text` VALUES (56463, 3, 0, 'Тьма поднялась из глубин. Она голодна...', 12, 0, 100, 2, 0, 0, 58187, 1000, 1000, 69676, 'Освобожденный дух to Мучимый дух');
INSERT INTO `creature_text` VALUES (56464, 0, 0, 'Ahh, get these things away from me!', 12, 0, 100, 0, 0, 0, 55144, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56464, 0, 1, 'Much obliged, stranger.', 12, 0, 100, 0, 0, 0, 55181, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56464, 0, 2, 'Oh, I was just resting... I rest sometimes... under rocks...', 12, 0, 100, 0, 0, 0, 55186, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56464, 0, 3, 'Woah... get away from me, crazy... spider-thing!', 12, 0, 100, 0, 0, 0, 55141, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56464, 0, 4, 'Oof, that\'s better... How\'s my hair?', 12, 0, 100, 0, 0, 0, 55182, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (56465, 3, 0, 'Пошли отсюда!', 12, 0, 100, 0, 0, 0, 55124, 282000, 282000, 0, 'Ана Громовая Пята');
INSERT INTO `creature_text` VALUES (56465, 3, 0, 'Прочь, сурки!', 12, 0, 100, 0, 0, 0, 55122, 244000, 356000, 0, 'Ана Громовая Пята');
INSERT INTO `creature_text` VALUES (56465, 3, 0, 'Прочь!', 12, 0, 100, 0, 0, 0, 135117, 228000, 228000, 0, 'Ана Громовая Пята');
INSERT INTO `creature_text` VALUES (56465, 3, 0, 'Сурки придут в ярость, когда увидят, что их норы засыпаны – но ты просто пинай их ногами, и они тебя не тронут.', 12, 0, 100, 1, 0, 0, 60484, 236000, 236000, 0, 'Ана Громовая Пята to Player');
INSERT INTO `creature_text` VALUES (56467, 0, 0, 'Hoh-hohhh! What a rush! I like you, $c. You keep things interesting.', 12, 0, 100, 0, 0, 0, 61219, 0, 0, 0, 'Hao Mann to player');
INSERT INTO `creature_text` VALUES (56467, 1, 0, 'Snow lily! We\'re back from the quarry! Got a special delivery just for you, my sweetness.', 12, 0, 100, 0, 0, 0, 61220, 0, 0, 0, 'Hao Mann to player');
INSERT INTO `creature_text` VALUES (56467, 2, 0, 'Sorry to keep you waiting, sugarfur. I know how you worry. Truth is, this stranger here saved my old hide. Go figure', 12, 0, 100, 0, 0, 0, 61223, 0, 0, 0, 'Hao Mann to player');
INSERT INTO `creature_text` VALUES (56471, 0, 0, '%s начинает пронзать Аспекта! Остановите его!', 41, 0, 100, 0, 0, 0, 56722, 0, 0, 0, 'Изменившееся оскверненное щупальце to 0');
INSERT INTO `creature_text` VALUES (56473, 0, 0, '|TInterface\\Icons\\inv_sword_24.blp:20|tЛетящий Снег использует |cFFFF0000|Hspell:106706|h[Стальной вихрь]|h|r!', 41, 0, 100, 0, 0, 0, 65524, 0, 0, 0, 'Летящий Снег to 0');
INSERT INTO `creature_text` VALUES (56485, 3, 0, 'I decided to find the Everblooming Dol. And now I have to study the healing properties of sacred sources.', 12, 0, 100, 0, 0, 27574, 55160, 6000, 6000, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (56485, 3, 0, 'Идем же.', 12, 0, 100, 0, 0, 27576, 55165, 35000, 35000, 0, 'Андуин Ринн to Салли \"Рассольный\" Маклири');
INSERT INTO `creature_text` VALUES (56485, 3, 0, 'Извини, но я не пойду. Думаю, ты понимаешь, почему.', 12, 0, 100, 0, 0, 27575, 55163, 23000, 23000, 0, 'Андуин Ринн to Салли \"Рассольный\" Маклири');
INSERT INTO `creature_text` VALUES (56485, 3, 0, 'Прости, но я пока не могу вернуться.', 12, 0, 100, 0, 0, 27573, 65151, 2000, 2000, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (56488, 3, 0, 'Идем, только тихо.', 12, 0, 100, 0, 0, 27842, 55162, 12000, 12000, 0, 'Салли \"Рассольный\" Маклири to Player');
INSERT INTO `creature_text` VALUES (56488, 3, 0, 'Маленький негодник! Не думал, что он на такое способен!', 12, 0, 100, 0, 0, 27846, 58214, 53000, 53000, 0, 'Салли \"Рассольный\" Маклири to Эмбер Кеарнен');
INSERT INTO `creature_text` VALUES (56488, 3, 0, 'Принц Андуин, мы пришли, чтобы доставить вас домой.', 12, 0, 100, 0, 0, 27841, 55161, 9000, 9000, 0, 'Салли \"Рассольный\" Маклири to Player');
