INSERT INTO `creature_text` VALUES (56541, 0, 0, 'Это ваша вина! Вы позволили ша пробудиться после стольких лет!', 14, 0, 100, 0, 0, 0, 55307, 0, 0, 0, 'Мастер Снежный Вихрь to Player');
INSERT INTO `creature_text` VALUES (56541, 1, 0, 'Если вы хотите исправить то зло, которое причинили этой земле, вам придется доказать, что вы на это способны. Здесь и сейчас.', 14, 0, 100, 0, 0, 0, 55308, 0, 0, 0, 'Мастер Снежный Вихрь to Player');
INSERT INTO `creature_text` VALUES (56541, 2, 0, 'Для начала вы сразитесь с новичками...', 14, 0, 100, 397, 0, 27632, 55309, 0, 0, 0, 'Мастер Снежный Вихрь to Player');
INSERT INTO `creature_text` VALUES (56541, 3, 0, 'Тебе нужно многому научиться, если ты хочешь защищать свой народ от Ша. Отойди в сторону и смотри!', 14, 0, 100, 397, 0, 0, 55311, 0, 0, 0, 'Мастер Снежный Вихрь to Новообращенный Шадо-Пан');
INSERT INTO `creature_text` VALUES (56541, 4, 0, 'Когда один падает без сил, его место занимает другой.', 14, 0, 100, 397, 0, 0, 55310, 0, 0, 0, 'Мастер Снежный Вихрь to Новообращенный Шадо-Пан');
INSERT INTO `creature_text` VALUES (56541, 6, 0, 'Вы победили новичков. Теперь вы сразитесь с двумя моими самыми опытными учениками.', 14, 0, 100, 0, 0, 0, 55312, 0, 0, 0, 'Мастер Снежный Вихрь to Новообращенный Шадо-Пан');
INSERT INTO `creature_text` VALUES (56541, 7, 0, 'Летящий Снег! Благоухающий Лотос! Сразитесь с ними!', 14, 0, 100, 22, 0, 27636, 55313, 0, 0, 0, 'Мастер Снежный Вихрь to Новообращенный Шадо-Пан');
INSERT INTO `creature_text` VALUES (56541, 8, 0, 'Вы победили моих лучших учеников. Думаю, вы сможете нам помочь...', 14, 0, 100, 21, 0, 27633, 55314, 0, 0, 0, 'Мастер Снежный Вихрь to Благоухающий Лотос');
INSERT INTO `creature_text` VALUES (56541, 9, 0, 'Однажды учитель сказал мне: \"Нельзя узнать кого-то по-настоящему, пока не вступишь с ним в поединок\".', 14, 0, 100, 0, 0, 27634, 55315, 0, 0, 0, 'Мастер Снежный Вихрь to Благоухающий Лотос');
INSERT INTO `creature_text` VALUES (56541, 10, 0, 'Подойдите, и мы сможем познакомиться.', 14, 0, 100, 0, 0, 27635, 59404, 0, 0, 0, 'Мастер Снежный Вихрь to Благоухающий Лотос');
INSERT INTO `creature_text` VALUES (56541, 11, 0, 'Что ж, посмотрим, так ли вы сильны.', 14, 0, 100, 0, 0, 27628, 55828, 0, 0, 0, 'Мастер Снежный Вихрь to 0');
INSERT INTO `creature_text` VALUES (56541, 12, 0, '|TInterface\\Icons\\inv_weapon_hand_30.blp:20|tМастер Снежный Вихрь применяет |cFFFF0000|Hspell:106853|h[неистовые кулаки]|h|r!', 41, 0, 100, 0, 0, 0, 65520, 0, 0, 0, 'Мастер Снежный Вихрь to 0');
INSERT INTO `creature_text` VALUES (56541, 13, 0, 'В бою нужно не только мастерски владеть оружием. Дух искусного бойца тоже должен быть силен. Я покажу вам, почему это важно.', 14, 0, 100, 0, 0, 27629, 55829, 0, 0, 0, 'Мастер Снежный Вихрь to 0');
INSERT INTO `creature_text` VALUES (56541, 14, 0, 'Мастер Снежный Вихрь исчезает в тенях и создает иллюзорные копии себя! Чтобы развеять иллюзию, нужно ударить ее.', 41, 0, 100, 14, 0, 0, 59472, 0, 0, 0, 'Мастер Снежный Вихрь to 0');
INSERT INTO `creature_text` VALUES (56541, 15, 0, 'В юности я едва мог нанести противнику удар. Но за годы тренировок я многому научился!', 14, 0, 100, 23, 0, 27630, 55830, 0, 0, 0, 'Мастер Снежный Вихрь to 0');
