INSERT INTO `creature_text` VALUES (56660, 3, 0, 'Заноза?.. Жаль, я не захватил с собой пинцет.', 12, 0, 100, 6, 0, 27300, 55574, 154000, 154000, 0, 'Цзи Огненная Лапа to Аиса Воспевающая Облака');
INSERT INTO `creature_text` VALUES (56660, 3, 0, 'И прилетели на нем вовсе не пандарены. У них нет меха.', 12, 0, 100, 1, 0, 27303, 55579, 195000, 195000, 0, 'Цзи Огненная Лапа to Аиса Воспевающая Облака');
INSERT INTO `creature_text` VALUES (56660, 3, 0, 'Почему такое огромное существо страдает из-за какой-то щепки?', 12, 0, 100, 6, 0, 27301, 55575, 160000, 160000, 0, 'Цзи Огненная Лапа to Аиса Воспевающая Облака');
INSERT INTO `creature_text` VALUES (56660, 3, 0, 'Ты только посмотри! Это что, корабль?!', 12, 0, 100, 5, 0, 27302, 55577, 181000, 181000, 0, 'Цзи Огненная Лапа to Аиса Воспевающая Облака');
INSERT INTO `creature_text` VALUES (56660, 3, 0, 'Я всегда почтителен! Ты меня обижаешь, Аиса.', 12, 0, 100, 6, 0, 27299, 55561, 31000, 31000, 0, 'Цзи Огненная Лапа to Аиса Воспевающая Облака');
INSERT INTO `creature_text` VALUES (56661, 3, 0, 'Да, корабль. Очень большой корабль. Заноза оказалась крупнее, чем я думала.', 12, 0, 100, 1, 0, 27439, 55578, 188000, 188000, 0, 'Аиса Воспевающая Облака to Воздушный шар Шан Си');
INSERT INTO `creature_text` VALUES (56661, 3, 0, 'Конечно, Шэнь-Цзынь Су! Но твой панцирь огромен, и я не знаю, где в нем может быть эта заноза.', 14, 0, 100, 1, 0, 27436, 55571, 119000, 119000, 0, 'Аиса Воспевающая Облака to Воздушный шар Шан Си');
INSERT INTO `creature_text` VALUES (56661, 3, 0, 'Кто-то врезался в наш остров. Но достать такую занозу будет сложнее, чем мы предполагали.', 12, 0, 100, 1, 0, 27440, 55581, 202000, 202000, 0, 'Аиса Воспевающая Облака to Воздушный шар Шан Си');
INSERT INTO `creature_text` VALUES (56661, 3, 0, 'Мы найдем ее и вытащим. Даем слово!', 14, 0, 100, 1, 0, 27437, 55573, 141000, 141000, 0, 'Аиса Воспевающая Облака to Воздушный шар Шан Си');
INSERT INTO `creature_text` VALUES (56661, 3, 0, 'Не заставляй нас краснеть за тебя.', 12, 0, 100, 1, 0, 27433, 55562, 36000, 36000, 0, 'Аиса Воспевающая Облака to Воздушный шар Шан Си');
INSERT INTO `creature_text` VALUES (56661, 3, 0, 'Расскажем обо всем старейшине Шаопаю. Он даст нам совет.', 12, 0, 100, 1, 0, 27441, 55582, 209000, 209000, 0, 'Аиса Воспевающая Облака to Воздушный шар Шан Си');
INSERT INTO `creature_text` VALUES (56661, 3, 0, 'Скоро мы это узнаем.', 12, 0, 100, 1, 0, 33104, 56701, 167000, 167000, 0, 'Аиса Воспевающая Облака to Воздушный шар Шан Си');
INSERT INTO `creature_text` VALUES (56661, 3, 0, 'Цзи, будь почтителен с Шэнь-Цзынь Су.', 12, 0, 100, 1, 0, 27432, 55560, 25000, 25000, 0, 'Аиса Воспевающая Облака to Воздушный шар Шан Си');
INSERT INTO `creature_text` VALUES (56661, 3, 0, 'Цзи... они же были в Лесу Посохов. Неужели ты не знаешь, где теперь мастер Шан?', 12, 0, 100, 1, 0, 27431, 55555, 8000, 8000, 0, 'Аиса Воспевающая Облака to Воздушный шар Шан Си');
INSERT INTO `creature_text` VALUES (56661, 3, 0, 'Что случилось с тобой, Шэнь-Цзынь Су? Что мы можем сделать?', 14, 0, 100, 6, 0, 27435, 55564, 57000, 57000, 0, 'Аиса Воспевающая Облака to Воздушный шар Шан Си');
INSERT INTO `creature_text` VALUES (56661, 3, 0, 'Шэнь-Цзынь Су, мы потомки Лю Лана. Мы почувствовали твою боль и хотим помочь.', 14, 0, 100, 1, 0, 33102, 55563, 49000, 49000, 0, 'Аиса Воспевающая Облака to Воздушный шар Шан Си');
INSERT INTO `creature_text` VALUES (56662, 0, 0, 'Мы готовы отправиться в любой момент, $n.', 12, 0, 100, 1, 0, 27428, 55549, 0, 0, 0, 'Аиса Воспевающая Облака');
INSERT INTO `creature_text` VALUES (56662, 1, 0, 'Забирайся.', 12, 0, 100, 1, 0, 27429, 55551, 0, 0, 0, 'Аиса Воспевающая Облака');
INSERT INTO `creature_text` VALUES (56662, 2, 0, 'Не слушай его. Нас ждут важные дела.', 12, 0, 100, 1, 0, 27430, 55553, 0, 0, 0, 'Аиса Воспевающая Облака');
INSERT INTO `creature_text` VALUES (56663, 3, 0, 'Слушай, если хочешь побродить по лесу, мы с Аисой подождем тебя здесь... Ну, ты понимаешь, да?', 12, 0, 100, 1, 0, 27296, 55552, 456000, 456000, 0, 'Цзи Огненная Лапа to Player');
INSERT INTO `creature_text` VALUES (56664, 0, 0, 'Not necessarily. I believe... yes... it just might work.', 12, 0, 100, 5, 0, 26547, 56664, 0, 0, 0, 'kalecgos open portal 1');
INSERT INTO `creature_text` VALUES (56664, 1, 0, 'The Focusing Iris within the Eye of Eternity may allow us to converge the magical matrix contained within the Dragon Soul.', 12, 0, 100, 5, 0, 26265, 56733, 0, 0, 0, 'kalecgos open portal 2');
INSERT INTO `creature_text` VALUES (56664, 2, 0, 'Then I will open the way into the Eye of Eternity. You must not fail, heroes. The future of Azeroth hinges on your actions.', 12, 0, 100, 1, 0, 26266, 56665, 0, 0, 0, 'kalecgos open portal 3');
INSERT INTO `creature_text` VALUES (56664, 3, 0, 'Excellent! We will begin the ritual at once!', 12, 0, 100, 5, 0, 26262, 56696, 0, 0, 0, 'kalecgos - event');
INSERT INTO `creature_text` VALUES (56664, 4, 0, 'Winds of the arcane be at their backs, and refresh them in this hour of darkness!', 14, 0, 100, 0, 0, 26267, 55327, 0, 0, 0, 'kalecgos - help');
INSERT INTO `creature_text` VALUES (56665, 3, 0, 'I feel the approach of Chaos ... My mind can not stand it!', 12, 0, 100, 0, 0, 26148, 55321, 279547, 279547, 0, 'Изера Пробудившаяся to Player');
INSERT INTO `creature_text` VALUES (56665, 3, 0, 'Хвала Титанам, они вернулись!', 12, 0, 100, 4, 0, 26145, 56694, 5781, 5781, 0, 'Изера Пробудившаяся to Изера Пробудившаяся');
INSERT INTO `creature_text` VALUES (56666, 0, 0, 'Not entirely unexpected.', 12, 0, 100, 0, 0, 25953, 56695, 0, 0, 0, 'nozdormu - event');
INSERT INTO `creature_text` VALUES (56666, 1, 0, 'The cycle of time brings an end to all things.', 14, 0, 100, 0, 0, 25954, 55328, 0, 0, 0, 'nozdormu - help');
INSERT INTO `creature_text` VALUES (56667, 0, 0, 'It\'s no use. The power of the Dragon Soul is too great. I cannot wield it safely. The raging forces within it maybe the doom of us all.', 12, 0, 100, 1, 0, 25904, 56084, 0, 0, 0, 'thrall open portal 1');
INSERT INTO `creature_text` VALUES (56667, 1, 0, 'Yes, I see. We can finally turn Deathwing\'s own power back against him.', 12, 0, 100, 1, 0, 25905, 56667, 0, 0, 0, 'thrall open portal 2');
INSERT INTO `creature_text` VALUES (56667, 2, 0, 'We have no choice. We must have faith in our allies.', 12, 0, 100, 1, 0, 25906, 56670, 0, 0, 0, 'thrall open portal 2');
INSERT INTO `creature_text` VALUES (56667, 3, 0, 'Heroes, this burden falls to you once again.', 12, 0, 100, 1, 0, 25898, 56703, 0, 0, 0, 'thrall - event 1');
INSERT INTO `creature_text` VALUES (56667, 4, 0, 'You must protect us from Deathwing\'s forces while we imbue the Dragon Soul with the power of the Aspects.', 12, 0, 100, 0, 0, 25899, 56723, 0, 0, 0, 'thrall - event 2');
INSERT INTO `creature_text` VALUES (56667, 5, 0, 'Speak to me when you are ready to begin.', 12, 0, 100, 0, 0, 25900, 72622, 0, 0, 0, 'thrall - event 3');
INSERT INTO `creature_text` VALUES (56667, 6, 0, 'Strength of the Earth, hear my call! Shield them in this dark hour, the last defenders of Azeroth!', 14, 0, 100, 0, 0, 25907, 55324, 0, 0, 0, 'thrall - start');
INSERT INTO `creature_text` VALUES (56667, 7, 0, 'Taretha... Cairne... Aggra... I will not fail you. I will not fail this world!', 12, 0, 100, 0, 0, 25908, 56531, 0, 0, 0, 'thrall - start');
INSERT INTO `creature_text` VALUES (56670, 1, 0, 'Где враг?', 12, 0, 100, 0, 0, 0, 55624, 0, 0, 0, 'Владыка вод - ученик');
INSERT INTO `creature_text` VALUES (56670, 3, 0, 'Учи меня, $n! Я обрушу на врагов всю силу воды!', 12, 0, 100, 0, 0, 0, 61241, 2000, 2000, 0, 'Владыка вод - ученик to Player');
INSERT INTO `creature_text` VALUES (56670, 4, 0, 'Кажется, теперь я понимаю.', 12, 0, 100, 0, 0, 0, 55613, 19000, 19000, 98901, 'Владыка вод - ученик to Player');
INSERT INTO `creature_text` VALUES (56670, 4, 0, 'Кажется, я уже понимаю, что к чему.', 12, 0, 100, 0, 0, 0, 55611, 13000, 13000, 79831, 'Владыка вод - ученик to Player');
INSERT INTO `creature_text` VALUES (56670, 4, 0, 'Племя одержит победу!', 12, 0, 100, 0, 0, 0, 55631, 18000, 18000, 21912, 'Владыка вод - ученик');
INSERT INTO `creature_text` VALUES (56670, 4, 0, 'Теперь я знаю все секреты воды – и смогу обратить их на благо жителей деревни.', 12, 0, 100, 0, 0, 0, 61237, 25000, 25000, 47292, 'Владыка вод - ученик to Player');
INSERT INTO `creature_text` VALUES (56672, 3, 0, 'Привет, $n! Прежде чем пойти в бой, я должен освоить навыки целителя.', 12, 0, 100, 0, 0, 0, 61238, 1000, 1000, 0, 'Ткач туманов - ученик to Player');
INSERT INTO `creature_text` VALUES (56672, 4, 0, 'Спасибо, $n. Я уже многому научился.', 12, 0, 100, 0, 0, 0, 61234, 31000, 31000, 106710, 'Ткач туманов - ученик to Player');
INSERT INTO `creature_text` VALUES (56674, 3, 0, 'Мои мечи остры, но мне не хватает навыков. $n, я хочу учиться.', 12, 0, 100, 0, 0, 0, 61239, 2000, 2000, 0, 'Волнорез-ученик to Player');
INSERT INTO `creature_text` VALUES (56674, 4, 0, 'Бежать я не собираюсь!', 12, 0, 100, 0, 0, 0, 55632, 8000, 8000, 47292, 'Волнорез-ученик');
INSERT INTO `creature_text` VALUES (56674, 4, 0, 'Кажется, теперь я понимаю.', 12, 0, 100, 0, 0, 0, 55613, 8000, 9000, 47292, 'Волнорез-ученик to Player');
INSERT INTO `creature_text` VALUES (56674, 4, 0, 'Мне удалось научиться чему-то новому!', 12, 0, 100, 0, 0, 0, 55612, 10000, 10000, 98901, 'Волнорез-ученик to Player');
INSERT INTO `creature_text` VALUES (56674, 4, 0, 'Обучение пошло мне на пользу.', 12, 0, 100, 0, 0, 0, 55614, 8000, 8000, 47292, 'Волнорез-ученик to Player');
INSERT INTO `creature_text` VALUES (56674, 4, 0, 'Спасибо за помощь, $n. Эти кинжалы проткнут множество врагов.', 12, 0, 100, 0, 0, 0, 61235, 11000, 11000, 0, 'Волнорез-ученик to Player');
INSERT INTO `creature_text` VALUES (56676, 3, 0, 'Боль мучает меня, но сердце мое радуется, что внуки Лю Лана не забыли обо мне.', 12, 0, 100, 0, 0, 27822, 55550, 30000, 30000, 0, 'Шэнь-Цзынь Су to Player');
INSERT INTO `creature_text` VALUES (56676, 3, 0, 'Боль просто невыносима. Я больше не могу плыть прямо!', 12, 0, 100, 0, 0, 27824, 55569, 58000, 58000, 0, 'Шэнь-Цзынь Су to Player');
INSERT INTO `creature_text` VALUES (56676, 3, 0, 'Она в том лесу, где пандарены никогда не бывают. Летите вдоль гор – и найдете ее.', 12, 0, 100, 0, 0, 27826, 55572, 93000, 93000, 0, 'Шэнь-Цзынь Су to Player');
INSERT INTO `creature_text` VALUES (56676, 3, 0, 'Прошу вас, дети, вытащите эту занозу. Я не могу сделать это сам.', 12, 0, 100, 0, 0, 27825, 55570, 71000, 71000, 0, 'Шэнь-Цзынь Су to Player');
INSERT INTO `creature_text` VALUES (56676, 3, 0, 'Спасибо, дети мои.', 12, 0, 100, 0, 0, 27827, 63407, 115000, 115000, 0, 'Шэнь-Цзынь Су to Player');
INSERT INTO `creature_text` VALUES (56676, 3, 0, 'У меня в боку заноза, и я никак не могу от нее избавиться.', 12, 0, 100, 0, 0, 27823, 55568, 44000, 44000, 0, 'Шэнь-Цзынь Су to Player');
INSERT INTO `creature_text` VALUES (56686, 0, 0, 'Три тысячи лет мы передаем знания наших предков новым поколениям. Старики – молодым. Учителя – ученикам.', 12, 0, 100, 1, 0, 27807, 55528, 0, 0, 0, 'Мастер Шан Си');
INSERT INTO `creature_text` VALUES (56686, 1, 0, 'В конце своей жизни каждый старейшина должен уйти и воткнуть свой посох рядом с посохами предков. Сегодня и мой посох станет частью этого леса.', 12, 0, 100, 1, 0, 27808, 55529, 0, 0, 0, 'Мастер Шан Си');
INSERT INTO `creature_text` VALUES (56686, 2, 0, 'Наш народ очень давно живет на спине великой черепахи Шэнь-Цзынь Су. Но вот уже много веков никто не разговаривал с ним.', 12, 0, 100, 1, 0, 27809, 55530, 0, 0, 0, 'Мастер Шан Си');
INSERT INTO `creature_text` VALUES (56686, 3, 0, 'Сейчас Шэнь-Цзынь Су болен, и всем нам грозит опасность. С помощью духов стихий ты прервешь вековое молчание. Ты поговоришь с ним.', 12, 0, 100, 1, 0, 27810, 55531, 0, 0, 0, 'Мастер Шан Си');
INSERT INTO `creature_text` VALUES (56686, 4, 0, 'Аиса и Цзи привели сюда духов стихий. Ты отправишься с ними, поговоришь с великим Шэнь-Цзынь Су и сделаешь все, что потребуется для спасения нашего народа.', 12, 0, 100, 1, 0, 27811, 55532, 0, 0, 0, 'Мастер Шан Си');
INSERT INTO `creature_text` VALUES (56686, 5, 0, 'Тебе многого удалось достичь. Я вижу в тебе великого героя... Я оставляю судьбу этой земли в твоих руках.', 12, 0, 100, 396, 0, 27812, 55533, 0, 0, 0, 'Мастер Шан Си');
INSERT INTO `creature_text` VALUES (56690, 0, 0, 'Поступки красноречивее слов. Мои воины мечтают о мести, но сначала им предстоит подготовиться к бою. А теперь уходи. У нас много дел.', 12, 0, 100, 0, 0, 0, 58063, 0, 0, 0, 'Инструктор Острый Гребень to Player');
INSERT INTO `creature_text` VALUES (56690, 3, 0, 'БЕЙ!', 14, 0, 100, 36, 0, 0, 55609, 76000, 76000, 0, 'Инструктор Острый Гребень');
INSERT INTO `creature_text` VALUES (56693, 0, 0, 'А, так ты и есть $gтот:та; $gсамый:самая; $gнезнакомец:незнакомка;. К сожалению, сейчас я делаю оружие – а ведь когда-то изготавливал вещи для молитв и празднеств. Эта война всех нас напугала.', 12, 0, 100, 0, 0, 0, 58057, 0, 0, 0, 'От-Теммдо to Player');
INSERT INTO `creature_text` VALUES (56701, 0, 0, 'За Жемчужный Плавник!', 12, 0, 100, 0, 0, 31787, 58038, 0, 0, 0, 'Смотритель пруда Жемчужного Плавника to Player');
INSERT INTO `creature_text` VALUES (56701, 0, 0, 'Мужайтесь, воины! Остановите хозенов!', 12, 0, 100, 0, 0, 0, 58034, 0, 0, 0, 'Смотритель пруда Жемчужного Плавника to Player');
INSERT INTO `creature_text` VALUES (56701, 4, 0, 'Смелее, братья! Вперед!', 12, 0, 100, 0, 0, 0, 58037, 10000, 10000, 0, 'Смотритель пруда Жемчужного Плавника to Player');
INSERT INTO `creature_text` VALUES (56701, 4, 0, 'Это $gтот:та; $gсамый:самая; $gнезнакомец:незнакомка;?', 12, 0, 100, 0, 0, 0, 58036, 17000, 17000, 119846, 'Смотритель пруда Жемчужного Плавника to Player');
INSERT INTO `creature_text` VALUES (56705, 3, 0, 'Хм... Какая броня...', 12, 0, 100, 23, 0, 0, 63491, 128000, 128000, 0, 'Огнегруб to Player');
INSERT INTO `creature_text` VALUES (56707, 3, 0, 'Это тот самый чужак, о котором говорил Чо. Интересно, ждать ли других?', 12, 0, 100, 0, 0, 0, 63496, 40000, 40000, 0, 'Чинь to Player');
INSERT INTO `creature_text` VALUES (56717, 0, 0, 'О да!', 14, 0, 100, 0, 0, 0, 73176, 0, 0, 0, 'Прыгопотам to 0');
INSERT INTO `creature_text` VALUES (56717, 1, 0, 'Крутиться!', 14, 0, 100, 0, 0, 0, 63062, 0, 0, 0, 'Прыгопотам to 0');
INSERT INTO `creature_text` VALUES (56717, 2, 0, 'Прыгопотам издает громкий рев! Гну-сини идут!', 41, 0, 100, 15, 0, 0, 59693, 0, 0, 0, 'Прыгопотам to 0');
INSERT INTO `creature_text` VALUES (56717, 3, 0, 'Последняя морковка была лишней...', 14, 0, 100, 0, 0, 0, 63060, 0, 0, 0, 'Прыгопотам to 0');
INSERT INTO `creature_text` VALUES (56717, 4, 0, 'У вас мозгов... как у репы...', 14, 0, 100, 0, 0, 0, 63058, 0, 0, 0, 'Прыгопотам to Player');
INSERT INTO `creature_text` VALUES (56719, 0, 0, 'Меня больше не запрут в клетку. Эти монахи не смогли меня остановить, и вы не сможете!', 14, 0, 100, 0, 0, 28346, 63337, 0, 0, 0, 'Ша Жестокости to 0');
INSERT INTO `creature_text` VALUES (56719, 1, 0, 'Пока в ваших сердцах есть место жестокости... я могу вернуться...', 14, 0, 100, 0, 0, 28347, 63338, 0, 0, 0, 'Ша Жестокости to Player');
INSERT INTO `creature_text` VALUES (56725, 3, 0, '$c, веди нас.', 12, 0, 100, 0, 0, 0, 61257, 1000, 1000, 0, 'Владыка вод Жемчужного Плавника to Player');
INSERT INTO `creature_text` VALUES (56726, 3, 0, 'Жду приказа.', 12, 0, 100, 0, 0, 0, 61254, 1000, 1000, 0, 'Мистик Жемчужного Плавника to Player');
INSERT INTO `creature_text` VALUES (56727, 3, 0, '$n, в бой.', 12, 0, 100, 0, 0, 0, 61255, 3000, 3000, 0, 'Волнорез Жемчужного Плавника to Player');
INSERT INTO `creature_text` VALUES (56728, 3, 0, '$r, наш час пробил.', 12, 0, 100, 0, 0, 0, 61256, 2000, 2000, 0, 'Страж волн Жемчужного Плавника to Player');
INSERT INTO `creature_text` VALUES (56728, 4, 0, 'Не стой на месте!', 12, 0, 100, 0, 0, 0, 112299, 4000, 4000, 0, 'Страж волн Жемчужного Плавника to Вождь Гукгут');
INSERT INTO `creature_text` VALUES (56729, 0, 0, 'Hmm... |3-7($c) not often drop to us ...', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Разводчик лотосов to Player');
INSERT INTO `creature_text` VALUES (56729, 1, 0, 'These Jin-yu with their mad rituals ...', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Разводчик лотосов to Player');
INSERT INTO `creature_text` VALUES (56732, 0, 0, 'The heart of the Great Serpent will not fall into your hands!', 14, 0, 100, 0, 0, 27603, 55904, 0, 0, 0, 'LIU FLAME AGRO 01');
INSERT INTO `creature_text` VALUES (56732, 1, 0, 'The haze has been lifted from my eyes... forgive me for doubting you...', 14, 0, 100, 0, 0, 27604, 55908, 0, 0, 0, 'LIU FLAME DEATH 01');
INSERT INTO `creature_text` VALUES (56732, 2, 0, 'Jade Serpent, grant me strength!', 14, 0, 100, 0, 0, 27605, 55742, 0, 0, 0, 'LIU FLAME EVENT 01');
INSERT INTO `creature_text` VALUES (56732, 3, 0, 'The cycle must continue, the Jade Serpent must be reborn!', 14, 0, 100, 0, 0, 27606, 55905, 0, 0, 0, 'LIU FLAME EVENT 02');
INSERT INTO `creature_text` VALUES (56732, 4, 0, 'Your forces are weak!', 14, 0, 100, 0, 0, 27607, 61565, 0, 0, 0, 'LIU FLAME INTRO 01');
INSERT INTO `creature_text` VALUES (56732, 5, 0, 'The heart is MINE!', 14, 0, 100, 0, 0, 27608, 55906, 0, 0, 0, 'LIU FLAME KILL 01');
