INSERT INTO `creature_text` VALUES (56742, 3, 0, 'Нужно зажечь сигнальный огонь. Не будет подкрепления – они разрушат ворота!', 12, 0, 100, 0, 0, 29068, 55755, 94343, 94343, 0, 'Защитник Змеиного Хребта to Player');
