INSERT INTO `creature_text` VALUES (56785, 0, 0, 'Путь наездника на змеях омыт слезами и кровью. Для вас он только начинается.', 14, 0, 100, 0, 0, 0, 56187, 0, 0, 0, 'Лю Пламенное Сердце');
