INSERT INTO `creature_text` VALUES (56802, 1, 0, '$n, I know I\'ve seen your friend. Believe me, he needs a rest. Let\'s talk.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Чжан Юэ to Player');
INSERT INTO `creature_text` VALUES (56843, 0, 0, 'You there! In the rafters! My sacred library has become possessed by the Sha!', 14, 0, 100, 0, 0, 30887, 57464, 0, 0, 0, 'Talk of lorewalkter stonestep 0');
INSERT INTO `creature_text` VALUES (56843, 1, 0, 'See how they pour from every letter of these sacred scrolls... corrupting them! Defiling them!', 14, 0, 100, 0, 0, 30888, 57465, 0, 0, 0, 'Talk of lorewalkter stonestep 1');
INSERT INTO `creature_text` VALUES (56843, 2, 0, 'I beseech you, kind strangers. Cleanse this place of their foul presence! Lay our stories to rest!', 14, 0, 100, 0, 0, 30889, 57604, 0, 0, 0, 'Talk of lorewalkter stonestep 2');
INSERT INTO `creature_text` VALUES (56843, 3, 0, 'You must destroy the source of the corruption! Quickly, before our cherished history is erased forever!', 14, 0, 100, 0, 0, 30890, 57467, 0, 0, 0, 'Talk of lorewalkter stonestep 3');
INSERT INTO `creature_text` VALUES (56843, 4, 0, 'Oh, my. If I am not mistaken, it appears that the tale of Zao Sunseeker has come to life before us.', 14, 0, 100, 0, 0, 30901, 57605, 0, 0, 0, 'Zao appears 0');
INSERT INTO `creature_text` VALUES (56843, 5, 0, 'According to the legend, long ago, there were five suns that burned brightly in the sky. When the winds blew, their intense heat caused great wildfires.', 14, 0, 100, 0, 0, 30902, 57607, 0, 0, 0, 'Zao appears 1');
INSERT INTO `creature_text` VALUES (56843, 6, 0, 'Crops wilted, villages burned, and families wandered endlessly to seek refuge from the flames...', 14, 0, 100, 0, 0, 30903, 57608, 0, 0, 0, 'Zao appears 2');
INSERT INTO `creature_text` VALUES (56843, 7, 0, 'I can allow this no longer!', 14, 0, 100, 0, 0, 27747, 57748, 0, 0, 0, 'Zao appears 3');
INSERT INTO `creature_text` VALUES (56843, 8, 0, 'The fires, they are burning the library! Please, heroes! Purge this evil before the temple crumbles to ash!', 14, 0, 100, 0, 0, 30904, 57610, 0, 0, 0, 'Zao appears 4');
INSERT INTO `creature_text` VALUES (56843, 9, 0, 'Zao has become corrupted too! Please, fight him with dignity...', 14, 0, 100, 0, 0, 30906, 57746, 0, 0, 0, 'Zao enters combat 0');
INSERT INTO `creature_text` VALUES (56843, 10, 0, 'Ah, it is not yet over. From what see, we face the trial of the yaungol. Let me shed some light...', 14, 0, 100, 0, 0, 30896, 57614, 0, 0, 0, 'trial appears 1');
INSERT INTO `creature_text` VALUES (56843, 11, 0, 'As the tale goes, the yaungol was traveling across the Kun\'lai plains when suddenly he was ambushed by two strange creatures!', 14, 0, 100, 0, 0, 0, 57986, 0, 0, 0, 'trial appears 2');
INSERT INTO `creature_text` VALUES (56843, 12, 0, 'The yaungol attacked the beasts with great determination and ferocity. Yet, the more he attacked, the more fearsome the beasts grew!', 14, 0, 100, 0, 0, 0, 57988, 0, 0, 0, 'trial appears 3');
INSERT INTO `creature_text` VALUES (56843, 13, 0, 'However, when he let it alone, the beast shrank to near nothingness.', 14, 0, 100, 0, 0, 0, 57989, 0, 0, 0, 'trial appears 4');
INSERT INTO `creature_text` VALUES (56843, 14, 0, 'For you see, strife feeds on conflict. when you have peace in your heart, strife will find somewhere else to roam.', 14, 0, 100, 0, 0, 0, 57990, 0, 0, 0, 'trial appears 5');
INSERT INTO `creature_text` VALUES (56843, 15, 0, 'Как вы уже поняли, конфликты порождают вражду. Если в вашем сердце царит покой, вокруг вас не будет и раздоров.', 14, 0, 100, 1, 0, 30900, 57990, 0, 0, 0, 'Хранитель истории Каменная Поступь to Опасность');
INSERT INTO `creature_text` VALUES (56843, 16, 0, 'Спасибо вам, герои. Вы принесли покой этому священному храму.', 14, 0, 100, 1, 0, 30885, 57612, 0, 0, 0, 'Хранитель истории Каменная Поступь to Опасность');
INSERT INTO `creature_text` VALUES (56843, 17, 0, 'Теперь идите! Прежде чем начать ритуал восстановления, я должен помедитировать.', 14, 0, 100, 1, 0, 30886, 57613, 0, 0, 0, 'Хранитель истории Каменная Поступь to Опасность');
INSERT INTO `creature_text` VALUES (56843, 18, 0, 'Цзао был настоящим героем нашего народа. Горько, что его история осквернена ша, но вы сумели восстановить добрую память о нем.', 14, 0, 100, 1, 0, 30907, 57611, 0, 0, 0, 'Хранитель истории Каменная Поступь to Опасность');
