INSERT INTO `creature_text` VALUES (57105, 3, 0, 'Personne ne touche au maître !', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Evade');
INSERT INTO `creature_text` VALUES (57106, 0, 0, 'Vous ne toucherez pas au maître !', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Aggro');
INSERT INTO `creature_text` VALUES (57106, 1, 0, 'Tremblez, tremblez sous cette secousse !', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Sort');
INSERT INTO `creature_text` VALUES (57106, 2, 0, 'Aaaaaah, maître !', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Death');
INSERT INTO `creature_text` VALUES (57106, 3, 0, 'Je vous l\'avez dit que vous ne survivrez pas...', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Evade');
INSERT INTO `creature_text` VALUES (57107, 3, 0, 'Герои! Нас прислал Ноздорму! Скорее, забирайтесь к нам на спины! Мы должны попасть к Душе Дракона!', 14, 0, 100, 0, 0, 26032, 55914, 5055, 5055, 0, 'Бронзовый дракон to Player');
INSERT INTO `creature_text` VALUES (57117, 3, 0, 'Я здесь, моя королева!', 14, 0, 100, 0, 0, 26136, 55912, 4650, 4650, 0, 'Капитан Варо\'тен to Player');
INSERT INTO `creature_text` VALUES (57119, 4, 0, 'Бортельный дукер!', 12, 0, 100, 25, 0, 0, 58275, 2000, 2000, 0, 'Хозен-опустошитель');
INSERT INTO `creature_text` VALUES (57119, 4, 0, 'Дукеры быстро дохнуть!', 12, 0, 100, 407, 0, 0, 57939, 14000, 14000, 0, 'Хозен-опустошитель');
INSERT INTO `creature_text` VALUES (57119, 4, 0, 'Таких дукеров, как ты, я еще не видел!', 12, 0, 100, 407, 0, 0, 57940, 12000, 12000, 0, 'Хозен-опустошитель');
INSERT INTO `creature_text` VALUES (57120, 0, 0, 'I feared that this day would come. I will pack up my things.', 12, 0, 100, 0, 0, 0, 55926, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (57121, 0, 0, 'That\'s right. Now please, $r, leave and let us defend our property.', 12, 0, 100, 0, 0, 0, 55931, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (57123, 0, 0, 'I see. I\'d hoped that we could wait out this invasion, but if the authorities want us to leave, we will leave.', 12, 0, 100, 0, 0, 0, 55934, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (57123, 1, 0, 'Ying, Huizhong... gather your things. We\'re leaving the farm until it\'s safe.', 12, 0, 100, 0, 0, 0, 55935, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (57123, 2, 0, 'I don\'t know, dear.', 12, 0, 100, 0, 0, 0, 55938, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (57123, 3, 0, 'Are you?', 12, 0, 100, 0, 0, 0, 55939, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (57126, 0, 0, 'I am a farmer, $c. It\'s all I know.', 12, 0, 100, 0, 0, 0, 55943, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (57201, 3, 0, 'Нееееет... прочь...', 14, 0, 100, 0, 0, 26110, 55973, 545837, 545837, 83237, 'Неведомое зло to Player');
INSERT INTO `creature_text` VALUES (57204, 0, 0, 'I only once had to do it ... I hope everything will turn out.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Мудрый Асе to Player');
INSERT INTO `creature_text` VALUES (57204, 1, 0, 'What ... What is this place? It\'s beautiful ... but something is wrong.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Мудрый Асе to Player');
INSERT INTO `creature_text` VALUES (57286, 3, 0, 'I am very old. My days in this world are almost over.', 12, 0, 100, 0, 0, 28576, 56135, 71000, 71000, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` VALUES (57286, 3, 0, 'Видишь большую статую там, вдали?', 12, 0, 100, 0, 0, 28577, 56136, 81000, 81000, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` VALUES (57286, 3, 0, 'Возможно, когда-нибудь тебе тоже придется встать на защиту того, что дорого для тебя.', 12, 0, 100, 0, 0, 28582, 56140, 121000, 121000, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` VALUES (57286, 3, 0, 'До свидания, маленький друг. Очень скоро мы снова увидимся!', 12, 0, 100, 0, 0, 28586, 61625, 155000, 155000, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` VALUES (57286, 3, 0, 'Еще немного нефрита, собранного с твоей помощью, и она будет закончена.', 12, 0, 100, 0, 0, 28579, 56138, 93000, 93000, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` VALUES (57286, 3, 0, 'Жрецы храма слишком напуганы, чтобы взглянуть правде в лицо...', 12, 0, 100, 0, 0, 28575, 56157, 65000, 65000, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` VALUES (57286, 3, 0, 'Когда придет такой день, вспомни о свете и всех чудесах этого мира, и сражайся.', 12, 0, 100, 0, 0, 28583, 56927, 129000, 129000, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` VALUES (57286, 3, 0, 'Мои силы на исходе. Мне нужно отдохнуть.', 12, 0, 100, 0, 0, 28585, 56147, 149000, 149000, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` VALUES (57286, 3, 0, 'Мы живем вместе и умираем вместе. Все в Пандарии связано между собой.', 12, 0, 100, 0, 0, 28584, 56928, 140000, 140000, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` VALUES (57286, 3, 0, 'Не печалься. Жизнь продолжается. Я не являюсь ни началом ее, ни концом.', 12, 0, 100, 0, 0, 28581, 56141, 112000, 112000, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` VALUES (57286, 3, 0, 'С моим последним вздохом, моя жизненная сущность перенесется в статую. Так появится новый страж.', 12, 0, 100, 0, 0, 28580, 56139, 101000, 101000, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` VALUES (57286, 3, 0, 'Скульпторы много лет усердно трудились над ней, и вот она почти готова.', 12, 0, 100, 0, 0, 28578, 56137, 87000, 87000, 0, 'Юй-лун to Юй-лун');
INSERT INTO `creature_text` VALUES (57298, 3, 0, 'Hey, this is a real nice gift. Thanks, $n!', 12, 0, 100, 1, 0, 0, 59075, 2102000, 2102000, 0, 'Farmer Fung to Player');
INSERT INTO `creature_text` VALUES (57298, 3, 0, 'Редиса для славной крошки Эллы? Конечно, штучка у меня найдется. Вот, держи.', 12, 0, 100, 0, 0, 0, 65907, 38417000, 38417000, 0, 'Фермер Фун to Player');
INSERT INTO `creature_text` VALUES (57310, 3, 0, 'А вот и они...', 12, 0, 100, 0, 0, 0, 85949, 33000, 33000, 0, 'И-Мо Длинная Бровь to Sad Panda Wheel');
INSERT INTO `creature_text` VALUES (57310, 3, 0, 'Говори что хочешь. Я не сдвинусь с места.', 12, 0, 100, 0, 0, 0, 56588, 2000, 2000, 0, 'И-Мо Длинная Бровь to Sad Panda Wheel');
INSERT INTO `creature_text` VALUES (57310, 3, 0, 'Держитесь, буревестники! Летите сюда, заберите меня с собой!', 12, 0, 100, 0, 0, 0, 56853, 127000, 127000, 0, 'И-Мо Длинная Бровь to Sad Panda Wheel');
INSERT INTO `creature_text` VALUES (57310, 3, 0, 'Не-е-ет!', 12, 0, 100, 0, 0, 0, 123493, 161000, 161000, 0, 'И-Мо Длинная Бровь to Sad Panda Wheel');
INSERT INTO `creature_text` VALUES (57310, 3, 0, 'Ой! Ты что это делаешь?', 12, 0, 100, 0, 0, 0, 56212, 11000, 11000, 0, 'И-Мо Длинная Бровь to Sad Panda Wheel');
INSERT INTO `creature_text` VALUES (57310, 3, 0, 'Ох... Голова кружится.', 12, 0, 100, 0, 0, 0, 56217, 86000, 86000, 0, 'И-Мо Длинная Бровь to Sad Panda Wheel');
INSERT INTO `creature_text` VALUES (57310, 3, 0, 'Ты совсем без ума уже! Вот еще один!', 12, 0, 100, 0, 0, 0, 56218, 100000, 100000, 0, 'И-Мо Длинная Бровь to Player');
INSERT INTO `creature_text` VALUES (57310, 3, 0, 'Уходи отсюда, или они заберут и тебя!', 12, 0, 100, 0, 0, 0, 56216, 62000, 62000, 0, 'И-Мо Длинная Бровь to Sad Panda Wheel');
INSERT INTO `creature_text` VALUES (57310, 3, 0, 'Что за?..', 12, 0, 100, 0, 0, 0, 56215, 55000, 55000, 0, 'И-Мо Длинная Бровь to Sad Panda Wheel');
INSERT INTO `creature_text` VALUES (57310, 3, 0, 'Эй, хватит! Я жду, пока меня заберут буревестники!', 12, 0, 100, 0, 0, 0, 56213, 17000, 17000, 0, 'И-Мо Длинная Бровь to Sad Panda Wheel');
INSERT INTO `creature_text` VALUES (57385, 0, 0, 'I\'d love to , $class, but I was tortured weeds. Tell me... Tell me, can you help me out ?', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Smart text');
INSERT INTO `creature_text` VALUES (57400, 3, 0, 'Гони их прочь!', 12, 0, 100, 0, 0, 0, 63414, 2536000, 2536000, 0, 'Страж Юй-лун to Player');
INSERT INTO `creature_text` VALUES (57400, 3, 0, 'Я достиг просветления. Спасибо, |3-6($r).', 12, 0, 100, 0, 0, 0, 63409, 2540000, 2540000, 0, 'Страж Юй-лун to Player');
INSERT INTO `creature_text` VALUES (57402, 3, 0, 'Говорят, путь в Союз земледельцев лежит через желудок его руководителя. Что скажешь?', 12, 0, 100, 1, 0, 0, 64495, 9441000, 9441000, 0, 'Хаохань Грязный Коготь to Player');
INSERT INTO `creature_text` VALUES (57405, 3, 0, 'Enjoy your tour of Pandaria\'s most ancient and respected silk farm.', 12, 0, 100, 0, 0, 0, 56524, 0, 13203, 0, 'Silkmaster Tsai to Player');
INSERT INTO `creature_text` VALUES (57406, 3, 0, 'Here we feed new silkworm larvae until they are ready to cocoon themselves.', 12, 0, 100, 7, 0, 0, 56527, 83078, 83078, 0, 'Silk Farmer to Player');
INSERT INTO `creature_text` VALUES (57406, 3, 0, 'We have spent generations perfecting just the right leaves for silkworm food.', 12, 0, 100, 432, 0, 0, 56526, 33782, 33782, 0, 'Silk Farmer to Player');
INSERT INTO `creature_text` VALUES (57406, 3, 0, 'ПЛОХОЙ ЧЕРВЬ! ПЛОХОЙ, ПЛОХОЙ, ПЛОХОЙ ЧЕРВЬ!', 14, 0, 100, 7, 0, 0, 56309, 4682000, 4682000, 0, 'Сборщик шелка');
INSERT INTO `creature_text` VALUES (57421, 0, 0, 'Mothallus approaches!', 41, 0, 100, 0, 0, 0, 56414, 0, 0, 0, 'Mothallus to Player');
INSERT INTO `creature_text` VALUES (57421, 1, 0, 'Mothallus ascends, free the Mothfighters from their cocoons so they can bring her down.', 41, 0, 100, 0, 0, 0, 56506, 0, 0, 0, 'Mothallus to 0');
INSERT INTO `creature_text` VALUES (57423, 0, 0, 'Let\'s go home.', 12, 0, 0, 1, 0, 0, 56267, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57423, 1, 0, 'Dad... I never want to see another carrot again.', 12, 0, 0, 1, 0, 0, 56257, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57432, 0, 0, 'Госпожа панда! Танцуй на вершине моркови!', 14, 0, 100, 0, 0, 0, 56224, 0, 0, 0, 'Прыгохвост-смотрящий');
INSERT INTO `creature_text` VALUES (57457, 0, 0, 'A little rain never hurt anybody... yet.', 12, 0, 100, 1, 0, 0, 56300, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57457, 0, 1, 'Don\'t bother me.', 12, 0, 100, 1, 0, 0, 56302, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57457, 0, 2, 'How could this happen to me? I\'m sick of this life.', 12, 0, 100, 1, 0, 0, 56303, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57457, 0, 3, 'Fine... whatever. Hand it over.', 12, 0, 100, 1, 0, 0, 57166, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57457, 0, 4, 'Go away.', 12, 0, 100, 1, 0, 0, 90142, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57457, 0, 5, 'I feel... happy!!', 12, 0, 100, 1, 0, 0, 57117, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57457, 0, 6, 'I... I feel... happy!', 12, 0, 100, 1, 0, 0, 57167, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57457, 0, 7, 'It\'s raining still. And freezing. At least we haven\'t had a typhoon lately.', 12, 0, 100, 1, 0, 0, 56295, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57457, 0, 8, 'Leave me alone.', 12, 0, 100, 1, 0, 0, 56289, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57457, 0, 9, 'Not much of a day. Just right for not much of a life.', 12, 0, 100, 1, 0, 0, 56299, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57457, 0, 10, 'Oh, bother.', 12, 0, 100, 1, 0, 0, 56296, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57457, 0, 11, 'Oh, great. An adventurer. Just what I needed.', 12, 0, 100, 1, 0, 0, 56298, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57457, 0, 12, 'Stop crowding me, everybody\'s always crowding around in this village. There\'s no space.', 12, 0, 100, 1, 0, 0, 56294, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57457, 0, 13, 'Sure, it\'s not like things could get any worse...', 12, 0, 100, 1, 0, 0, 57152, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57457, 0, 14, 'Thank you so much, brave heroes! I will tell stories of you! Of <name> the Mighty! And Ken-Ken the Fearless!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57457, 0, 15, 'What day is it? Nobody tells me anything.', 12, 0, 100, 1, 0, 0, 56301, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57457, 0, 16, 'What do you want?', 12, 0, 100, 1, 0, 0, 74299, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57457, 0, 17, 'What\'s the point anymore?', 12, 0, 100, 1, 0, 0, 56290, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57457, 1, 0, 'Everything suddenly seems... brighter.', 12, 0, 100, 1, 0, 0, 57116, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57457, 1, 3, 'What am I doing out here? I\'ve got work to do!', 12, 0, 100, 1, 0, 0, 57120, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57457, 1, 4, 'Ugh... where am I?', 12, 0, 100, 1, 0, 0, 57082, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57457, 1, 5, 'Ugh... what was that stuff? It went through me like bad tea.', 12, 0, 100, 1, 0, 0, 57164, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57457, 1, 6, 'Oh... ughh... what have you done to me?!', 12, 0, 100, 1, 0, 0, 57156, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57459, 3, 0, 'Налево... Налево! НАААЛЕЕЕЕВОООО!', 14, 0, 100, 0, 0, 0, 56271, 599390, 599390, 0, 'Гор\'ин Малопупс');
INSERT INTO `creature_text` VALUES (57460, 3, 0, 'Гори-и-и!', 14, 0, 100, 0, 0, 0, 56274, 25031, 303969, 0, 'Мистер Топотун');
INSERT INTO `creature_text` VALUES (57460, 3, 0, 'Растопчу.', 14, 0, 100, 0, 0, 0, 56275, 345000, 345000, 0, 'Мистер Топотун');
INSERT INTO `creature_text` VALUES (57638, 0, 0, 'Тростник Поющих прудов – прочнейший в этих краях, но для моего лба даже он – не прочнее воздуха.', 12, 0, 100, 1, 0, 0, 56333, 0, 0, 0, 'Йо-Йо Железная Бровь');
INSERT INTO `creature_text` VALUES (57638, 1, 0, 'Многие пытались испытать меня, но я несокрушим.', 12, 0, 100, 1, 0, 0, 56334, 0, 0, 0, 'Йо-Йо Железная Бровь');
INSERT INTO `creature_text` VALUES (57648, 1, 0, 'So sticky! I\'ll try to bring that moth down for you.', 12, 0, 100, 0, 0, 0, 56359, 0, 0, 108851, 'Mothfighter to Mothallus');
INSERT INTO `creature_text` VALUES (57648, 1, 0, 'Thank you! I will try to break some of that silk shield off.', 12, 0, 100, 0, 0, 0, 56358, 42781, 42781, 0, 'Mothfighter to Mothallus');
INSERT INTO `creature_text` VALUES (57648, 4, 0, 'Mothallus is defeated! Hurray for $p!', 12, 0, 100, 0, 0, 0, 56345, 5469, 5469, 43671, 'Mothfighter to Mothallus');
INSERT INTO `creature_text` VALUES (57662, 0, 0, 'A little help here?', 12, 0, 100, 0, 0, 0, 56337, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (57662, 0, 1, 'Anybody out there?', 12, 0, 100, 0, 0, 0, 56335, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (57662, 0, 2, 'Can anybody hear me?', 12, 0, 100, 0, 0, 0, 56342, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (57662, 0, 3, 'Get me out of this barrel!', 12, 0, 100, 0, 0, 0, 56341, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (57662, 0, 4, 'Heeelllp!', 12, 0, 100, 0, 0, 0, 56340, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (57669, 0, 0, 'Наши ремесленники выстругивают отличные доски. Эти доски могут выдержать даже самую сильную бурю. Для моего сокрушительного черепа они не прочнее воды.', 12, 0, 100, 1, 0, 0, 56343, 0, 0, 0, 'Йо-Йо Железная Бровь');
INSERT INTO `creature_text` VALUES (57669, 1, 0, 'Пандаренам не по силам соорудить барьер, который остановил бы меня.', 12, 0, 100, 2, 0, 0, 56344, 0, 0, 0, 'Йо-Йо Железная Бровь');
INSERT INTO `creature_text` VALUES (57670, 0, 0, 'Эти камни – самые твердые, что есть на Шэнь-Цзынь Су, но и они не устоят перед мощью моих ударов.', 12, 0, 100, 1, 0, 0, 56355, 0, 0, 0, 'Йо-Йо Железная Бровь');
INSERT INTO `creature_text` VALUES (57670, 1, 0, 'Ни одно разумное создание не усомнится в моей мощи.', 12, 0, 100, 2, 0, 0, 56356, 0, 0, 0, 'Йо-Йо Железная Бровь');
INSERT INTO `creature_text` VALUES (57681, 0, 0, 'Get out of here until he\'s back!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (57681, 0, 1, 'I need a beer.', 12, 0, 100, 0, 0, 0, 56348, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (57682, 3, 0, 'Аспекты уже в сборе и ждут вашего прибытия. Идите же!', 12, 0, 100, 396, 0, 0, 56747, 528000, 528000, 0, 'Тиригоса to Player');
INSERT INTO `creature_text` VALUES (57682, 3, 0, 'Безликие полководцы, Зон\'озз и Йор\'садж, командуют вражескими полчищами оттуда – видите эти ужасные тени вдали?', 12, 0, 100, 397, 0, 26337, 56743, 27000, 27000, 0, 'Тиригоса');
INSERT INTO `creature_text` VALUES (57682, 3, 0, 'Древние боги явили себя – из их пастей к храму катится волна тьмы. Мы ДОЛЖНЫ выиграть время для Аспектов.', 12, 0, 100, 396, 0, 0, 56742, 15000, 15000, 0, 'Тиригоса');
INSERT INTO `creature_text` VALUES (57682, 3, 0, 'Остался лишь один полководец. Вам нужно спешить!', 12, 0, 100, 22, 0, 26311, 56745, 1515, 1515, 0, 'Тиригоса to Player');
INSERT INTO `creature_text` VALUES (57682, 3, 0, 'Поток тьмы слабеет. Вы настоящие герои легенд!', 12, 0, 100, 113, 0, 26312, 56746, 97578, 97578, 0, 'Тиригоса to Player');
INSERT INTO `creature_text` VALUES (57682, 3, 0, 'Силы возвращаются ко мне.', 12, 0, 100, 0, 0, 0, 56741, 10000, 10000, 0, 'Тиригоса');
INSERT INTO `creature_text` VALUES (57684, 3, 0, 'Тебе надо спешить... Драконий Покой практически пал... Все... Потеряно...', 14, 0, 100, 0, 0, 26306, 56740, 3000, 3000, 0, 'Проекция Тиригосы to Лорд Афрасастраз');
INSERT INTO `creature_text` VALUES (57692, 0, 0, 'Невероятно! Никогда не видел столь искусной работы.', 12, 0, 100, 1, 0, 0, 56363, 0, 0, 0, 'Йо-Йо Железная Бровь');
INSERT INTO `creature_text` VALUES (57692, 1, 0, 'Остается только одно. Разбить на куски! И тогда все раз и навсегда уверятся в силе моих могучих ударов.', 12, 0, 100, 1, 0, 0, 56361, 0, 0, 0, 'Йо-Йо Железная Бровь');
INSERT INTO `creature_text` VALUES (57692, 2, 0, 'Ох... моя голова... Как больно! Я... В этом пьедестале, должно быть, содержится невероятная мощь... он мощнее, чем мой лоб.', 12, 0, 100, 18, 0, 0, 56362, 0, 0, 0, 'Йо-Йо Железная Бровь');
INSERT INTO `creature_text` VALUES (57692, 3, 0, 'I ... I, perhaps, lie down ... And then something head ached ...', 12, 0, 100, 0, 0, 0, 56364, 0, 0, 0, 'Йо-Йо Железная Бровь');
INSERT INTO `creature_text` VALUES (57712, 0, 0, 'Здравствуй, друг! Если хочешь, можешь взять мою повозку. Она отвезет тебя на крестьянский двор Дай-Ло.', 12, 0, 100, 3, 0, 0, 56406, 0, 0, 0, 'Хозяин повозки');
INSERT INTO `creature_text` VALUES (57712, 1, 0, 'Здравствуй, друг! Если хочешь, можешь взять мою повозку. Она отвезет тебя в Храм Пяти Рассветов.', 12, 0, 100, 3, 0, 0, 58657, 0, 0, 0, 'Хозяин повозки');
INSERT INTO `creature_text` VALUES (57713, 4, 0, 'I will try to ask the threads ...', 12, 0, 100, 6, 0, 30691, 56387, 3000, 3000, 0, 'Мудрый Асе to Линь Мягкая Лапа');
INSERT INTO `creature_text` VALUES (57714, 3, 0, 'Отличная мысль, Кен-Кен. Я пойду на юг, в джунгли.', 12, 0, 100, 1, 0, 31939, 56390, 36000, 36000, 0, 'Кан Ежевичный Посох to Линь Мягкая Лапа');
INSERT INTO `creature_text` VALUES (57714, 3, 0, 'Спросить у потоков? Асё, ты же не старейшина. Ты еще даже не взрослый! Только зря время потратишь.', 12, 0, 100, 274, 0, 31938, 56388, 23000, 23000, 0, 'Кан Ежевичный Посох to Линь Мягкая Лапа');
INSERT INTO `creature_text` VALUES (57715, 3, 0, 'Кен-Кен думать, надо разделиться. Идти за четырьмя ветрами.', 12, 0, 100, 1, 0, 30866, 56389, 30000, 30000, 0, 'Кен-Кен to Линь Мягкая Лапа');
INSERT INTO `creature_text` VALUES (57715, 3, 0, 'Кен-Кен идти за восточным ветром. Ближе всего.', 12, 0, 100, 1, 0, 30867, 56392, 46000, 46000, 0, 'Кен-Кен to Линь Мягкая Лапа');
INSERT INTO `creature_text` VALUES (57716, 3, 0, 'Кто-нибудь из вас знает, где он живет?', 12, 0, 100, 6, 0, 30881, 56383, 3000, 3000, 0, 'Линь Мягкая Лапа to Player');
INSERT INTO `creature_text` VALUES (57716, 3, 0, 'Ну да. Прости.', 12, 0, 100, 24, 0, 30882, 56385, 13000, 13000, 0, 'Линь Мягкая Лапа to Player');
INSERT INTO `creature_text` VALUES (57716, 3, 0, 'Так откуда начнем поиски?', 12, 0, 100, 6, 0, 30883, 56386, 16000, 16000, 0, 'Линь Мягкая Лапа to Player');
INSERT INTO `creature_text` VALUES (57721, 3, 0, 'Я бы хотела сопроводить этих воинов Альянса домой.', 12, 0, 100, 1, 0, 27405, 60628, 34000, 34000, 0, 'Аиса Воспевающая Облака to Player');
INSERT INTO `creature_text` VALUES (57767, 1, 0, 'That\'s right, mortal. The prize you seek no longer sleeps within a shell. Here I am: in the flesh.', 12, 0, 100, 3, 0, 0, 56836, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (57767, 2, 0, 'I\'m not some trophy for a red dragons mantle-piece, an I\'m never going back.', 12, 0, 100, 3, 0, 0, 0, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (57767, 3, 0, 'Don\'t look surprised! We dragons are consicious, even within our shells. As I glew, I could hear the plotting and scheming. I was to be born a prisoner.', 12, 0, 100, 3, 0, 0, 0, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (57767, 4, 0, 'Follow me.', 12, 0, 100, 3, 0, 0, 93565, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (57777, 1, 0, 'Excellent work, friend. You\'ve proven that a single skilled assassin can strike a decisive blow where a whole army may fail.', 12, 0, 100, 3, 0, 0, 56554, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (57777, 2, 0, 'If you wish to continue our work, we will need some additional supplies...', 12, 0, 100, 3, 0, 0, 56555, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (57777, 3, 0, 'The gems you collected will be able to augment the power of the weapons I gave you, but I require another ingredient. It will come from your next target.', 12, 0, 100, 3, 0, 0, 0, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (57777, 4, 0, 'I hope to see you back from this mission alive. But if I do not, I want so say I\'ve enjoyed seeing you work. You\'re a credit to yuor race. Good luck.', 12, 0, 100, 3, 0, 0, 0, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (57777, 5, 0, 'Incredible! Few assassins would be clever enough to infiltrate Nalice\'s wards and use her own magic against her.', 12, 0, 100, 3, 0, 0, 56728, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (57777, 6, 0, 'Perhaps. For that reason, we will need to gather more supplies...', 12, 0, 100, 3, 0, 0, 56729, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (57777, 7, 0, 'Succeed here, and our work is nearly done. Good luck, hero!', 12, 0, 100, 3, 0, 0, 56734, 0, 0, 0, 'say');
INSERT INTO `creature_text` VALUES (57825, 0, 0, 'Oh great! Someone else has come to bother me.', 12, 0, 100, 1, 0, 0, 57431, 0, 0, 0, 'Smart Text');
INSERT INTO `creature_text` VALUES (57830, 3, 0, '%s мирно храпит.', 16, 0, 100, 0, 0, 0, 71841, 93000, 98000, 0, 'Сунни to Player');
INSERT INTO `creature_text` VALUES (57830, 3, 0, 'А я вот еще денек посплю...', 12, 0, 100, 0, 0, 0, 57432, 98000, 98000, 0, 'Сунни to Player');
INSERT INTO `creature_text` VALUES (57898, 3, 0, 'Ну все, все! Хватит уже толкаться!', 12, 0, 100, 0, 0, 0, 57072, 2000, 2000, 0, 'И-Мо Длинная Бровь to Player');
INSERT INTO `creature_text` VALUES (57898, 3, 0, 'Я иду к себе. Можешь рассказывать тетушке, как \"героически\" $gспас:спасла; меня от самого себя... да о чем угодно.', 12, 0, 100, 0, 0, 0, 57080, 6000, 6000, 0, 'И-Мо Длинная Бровь to Player');
INSERT INTO `creature_text` VALUES (57913, 3, 0, 'Эй! Сюда! Нет! Внизу! Если хочешь, я могу доставить тебя назад в настоящее.', 12, 0, 100, 0, 0, 0, 56600, 153928, 153928, 0, 'Хроми to Player');
INSERT INTO `creature_text` VALUES (57916, 3, 0, 'Он движется к Водовороту! Мы должны остановить его, У нас не будет второго шанса, если он сбежит в Подземье.', 12, 0, 100, 1, 0, 26263, 56628, 18110, 18110, 110440, 'Калесгос to Player');
INSERT INTO `creature_text` VALUES (57943, 3, 0, 'Это ловушка. Ее устроили специально для нас в этот решающий момент.', 12, 0, 100, 6, 0, 26147, 56669, 50890, 50890, 109523, 'Изера Пробудившаяся to Player');
INSERT INTO `creature_text` VALUES (57944, 3, 0, 'Тогда... все потеряно.', 12, 0, 100, 66, 0, 26503, 56668, 15593, 15593, 109524, 'Алекстраза Хранительница Жизни to Player');
INSERT INTO `creature_text` VALUES (57946, 3, 0, 'Бесполезно, мощь Души Дракона слишком велика. Мне не справиться с потоками хаотической энергии. Она может уничтожить нас всех.', 12, 0, 100, 1, 0, 25904, 56666, 2265, 2265, 109520, 'Тралл to Player');
INSERT INTO `creature_text` VALUES (57946, 3, 0, 'Да, я понимаю. Мы можем обернуть магию Смертокрыла против него самого.', 12, 0, 100, 1, 0, 25905, 56667, 41172, 41172, 109521, 'Тралл to Player');
INSERT INTO `creature_text` VALUES (57946, 3, 0, 'У нас нет выбора. Вся надежда на наших союзников.', 12, 0, 100, 1, 0, 25906, 56670, 61828, 61828, 109522, 'Тралл to Player');
INSERT INTO `creature_text` VALUES (57947, 3, 0, 'Необязательно. Мне кажется... да... а это может сработать!', 12, 0, 100, 5, 0, 26547, 56664, 20468, 20468, 109518, 'Калесгос to Player');
INSERT INTO `creature_text` VALUES (57947, 3, 0, 'Потом я открою путь в Око Вечности. У вас нет права на ошибку, герои. Будущее Азерота – в ваших руках.', 12, 0, 100, 1, 0, 26266, 56665, 67922, 67922, 109519, 'Калесгос to Player');
INSERT INTO `creature_text` VALUES (57947, 3, 0, 'С помощью Радужного Средоточия Ока Вечности мы сможем сфокусировать магическую матрицу, заключенную в Душе Дракона.', 12, 0, 100, 5, 0, 26265, 56733, 30187, 30187, 109675, 'Калесгос to Player');
INSERT INTO `creature_text` VALUES (58068, 3, 0, 'Властелин Грома вернется, и тогда вам не устоять!', 12, 0, 100, 0, 0, 0, 57191, 6000, 6000, 0, 'Разведчик из Доцзяна to Player');
INSERT INTO `creature_text` VALUES (58068, 3, 0, 'Отведи взгляд, крестьянин!', 12, 0, 100, 0, 0, 0, 57193, 378000, 378000, 0, 'Разведчик из Доцзяна to Player');
INSERT INTO `creature_text` VALUES (58068, 3, 0, 'Преклони колена, проситель!', 12, 0, 100, 0, 0, 0, 57189, 554000, 554000, 0, 'Разведчик из Доцзяна to Player');
INSERT INTO `creature_text` VALUES (58071, 3, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 1150, 29000, 29000, 0, 'Trained Compy');
INSERT INTO `creature_text` VALUES (58071, 4, 0, '%s is trying to escape in a panic!', 16, 0, 100, 0, 0, 0, 1150, 6000, 6000, 0, 'Дрессированный компсогнат');
INSERT INTO `creature_text` VALUES (58113, 3, 0, 'Какая часть нашего путешествия создала у тебя впечатление, что у нас есть выбор?', 12, 0, 100, 0, 0, 0, 62752, 1223000, 1223000, 0, 'Служитель солнца Дезко to Player');
INSERT INTO `creature_text` VALUES (58113, 3, 0, 'Пока нельзя, Леза рожает. Надо подождать.', 12, 0, 100, 0, 0, 0, 62751, 1215000, 1215000, 0, 'Служитель солнца Дезко to Player');
INSERT INTO `creature_text` VALUES (58113, 3, 0, 'Это не должно было быть боевым заданием.', 12, 0, 100, 0, 0, 0, 62753, 931000, 931000, 0, 'Служитель солнца Дезко to Player');
INSERT INTO `creature_text` VALUES (58114, 3, 0, '$p, сразись с богомолами, а мы продолжим борьбу с могу. Если повезет, мы скоро к тебе присоединимся.', 12, 0, 100, 0, 0, 0, 57494, 5876000, 5876000, 0, 'Кор Кровавый Бивень to Player');
INSERT INTO `creature_text` VALUES (58114, 3, 0, 'Дезко, мы должны нанести удар сейчас, когда могу считают нас слабыми. Давай спустим на них |3-3($c).', 12, 0, 100, 0, 0, 0, 57949, 927000, 927000, 0, 'Кор Кровавый Бивень to Player');
INSERT INTO `creature_text` VALUES (58114, 3, 0, 'Дезко, надо продолжать идти на запад. Здесь мы уязвимы.', 12, 0, 100, 0, 0, 0, 62756, 1212000, 1212000, 0, 'Кор Кровавый Бивень to Player');
INSERT INTO `creature_text` VALUES (58114, 3, 0, 'Дезко, это оружие Орды. Тебе надо использовать его против Альянса.', 12, 0, 100, 0, 0, 0, 62754, 928000, 928000, 0, 'Кор Кровавый Бивень to Player');
INSERT INTO `creature_text` VALUES (58114, 3, 0, 'Могу – прирожденные лидеры. Соблюдай осторожность.', 12, 0, 100, 0, 0, 0, 56978, 1228000, 1228000, 0, 'Кор Кровавый Бивень to Player');
INSERT INTO `creature_text` VALUES (58114, 3, 0, 'Никто не знал, что Альянс станет руководствоваться точно такими же видениями, как те, что были у твоей жены.', 12, 0, 100, 0, 0, 0, 62755, 935000, 935000, 0, 'Кор Кровавый Бивень to Player');
INSERT INTO `creature_text` VALUES (58114, 3, 0, 'Я благодарен твоей жене, старина, за то, что она привела нас сюда. Но она выбрала неудачное время для родов.', 12, 0, 100, 0, 0, 0, 62757, 1219000, 1219000, 0, 'Кор Кровавый Бивень to Player');
INSERT INTO `creature_text` VALUES (58117, 1, 0, 'Склонись пред нами!', 12, 0, 100, 0, 0, 0, 57195, 0, 0, 0, 'Искатель из Доцзяна to Player');
INSERT INTO `creature_text` VALUES (58117, 3, 0, 'Властелин Грома вернется, и тогда вам не устоять!', 12, 0, 100, 0, 0, 0, 57191, 55000, 55000, 0, 'Искатель из Доцзяна to Player');
INSERT INTO `creature_text` VALUES (58117, 3, 0, 'Мы возродимся, и тогда вы будете служить нам!', 12, 0, 100, 0, 0, 0, 57190, 48000, 48000, 0, 'Искатель из Доцзяна to Player');
INSERT INTO `creature_text` VALUES (58117, 3, 0, 'Положи к остальным, Лорд Искатель осмотрит их.', 12, 0, 100, 0, 0, 0, 62771, 78000, 78000, 0, 'Искатель из Доцзяна to Искатель из Доцзяна');
INSERT INTO `creature_text` VALUES (58117, 3, 0, 'Преклони колена, проситель!', 12, 0, 100, 0, 0, 0, 57189, 97000, 97000, 0, 'Искатель из Доцзяна to Player');
INSERT INTO `creature_text` VALUES (58117, 3, 0, 'Твоему народу суждено подчиниться.', 12, 0, 100, 0, 0, 0, 57194, 16000, 16000, 0, 'Искатель из Доцзяна to Player');
INSERT INTO `creature_text` VALUES (58117, 3, 0, 'Что такое? Я занят.', 12, 0, 100, 0, 0, 0, 62769, 74000, 74000, 0, 'Искатель из Доцзяна to Искатель из Доцзяна');
INSERT INTO `creature_text` VALUES (58117, 3, 0, 'Этого достаточно?', 12, 0, 100, 0, 0, 0, 62770, 74000, 74000, 0, 'Искатель из Доцзяна to Искатель из Доцзяна');
INSERT INTO `creature_text` VALUES (58146, 5, 0, 'Удерживайте их! Защищайте лучников!', 14, 0, 100, 0, 0, 0, 56892, 0, 0, 0, 'Защитник Змеиного Хребта to Player');
INSERT INTO `creature_text` VALUES (58158, 3, 0, 'Охотничий лагерь Эрнестуэя находится в западной части Долины Четырех Ветров. Если когда-нибудь решишь отправиться на запад, заходи к нам. Удачной охоты!', 12, 0, 100, 0, 0, 0, 57200, 1000, 1000, 0, 'Даггл Бомбстер to Player');
INSERT INTO `creature_text` VALUES (58164, 3, 0, 'А-А-А! ММ-М!', 14, 0, 100, 0, 0, 0, 57330, 30000, 30000, 0, 'Леза Искатель Зари');
INSERT INTO `creature_text` VALUES (58165, 3, 0, 'Быстрее, глупцы. Эти артефакты нужны нам как воздух! Шевелитесь, а не то отведаете палки!', 14, 0, 100, 0, 0, 0, 57161, 300000, 600000, 0, 'Доминатор из Доцзяна');
INSERT INTO `creature_text` VALUES (58198, 4, 0, 'Ты сильнее меня. Если хочешь продолжить, найди мастера Снежного Вихря в додзё на вершине горы.', 12, 0, 100, 16, 0, 0, 62666, 4826, 4826, 0, 'Послушник Шадо-Пан');
INSERT INTO `creature_text` VALUES (58202, 3, 0, 'Гргрлл!', 14, 0, 100, 0, 0, 0, 66517, 170203, 170203, 0, 'Защитник Змеиного Хребта to Player');
INSERT INTO `creature_text` VALUES (58202, 3, 0, 'М-м-мпф-ф!', 14, 0, 100, 0, 0, 0, 66519, 138179, 138179, 0, 'Защитник Змеиного Хребта to Player');
INSERT INTO `creature_text` VALUES (58206, 0, 0, 'Ты можешь многому научиться, наблюдая меня в деле.', 12, 0, 100, 0, 0, 0, 56946, 0, 0, 0, 'Кан Ежевичный Посох to Player');
INSERT INTO `creature_text` VALUES (58206, 3, 0, 'В древних легендах часто упоминается Доцзянская династия и их Пруд Молодости. Посмотрим, правдивы ли эти легенды.', 12, 0, 100, 0, 0, 0, 56955, 2000, 2000, 0, 'Кан Ежевичный Посох to Player');
INSERT INTO `creature_text` VALUES (58206, 3, 0, 'Мне бы хотелось своими глазами увидеть эти земли.', 12, 0, 100, 0, 0, 0, 56945, 2000, 2000, 0, 'Кан Ежевичный Посох to Player');
INSERT INTO `creature_text` VALUES (58206, 3, 0, 'Это старая императорская дорога. Говорят, в империи могу сюда приводили пленников для пыток.', 12, 0, 100, 0, 0, 0, 0, 108000, 108000, 0, 'Кан Ежевичный Посох');
INSERT INTO `creature_text` VALUES (58206, 4, 0, 'Ты смотри! Тот парнишка оставил тебе немного очищенной воды.', 12, 0, 100, 0, 0, 0, 56971, 27000, 27000, 0, 'Кан Ежевичный Посох to На Лек');
INSERT INTO `creature_text` VALUES (58224, 0, 0, 'Не желаешь преклонять перед нами колени? Тогда умри как последний гну-синь!', 12, 0, 100, 0, 0, 0, 57437, 0, 0, 0, 'Первопроходец Брожай to Player');
INSERT INTO `creature_text` VALUES (58224, 0, 0, 'Первопроходец Брожай воскрешает павшего стража.', 41, 0, 100, 0, 0, 0, 57436, 0, 0, 0, 'Первопроходец Брожай');
INSERT INTO `creature_text` VALUES (58224, 0, 0, 'Страж! Вставай и защити своего господина!', 14, 0, 100, 0, 0, 0, 57435, 0, 0, 0, 'Первопроходец Брожай');
INSERT INTO `creature_text` VALUES (58225, 3, 0, 'А-а, вот хорошее яичко. Да, оно подойдет. Нет, не это – оно слишком маленькое. Вот это тоже ничего...', 12, 0, 100, 0, 0, 0, 56993, 378000, 378000, 0, 'Инструктор Тун to Player');
INSERT INTO `creature_text` VALUES (58242, 3, 0, '%s держится за громко бурлящий живот.', 16, 0, 100, 0, 0, 0, 57153, 25000, 25000, 0, 'Подавленный смотритель Чжу to Player');
INSERT INTO `creature_text` VALUES (58242, 3, 0, 'Кто, я?', 12, 0, 100, 0, 0, 0, 57151, 10000, 10000, 0, 'Подавленный смотритель Чжу to Player');
INSERT INTO `creature_text` VALUES (58242, 3, 0, 'Нет!', 14, 0, 100, 0, 0, 0, 135514, 46000, 46000, 0, 'Подавленный смотритель Чжу to Player');
INSERT INTO `creature_text` VALUES (58242, 3, 0, 'Ну ладно, хуже мне все равно не будет.', 12, 0, 100, 0, 0, 0, 57152, 15000, 15000, 0, 'Подавленный смотритель Чжу to Player');
INSERT INTO `creature_text` VALUES (58242, 3, 0, 'Ой... ох... что ты со мной сделал?!', 12, 0, 100, 0, 0, 0, 57156, 28000, 28000, 0, 'Подавленный смотритель Чжу to Player');
INSERT INTO `creature_text` VALUES (58242, 3, 0, 'Ох-х... р-р-р...', 14, 0, 100, 0, 0, 0, 57159, 43000, 43000, 0, 'Подавленный смотритель Чжу to Player');
INSERT INTO `creature_text` VALUES (58257, 1, 0, 'Прекрас-сное оружие. Оно должно быть моим!', 12, 0, 100, 0, 0, 0, 63418, 0, 0, 0, 'Шкуродер Речного Клинка to Player');
INSERT INTO `creature_text` VALUES (58273, 3, 0, 'Какая блес-стящ-щая броня. Отдай ее мне!', 12, 0, 100, 0, 0, 0, 63420, 8994000, 8994000, 0, 'Бандит Речного Клинка to Player');
INSERT INTO `creature_text` VALUES (58273, 3, 0, 'Любишь цзинь-юй?!', 12, 0, 100, 0, 0, 0, 63417, 5910000, 5910000, 0, 'Бандит Речного Клинка to Player');
INSERT INTO `creature_text` VALUES (58273, 3, 0, 'С-сдавайс-ся!', 12, 0, 100, 0, 0, 0, 63419, 7433000, 7433000, 0, 'Бандит Речного Клинка to Player');
INSERT INTO `creature_text` VALUES (58274, 4, 0, '%s is trying to escape in a panic!', 16, 0, 100, 0, 0, 0, 1150, 13000, 13000, 0, 'Налетчик Речного Клинка');
INSERT INTO `creature_text` VALUES (58315, 3, 0, '$n, надеюсь, ты будешь усердно трудиться. Выращивать облачного змея – великая честь.', 12, 0, 100, 2, 0, 0, 57078, 14000, 14000, 0, 'Инструктор Небесный Шип to Player');
INSERT INTO `creature_text` VALUES (58315, 3, 0, 'К сожалению, детеныш не хочет ждать!', 12, 0, 100, 11, 0, 0, 57077, 8000, 8000, 0, 'Инструктор Небесный Шип to Player');
