INSERT INTO `creature_text` VALUES (58444, 0, 0, 'Do you see the hanging pennants above our heads? These flags mark the race course. If you ever get lost, you can use these flags as a guide.', 12, 0, 100, 0, 0, 0, 57236, 0, 0, 0, 'Instructor Windblade to Jade Cloud Serpent');
